	mulps xmm5, xmm5
	movaps xmm1, [esp + i1020_three]
	mulps xmm5, xmm4	;# rsq*lu*lu 			
	movaps xmm0, [esp + i1020_half]
	subps xmm1, xmm5	;# 30-rsq*lu*lu 
