	mulps  xmm4, xmm3	;# xmm4=total fscal 
	addps  xmm3, [esp + i1020_vctot]

	movaps xmm0, [esp + i1020_dxO]
	movaps xmm1, [esp + i1020_dyO]
	movaps xmm2, [esp + i1020_dzO]

	movaps [esp + i1020_vctot], xmm3

	mulps  xmm0, xmm4
	mulps  xmm1, xmm4
	mulps  xmm2, xmm4
