	addss  xmm4, [esp + i1120_fiyH2]
	addss  xmm5, [esp + i1120_fizH2]
	movss  [esp + i1120_fixH2], xmm3	
	movss  [esp + i1120_fiyH2], xmm4	
	movss  [esp + i1120_fizH2], xmm5	;# updated the H2 force 

