	mov   eax, [ebp + i1120_Vc]
	addss xmm7, [eax + edx*4] 
	;# move back to mem 
	movss [eax + edx*4], xmm7 
	
	;# accumulate total lj energy and update it 
	movaps xmm7, [esp + i1120_vnbtot]
	;# accumulate 
	movhlps xmm6, xmm7
	addps  xmm7, xmm6	;# pos 0-1 in xmm7 have the sum now 
	movaps xmm6, xmm7
	shufps xmm6, xmm6, 1
	addss  xmm7, xmm6		

	;# add earlier value from mem 
	mov   eax, [ebp + i1120_Vnb]
	addss xmm7, [eax + edx*4] 
	;# move back to mem 
	movss [eax + edx*4], xmm7 
	
	;# finish if last 
	mov   ecx, [ebp + i1120_nri]
	dec ecx
	jecxz i1120_end
	;# not last, iterate once more!  
	mov [ebp + i1120_nri], ecx
	jmp i1120_outer
i1120_end:
	emms
	mov eax, [esp + i1120_salign]
	add esp, eax
	add esp, 696
	pop edi
	pop esi
    pop edx
    pop ecx
    pop ebx
    pop eax
	leave
	ret


	
