.equiv		i3000_faction,		44
.equiv		i3000_charge,		48
.equiv		i3000_facel,		52
.equiv		i3000_Vc,			56			
.equiv		i3000_tabscale,		60
.equiv		i3000_VFtab,		64
	;# stack offsets for local variables  
	;# bottom of stack is cache-aligned for sse use 
.equiv		i3000_ix,			0
.equiv		i3000_iy,			16
.equiv		i3000_iz,			32
.equiv		i3000_iq,			48
.equiv		i3000_dx,			64
.equiv		i3000_dy,			80
.equiv		i3000_dz,			96
.equiv		i3000_two,			112
.equiv		i3000_tsc,			128
.equiv		i3000_qq,			144	
.equiv		i3000_fs,			160
.equiv		i3000_vctot,		176
.equiv		i3000_fix,			192
.equiv		i3000_fiy,			208
.equiv		i3000_fiz,			224
.equiv		i3000_half,			240
.equiv		i3000_three,		256
.equiv		i3000_is3,			272
.equiv		i3000_ii3,			276
.equiv		i3000_innerjjnr,	280
.equiv		i3000_innerk,		284
