	mulps xmm3, [esp + i3000_tsc]
	mulps xmm3, xmm0
	subps  xmm4, xmm3
	mov    edi, [ebp + i3000_faction]

	movaps xmm0, [esp + i3000_dx]
	movaps xmm1, [esp + i3000_dy]
	movaps xmm2, [esp + i3000_dz]

	mulps  xmm0, xmm4
	mulps  xmm1, xmm4
	mulps  xmm2, xmm4
	;# xmm0-xmm2 contains tx-tz (partial force) 
