	addss  xmm4, xmm1
	addss  xmm5, xmm2
	movaps [esp + i3000_fix], xmm3
	movaps [esp + i3000_fiy], xmm4
	movaps [esp + i3000_fiz], xmm5
	;# update fj 
	
	movss   xmm3, [edi + eax*4]
	movss   xmm4, [edi + eax*4 + 4]
	movss   xmm5, [edi + eax*4 + 8]
