	subps  xmm4, xmm3

	movaps xmm0, [esp + i3010_dx]
	movaps xmm1, [esp + i3010_dy]
	movaps xmm2, [esp + i3010_dz]

	mulps  xmm0, xmm4
	mulps  xmm1, xmm4
