	movaps [esp + i3310_iq], xmm3
	
	shufps xmm0, xmm0, 0
	shufps xmm1, xmm1, 0
	shufps xmm2, xmm2, 0

	movaps [esp + i3310_ix], xmm0
	movaps [esp + i3310_iy], xmm1
	movaps [esp + i3310_iz], xmm2

	mov   [esp + i3310_ii3], ebx
	
