	movaps xmm4, [esp + i3310_c6]
	mulps  xmm7, xmm4	 ;# fijD 
	mulps  xmm5, xmm4	 ;# vnb6 
	addps  xmm7, [esp + i3310_fscal] ;# add to fscal 

