	movaps  xmm3, [esp + mci1030_three]
	movaps  xmm7, xmm3
	mulps   xmm1, xmm0
	mulps   xmm5, xmm4
	subps   xmm3, xmm1
	subps   xmm7, xmm5
	mulps   xmm3, xmm2
	mulps   xmm7, xmm6
	mulps   xmm3, [esp + mci1030_half] ;# rinvH2H2 
	mulps   xmm7, [esp + mci1030_half] ;# rinvH2H1 
	movaps  [esp + mci1030_rinvH2H2], xmm3
	movaps  [esp + mci1030_rinvH2H1], xmm7
	
