	addps  xmm0, xmm1
	subps  xmm0, [esp + mci2120_crf] ;# xmm0=rinv+ krsq-crf 
	subps  xmm7, xmm1
	mulps  xmm0, [esp + mci2120_qqO]
	addps  xmm0, [esp + mci2120_vctot]
	movaps [esp + mci2120_vnbtot], xmm3
	movaps [esp + mci2120_vctot], xmm0

