	movaps xmm2, xmm5
	mulps xmm5, xmm5
	movaps xmm1, [esp + mci3010_three]
	mulps xmm5, xmm4	;# rsq*lu*lu 			
	movaps xmm0, [esp + mci3010_half]
	subps xmm1, xmm5	;# 30-rsq*lu*lu 
	mulps xmm1, xmm2	
