.intel_syntax noprefix

/* NB: We prefix all local variables with underscore, to avoid stupid
 * bugs. Otherwise, dx will e.g. be interpreted as a register,
 * without any further warning!
 */

.text
.align 16

sse2_minushalf:	
        .long 0x00000000
	.long 0xbfe00000	
	.long 0000000000
	.long 0xbfe00000	
sse2_half:	
        .long 0x00000000
	.long 0x3fe00000
	.long 0x00000000
	.long 0x3fe00000
sse2_two:	
        .long 0x00000000
	.long 0x40000000
	.long 0x00000000
	.long 0x40000000
sse2_three:	
        .long 0x00000000
	.long 0x40080000
	.long 0x00000000
	.long 0x40080000
sse2_six:
        .long 0x00000000
	.long 0x40180000
	.long 0x00000000
	.long 0x40180000
sse2_twelve:	
        .long 0x00000000
	.long 0x40280000
	.long 0x00000000
	.long 0x40280000


.globl checksse2	 /* try to issue a SSE2 instruction */
	.type checksse2,@function
checksse2:
	emms
	xorpd xmm0,xmm0
	emms
	ret

.align 16
	
.globl vecinvsqrt_sse2
	.type vecinvsqrt_sse2,@function
vecinvsqrt_sse2:	
	push ebp
	mov ebp,esp	
	push eax
	push ebx
	push ecx
	push edx

	mov eax, [ebp + 8]
	mov ebx, [ebp + 12]	
	mov ecx, [ebp + 16]
        mov edx, ecx
	movupd xmm6,[sse2_three]
	movupd xmm7,[sse2_minushalf]
        shr ecx, 2
        jecxz .vecinvsqrt_iter2
        emms	
.vecinvsqrt_loop4:	
	movapd xmm0,[eax]	
	add eax,  16
	cvtpd2ps xmm4, xmm0
	movapd xmm2,[eax]
	add eax,  16
	cvtpd2ps xmm5, xmm2
	rsqrtps xmm1, xmm4	
	rsqrtps xmm3, xmm5
	movapd  xmm4, xmm0	
	movapd  xmm5, xmm2	
	cvtps2pd xmm1, xmm1	
	cvtps2pd xmm3, xmm3	
	mulpd xmm0,xmm1		
        mulpd xmm2,xmm3		
	mulpd xmm0,xmm1		
        mulpd xmm2,xmm3		
	subpd xmm0,xmm6		
	subpd xmm2,xmm6		
	mulpd xmm0,xmm1		
	mulpd xmm2,xmm3
	mulpd xmm0,xmm7		
	mulpd xmm2,xmm7		
	mulpd xmm4,xmm0		
        mulpd xmm5,xmm2		
	mulpd xmm4,xmm0		
        mulpd xmm5,xmm2		
	subpd xmm4,xmm6		
	subpd xmm5,xmm6		
	mulpd xmm4,xmm0		
	mulpd xmm5,xmm2
	mulpd xmm4,xmm7		
	mulpd xmm5,xmm7		
	movapd [ebx],xmm4
	add ebx,  16
	movapd [ebx],xmm5
	add ebx,  16
        dec ecx
        jecxz .vecinvsqrt_iter2
        jmp .vecinvsqrt_loop4
.vecinvsqrt_iter2:
        mov ecx,edx
        and ecx, 2
        jecxz .vecinvsqrt_iter1
	movapd xmm0,[eax]
	add eax,  16
	cvtpd2ps xmm4, xmm0
	rsqrtps xmm1, xmm4
	movapd xmm4, xmm0
	cvtps2pd xmm1, xmm1	
	mulpd xmm0,xmm1
	mulpd xmm0,xmm1		
	subpd xmm0,xmm6		
	mulpd xmm0,xmm1		
	mulpd xmm0,xmm7		
	mulpd xmm4,xmm0		
	mulpd xmm4,xmm0		
	subpd xmm4,xmm6		
	mulpd xmm4,xmm0		
	mulpd xmm4,xmm7			
	movapd [ebx],xmm4
	add ebx,  16        
.vecinvsqrt_iter1:
        mov ecx,edx
        and ecx, 1
        jecxz .vecinvsqrt_end
	movsd xmm0,[eax]
	add eax,  8
	cvtsd2ss xmm4, xmm0
	rsqrtss xmm1, xmm4	
	movsd xmm4, xmm0
	cvtss2sd xmm1, xmm1	
	mulsd xmm0,xmm1
	mulsd xmm0,xmm1		
	subsd xmm0,xmm6		
	mulsd xmm0,xmm1		
	mulsd xmm0,xmm7		
	mulsd xmm4,xmm0		
	mulsd xmm4,xmm0		
	subsd xmm4,xmm6		
	mulsd xmm4,xmm0		
	mulsd xmm4,xmm7			
	movsd [ebx],xmm4
	add ebx,  8
.vecinvsqrt_end:	
	emms
	pop edx
	pop ecx
	pop ebx
	pop eax
	leave
	ret
	
	
.globl vecrecip_sse2
	.type vecrecip_sse2,@function
vecrecip_sse2:	
	push ebp
	mov ebp,esp	
	push eax
	push ebx
	push ecx
	push edx

	mov eax, [ebp + 8]
	mov ebx, [ebp + 12]	
	mov ecx, [ebp + 16]
        mov edx, ecx
	movupd xmm6,[sse2_two]
        shr ecx, 2
        jecxz .vecrecip_iter2
        emms	
.vecrecip_loop4:	
	movapd xmm0,[eax]	/* xa */
	add eax,  16
	cvtpd2ps xmm4, xmm0	
	movapd xmm2,[eax]	/* xb */
	add eax,  16
	cvtpd2ps xmm5, xmm2
	rcpps xmm4, xmm4	
	rcpps xmm5, xmm5
	movapd xmm7, xmm6
	movapd xmm1, xmm0	/* xa */
	movapd xmm3, xmm2	/* xb */
	cvtps2pd xmm4, xmm4	/* lu a */	
	cvtps2pd xmm5, xmm5	/* lu b */
	mulpd xmm0, xmm4	/* x*lu a */
	mulpd xmm2, xmm5	/* x*lu b */
	subpd xmm7, xmm0	/* 2-x*lu a */
	mulpd xmm4, xmm7	/* lu*(2-x*lu) a */
	movapd xmm7, xmm6	/* 2.0 */
	subpd xmm7, xmm2	/* 2-x*lu b */
	mulpd xmm5, xmm7	/* lu*(2-x*lu) b */
	movapd xmm7, xmm6	
	mulpd xmm1, xmm4	/* x*lu a */
	mulpd xmm3, xmm5	/* x*lu b */
	subpd xmm7, xmm1	/* 2-x*lu a */
	mulpd xmm4, xmm7	/* lu*(2-x*lu) a */
	movapd xmm7, xmm6	/* 2.0 */
	subpd xmm7, xmm3	/* 2-x*lu b */
	mulpd xmm5, xmm7	/* lu*(2-x*lu) b */	
	movapd [ebx],xmm4
	add ebx,  16
	movapd [ebx],xmm5
	add ebx,  16
        dec ecx
        jecxz .vecrecip_iter2
        jmp .vecrecip_loop4
.vecrecip_iter2:
        mov ecx,edx
        and ecx, 2
        jecxz .vecrecip_iter1
	movapd xmm0,[eax]	
	add eax,  16
	cvtpd2ps xmm4, xmm0	
	rcpps xmm4, xmm4
	cvtps2pd xmm4, xmm4	/* lu */
	movapd xmm3, xmm6	/* 2 */
	movapd xmm1,xmm0	/* x */
	mulpd xmm0,xmm4		/* lu*x */
	subpd xmm3,xmm0		/* 2-lu*x */
	mulpd xmm4, xmm3	/* iter1: lu=lu*(2-lu*x) */

	movapd xmm3, xmm6
	mulpd xmm1, xmm4	/* x*lu */
	subpd xmm3, xmm1	/* 2-x*lu */
	mulpd xmm4, xmm3	/* 1/x */
	movapd [ebx],xmm4
	add ebx,  16        
.vecrecip_iter1:
        mov ecx,edx
        and ecx, 1
        jecxz .vecrecip_end
	movsd xmm0,[eax]
	cvtsd2ss xmm4, xmm0	
	rcpss xmm4, xmm4
	cvtss2sd xmm4, xmm4	/* lu */
	movapd xmm3, xmm6	/* 2 */
	movsd xmm1,xmm0		/* x */
	mulsd xmm0,xmm4		/* lu*x */
	subsd xmm3,xmm0		/* 2-lu*x */
	mulsd xmm4, xmm3	/* iter1: lu=lu*(2-lu*x) */

	mulsd xmm1, xmm4	/* x*lu */
	subsd xmm6, xmm1	/* 2-x*lu */
	mulsd xmm4, xmm6	/* 1/x */
	movsd [ebx],xmm4
.vecrecip_end:	
	emms
	pop edx
	pop ecx
	pop ebx
	pop eax
	leave
	ret
	
	
.globl inl0100_sse2
	.type inl0100_sse2,@function
inl0100_sse2:	
.equ		_nri, 		8
.equ		_iinr,		12
.equ		_jindex,	16
.equ		_jjnr,		20
.equ		_shift,		24
.equ		_shiftvec,	28
.equ		_fshift,	32
.equ		_gid,		36
.equ		_pos,		40		
.equ		_faction,	44
.equ		_type,		48
.equ		_ntype,		52
.equ		_nbfp,		56	
.equ		_Vnb,		60	
	/* stack offsets for local variables */ 
	/* bottom of stack is cache-aligned for sse use */
.equ		_ix,		0
.equ		_iy,	        16
.equ		_iz,            32
.equ		_dx,            48
.equ		_dy,            64
.equ		_dz,            80
.equ		_two,           96		
.equ		_c6,		112
.equ		_c12,		128
.equ		_six,		144
.equ		_twelve,	160		 
.equ		_vnbtot,	176
.equ		_fix,		192
.equ		_fiy,		208
.equ		_fiz,		224
.equ		_half,		240
.equ		_three,		256
.equ		_is3,		272
.equ		_ii3,		276
.equ		_ntia,		280	
.equ		_innerjjnr,     284
.equ		_innerk,        288
.equ		_salign,        292
	push ebp
	mov ebp,esp	
        push eax
        push ebx
        push ecx
        push edx
	push esi
	push edi
	sub esp, 296		/* local stack space */
	mov  eax, esp
	and  eax, 0xf
	sub esp, eax
	mov [esp + _salign], eax

	emms

	movupd xmm1, [sse2_two]
	movupd xmm2, [sse2_six]
	movupd xmm3, [sse2_twelve]
	movapd [esp + _two], xmm1
	movapd [esp + _six],  xmm2
	movapd [esp + _twelve], xmm3

	/* assume we have at least one i particle - start directly */	
.i0100_outer:
	mov   eax, [ebp + _shift]      /* eax = pointer into shift[] */
	mov   ebx, [eax]		/* ebx=shift[n] */
	add   dword ptr [ebp + _shift],  4  /* advance pointer one step */

	lea   ebx, [ebx + ebx*2]        /* ebx=3*is */
	mov   [esp + _is3],ebx    	/* store is3 */

	mov   eax, [ebp + _shiftvec]   /* eax = base of shiftvec[] */

	movsd xmm0, [eax + ebx*8]
	movsd xmm1, [eax + ebx*8 + 8]
	movsd xmm2, [eax + ebx*8 + 16] 

	mov   ecx, [ebp + _iinr]       /* ecx = pointer into iinr[] */	
	add   dword ptr [ebp + _iinr],  4   /* advance pointer */
	mov   ebx, [ecx]	        /* ebx =ii */

        mov   edx, [ebp + _type] 
        mov   edx, [edx + ebx*4]
        imul  edx, [ebp + _ntype]
        shl   edx, 1
        mov   [esp + _ntia], edx
		
	lea   ebx, [ebx + ebx*2]	/* ebx = 3*ii=ii3 */
	mov   eax, [ebp + _pos]        /* eax = base of pos[] */ 

	addsd xmm0, [eax + ebx*8]
	addsd xmm1, [eax + ebx*8 + 8]
	addsd xmm2, [eax + ebx*8 + 16]
	
	shufpd xmm0, xmm0, 0
	shufpd xmm1, xmm1, 0
	shufpd xmm2, xmm2, 0

	movapd [esp + _ix], xmm0
	movapd [esp + _iy], xmm1
	movapd [esp + _iz], xmm2

	mov   [esp + _ii3], ebx
	
	/* clear vnbtot and i forces */
	xorpd xmm4, xmm4
	movapd [esp + _vnbtot], xmm4
	movapd [esp + _fix], xmm4
	movapd [esp + _fiy], xmm4
	movapd [esp + _fiz], xmm4
	
	mov   eax, [ebp + _jindex]
	mov   ecx, [eax]	         /* jindex[n] */
	mov   edx, [eax + 4]	         /* jindex[n+1] */
	add   dword ptr [ebp + _jindex],  4
	sub   edx, ecx                   /* number of innerloop atoms */

	mov   esi, [ebp + _pos]
	mov   edi, [ebp + _faction]	
	mov   eax, [ebp + _jjnr]
	shl   ecx, 2
	add   eax, ecx
	mov   [esp + _innerjjnr], eax     /* pointer to jjnr[nj0] */
	sub   edx,  2
	mov   [esp + _innerk], edx        /* number of innerloop atoms */
	
	jge   .i0100_unroll_loop
	jmp   .i0100_checksingle
.i0100_unroll_loop:
	/* twice unrolled innerloop here */
	mov   edx, [esp + _innerjjnr]     /* pointer to jjnr[k] */
	mov   eax, [edx]	
	mov   ebx, [edx + 4]              
	add   dword ptr [esp + _innerjjnr],  8 /* advance pointer (unrolled 2) */

	movd  mm0, eax		/* use mmx registers as temp storage */
	movd  mm1, ebx
	
	mov esi, [ebp + _type]
	mov eax, [esi + eax*4]
	mov ebx, [esi + ebx*4]
	mov esi, [ebp + _nbfp]
	shl eax, 1
	shl ebx, 1
	mov edi, [esp + _ntia]
	add eax, edi
	add ebx, edi

	movupd xmm6, [esi + eax*8]	/* c6a c12a */
	movupd xmm7, [esi + ebx*8]	/* c6b c12b */
	movapd xmm4, xmm6
	unpcklpd xmm4, xmm7
	unpckhpd xmm6, xmm7

	movd  eax, mm0		
	movd  ebx, mm1	
	
	movapd [esp + _c6], xmm4
	movapd [esp + _c12], xmm6

	mov esi, [ebp + _pos]       /* base of pos[] */

	lea   eax, [eax + eax*2]         /* replace jnr with j3 */
	lea   ebx, [ebx + ebx*2]	

	/* move two coordinates to xmm0-xmm2 */	

	movlpd xmm0, [esi + eax*8]
	movlpd xmm1, [esi + eax*8 + 8]
	movlpd xmm2, [esi + eax*8 + 16]
	movhpd xmm0, [esi + ebx*8]
	movhpd xmm1, [esi + ebx*8 + 8]
	movhpd xmm2, [esi + ebx*8 + 16]

	/* move ix-iz to xmm4-xmm6 */
	movapd xmm4, [esp + _ix]
	movapd xmm5, [esp + _iy]
	movapd xmm6, [esp + _iz]

	/* calc dr */
	subpd xmm4, xmm0
	subpd xmm5, xmm1
	subpd xmm6, xmm2

	/* store dr */
	movapd [esp + _dx], xmm4 
	movapd [esp + _dy], xmm5
	movapd [esp + _dz], xmm6
	/* square it */
	mulpd xmm4,xmm4
	mulpd xmm5,xmm5
	mulpd xmm6,xmm6
	addpd xmm4, xmm5
	addpd xmm4, xmm6	/* rsq in xmm4 */

	cvtpd2ps xmm6, xmm4	
	rcpps xmm6, xmm6
	cvtps2pd xmm6, xmm6	/* lu in low xmm6 */
	
	/* 1/x lookup seed in xmm6 */
	movapd xmm0, [esp + _two]
	movapd xmm5, xmm4
	mulpd xmm4, xmm6	/* lu*rsq */
	subpd xmm0, xmm4	/* 2-lu*rsq */
	mulpd xmm6, xmm0	/* (new lu) */
	
	movapd xmm0, [esp + _two]
	mulpd xmm5, xmm6	/* lu*rsq */
	subpd xmm0, xmm5	/* 2-lu*rsq */
	mulpd xmm0, xmm6	/* xmm0=rinvsq */

	movapd xmm1, xmm0
	mulpd  xmm1, xmm0
	mulpd  xmm1, xmm0	/* xmm1=rinvsix */
	movapd xmm2, xmm1
	mulpd  xmm2, xmm2	/* xmm2=rinvtwelve */

	mulpd  xmm1, [esp + _c6]
	mulpd  xmm2, [esp + _c12]
	movapd xmm5, xmm2
	subpd  xmm5, xmm1	/* vnb=vnb12-vnb6 */
	addpd  xmm5, [esp + _vnbtot]
	mulpd  xmm1, [esp + _six]
	mulpd  xmm2, [esp + _twelve]
	subpd  xmm2, xmm1
	mulpd  xmm0, xmm2	/* xmm4=total fscal */
	movapd xmm4, xmm0
	
	movapd xmm0, [esp + _dx]
	movapd xmm1, [esp + _dy]
	movapd xmm2, [esp + _dz]

	movapd [esp + _vnbtot], xmm5

	mov    edi, [ebp + _faction]
	mulpd  xmm0, xmm4
	mulpd  xmm1, xmm4
	mulpd  xmm2, xmm4
	/* xmm0-xmm2 contains tx-tz (partial force) */
	/* now update f_i */
	movapd xmm3, [esp + _fix]
	movapd xmm4, [esp + _fiy]
	movapd xmm5, [esp + _fiz]
	addpd  xmm3, xmm0
	addpd  xmm4, xmm1
	addpd  xmm5, xmm2
	movapd [esp + _fix], xmm3
	movapd [esp + _fiy], xmm4
	movapd [esp + _fiz], xmm5
	
	/* the fj's - start by accumulating forces from memory */
	movlpd xmm3, [edi + eax*8]
	movlpd xmm4, [edi + eax*8 + 8]
	movlpd xmm5, [edi + eax*8 + 16]
	movhpd xmm3, [edi + ebx*8]
	movhpd xmm4, [edi + ebx*8 + 8]
	movhpd xmm5, [edi + ebx*8 + 16]
	subpd xmm3, xmm0
	subpd xmm4, xmm1
	subpd xmm5, xmm2
	movlpd [edi + eax*8], xmm3
	movlpd [edi + eax*8 + 8], xmm4
	movlpd [edi + eax*8 + 16], xmm5
	movhpd [edi + ebx*8], xmm3
	movhpd [edi + ebx*8 + 8], xmm4
	movhpd [edi + ebx*8 + 16], xmm5
		
	/* should we do one more iteration? */
	sub   dword ptr [esp + _innerk],  2
	jl    .i0100_checksingle
	jmp   .i0100_unroll_loop
.i0100_checksingle:				
	mov   edx, [esp + _innerk]
	and   edx, 1
	jnz    .i0100_dosingle
	jmp    .i0100_updateouterdata
.i0100_dosingle:
	mov edi, [ebp + _pos]
	mov   ecx, [esp + _innerjjnr]
	mov   eax, [ecx]		

	movd  mm0, eax		/* use mmx registers as temp storage */	
	mov esi, [ebp + _type]
	mov eax, [esi + eax*4]
	mov esi, [ebp + _nbfp]
	shl eax, 1
	mov edi, [esp + _ntia]
	add eax, edi

	movupd xmm6, [esi + eax*8]	/* c6a c12a */
	xorpd xmm7, xmm7
	movapd xmm4, xmm6
	unpcklpd xmm4, xmm7
	unpckhpd xmm6, xmm7

	movd  eax, mm0		
	
	movapd [esp + _c6], xmm4
	movapd [esp + _c12], xmm6
	
	mov esi, [ebp + _pos]       /* base of pos[] */

	lea   eax, [eax + eax*2]         /* replace jnr with j3 */

	/* move coordinates to xmm0-xmm2 */	

	movlpd xmm0, [esi + eax*8]
	movlpd xmm1, [esi + eax*8 + 8]
	movlpd xmm2, [esi + eax*8 + 16]
	
	/* move ix-iz to xmm4-xmm6 */
	movapd xmm4, [esp + _ix]
	movapd xmm5, [esp + _iy]
	movapd xmm6, [esp + _iz]

	/* calc dr */
	subsd xmm4, xmm0
	subsd xmm5, xmm1
	subsd xmm6, xmm2

	/* store dr */
	movapd [esp + _dx], xmm4 
	movapd [esp + _dy], xmm5
	movapd [esp + _dz], xmm6
	/* square it */
	mulsd xmm4,xmm4
	mulsd xmm5,xmm5
	mulsd xmm6,xmm6
	addsd xmm4, xmm5
	addsd xmm4, xmm6	/* rsq in xmm4 */

	cvtsd2ss xmm6, xmm4	
	rcpss xmm6, xmm6
	cvtss2sd xmm6, xmm6	/* lu in low xmm6 */
	
	/* 1/x lookup seed in xmm6 */
	movapd xmm0, [esp + _two]
	movapd xmm5, xmm4
	mulsd xmm4, xmm6	/* lu*rsq */
	subsd xmm0, xmm4	/* 2-lu*rsq */
	mulsd xmm6, xmm0	/* (new lu) */
	
	movapd xmm0, [esp + _two]
	mulsd xmm5, xmm6	/* lu*rsq */
	subsd xmm0, xmm5	/* 2-lu*rsq */
	mulsd xmm0, xmm6	/* xmm0=rinvsq */
	movapd xmm4, xmm0
	
	movapd xmm1, xmm0
	mulsd  xmm1, xmm0
	mulsd  xmm1, xmm0	/* xmm1=rinvsix */
	movapd xmm2, xmm1
	mulsd  xmm2, xmm2	/* xmm2=rinvtwelve */

	mulsd  xmm1, [esp + _c6]
	mulsd  xmm2, [esp + _c12]
	movapd xmm5, xmm2
	subsd  xmm5, xmm1	/* vnb=vnb12-vnb6 */
	addsd  xmm5, [esp + _vnbtot]
	mulsd  xmm1, [esp + _six]
	mulsd  xmm2, [esp + _twelve]
	subsd  xmm2, xmm1
	mulsd  xmm4, xmm2	/* xmm4=total fscal */

	movapd xmm0, [esp + _dx]
	movapd xmm1, [esp + _dy]
	movapd xmm2, [esp + _dz]

	movlpd [esp + _vnbtot], xmm5

	mov    edi, [ebp + _faction]
	mulsd  xmm0, xmm4
	mulsd  xmm1, xmm4
	mulsd  xmm2, xmm4
	/* xmm0-xmm2 contains tx-tz (partial force) */
	/* now update f_i */
	movlpd xmm3, [esp + _fix]
	movlpd xmm4, [esp + _fiy]
	movlpd xmm5, [esp + _fiz]
	addsd  xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm5, xmm2
	movlpd [esp + _fix], xmm3
	movlpd [esp + _fiy], xmm4
	movlpd [esp + _fiz], xmm5
	
	/* the fj's - start by accumulating forces from memory */
	movlpd xmm3, [edi + eax*8]
	movlpd xmm4, [edi + eax*8 + 8]
	movlpd xmm5, [edi + eax*8 + 16]
	subpd xmm3, xmm0
	subpd xmm4, xmm1
	subpd xmm5, xmm2
	movlpd [edi + eax*8], xmm3
	movlpd [edi + eax*8 + 8], xmm4
	movlpd [edi + eax*8 + 16], xmm5

.i0100_updateouterdata:
	mov   ecx, [esp + _ii3]
	mov   edi, [ebp + _faction]
	mov   esi, [ebp + _fshift]
	mov   edx, [esp + _is3]

	/* accumulate i forces in xmm0, xmm1, xmm2 */
	movapd xmm0, [esp + _fix]
	movapd xmm1, [esp + _fiy]
	movapd xmm2, [esp + _fiz]

	movhlps xmm3, xmm0
	movhlps xmm4, xmm1
	movhlps xmm5, xmm2
	addsd  xmm0, xmm3
	addsd  xmm1, xmm4
	addsd  xmm2, xmm5 /* sum is in low xmm0-xmm2 */

	/* increment i force */
	movsd  xmm3, [edi + ecx*8]
	movsd  xmm4, [edi + ecx*8 + 8]
	movsd  xmm5, [edi + ecx*8 + 16]
	addsd  xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm5, xmm2
	movsd  [edi + ecx*8],     xmm3
	movsd  [edi + ecx*8 + 8], xmm4
	movsd  [edi + ecx*8 + 16], xmm5

	/* increment fshift force */ 
	movsd  xmm3, [esi + edx*8]
	movsd  xmm4, [esi + edx*8 + 8]
	movsd  xmm5, [esi + edx*8 + 16]
	addsd  xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm5, xmm2
	movsd  [esi + edx*8],     xmm3
	movsd  [esi + edx*8 + 8], xmm4
	movsd  [esi + edx*8 + 16], xmm5

	/* get group index for i particle */
	mov   edx, [ebp + _gid]      /* get group index for this i particle */
	mov   edx, [edx]
	add   dword ptr [ebp + _gid],  4  /* advance pointer */
	
	/* accumulate total lj energy and update it */
	movapd xmm7, [esp + _vnbtot]
	/* accumulate */
	movhlps xmm6, xmm7
	addsd  xmm7, xmm6	/* low xmm7 have the sum now */

	/* add earlier value from mem */
	mov   eax, [ebp + _Vnb]
	addsd xmm7, [eax + edx*8] 
	/* move back to mem */
	movsd [eax + edx*8], xmm7 

	/* finish if last */
	mov   ecx, [ebp + _nri]
	dec ecx
	jecxz .i0100_end

	/* not last, iterate once more! */ 
	mov [ebp + _nri], ecx
	jmp .i0100_outer
.i0100_end:
	emms
	mov eax, [esp + _salign]
	add esp, eax
	add esp, 296
	pop edi
	pop esi
        pop edx
        pop ecx
        pop ebx
        pop eax
	leave
	ret



.globl inl0300_sse2
