	xorpd xmm3, xmm3
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1  */
	unpckhpd xmm7, xmm3	/* H1  */
	
	/* table ready, in xmm4-xmm7 */	
	mulsd  xmm6, xmm1	/* xmm6=Geps */
	mulsd  xmm7, xmm2	/* xmm7=Heps2 */
	addsd  xmm5, xmm6
	addsd  xmm5, xmm7	/* xmm5=Fp */	
	mulsd  xmm7, [esp + _two]	/* two*Heps2 */
	addsd  xmm7, xmm6
	addsd  xmm7, xmm5 /* xmm7=FF */
	mulsd  xmm5, xmm1 /* xmm5=eps*Fp */
	addsd  xmm5, xmm4 /* xmm5=VV */
	
	movapd xmm4, [esp + _c12]
	mulsd  xmm7, xmm4 
	mulsd  xmm5, xmm4  
	addsd  xmm7, [esp + _fscal] 
	
	addsd  xmm5, [esp + _vnbtot]
	movlpd [esp + _vnbtot], xmm5
	xorpd  xmm4, xmm4

	mulsd xmm7, [esp + _tsc]
	mulsd xmm7, xmm0
	subsd  xmm4, xmm7

	movapd xmm0, [esp + _dx]
	movapd xmm1, [esp + _dy]
	movapd xmm2, [esp + _dz]

	movd eax, mm0	

	mov    edi, [ebp + _faction]
	mulsd  xmm0, xmm4
	mulsd  xmm1, xmm4
	mulsd  xmm2, xmm4
	/* xmm0-xmm2 contains tx-tz (partial force) */
	/* now update f_i */
	movapd xmm3, [esp + _fix]
	movapd xmm4, [esp + _fiy]
	movapd xmm5, [esp + _fiz]
	addsd  xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm5, xmm2
	movlpd [esp + _fix], xmm3
	movlpd [esp + _fiy], xmm4
	movlpd [esp + _fiz], xmm5
	/* the fj's - start by accumulating forces from memory */
	movlpd xmm3, [edi + eax*8]
	movlpd xmm4, [edi + eax*8 + 8]
	movlpd xmm5, [edi + eax*8 + 16]
	subsd xmm3, xmm0
	subsd xmm4, xmm1
	subsd xmm5, xmm2
	movlpd [edi + eax*8], xmm3
	movlpd [edi + eax*8 + 8], xmm4
	movlpd [edi + eax*8 + 16], xmm5
.i0300_updateouterdata:
	mov   ecx, [esp + _ii3]
	mov   edi, [ebp + _faction]
	mov   esi, [ebp + _fshift]
	mov   edx, [esp + _is3]

	/* accumulate i forces in xmm0, xmm1, xmm2 */
	movapd xmm0, [esp + _fix]
	movapd xmm1, [esp + _fiy]
	movapd xmm2, [esp + _fiz]

	movhlps xmm3, xmm0
	movhlps xmm4, xmm1
	movhlps xmm5, xmm2
	addsd  xmm0, xmm3
	addsd  xmm1, xmm4
	addsd  xmm2, xmm5 /* sum is in low xmm0-xmm2 */

	/* increment i force */
	movsd  xmm3, [edi + ecx*8]
	movsd  xmm4, [edi + ecx*8 + 8]
	movsd  xmm5, [edi + ecx*8 + 16]
	addsd  xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm5, xmm2
	movsd  [edi + ecx*8],     xmm3
	movsd  [edi + ecx*8 + 8], xmm4
	movsd  [edi + ecx*8 + 16], xmm5

	/* increment fshift force */ 
	movsd  xmm3, [esi + edx*8]
	movsd  xmm4, [esi + edx*8 + 8]
	movsd  xmm5, [esi + edx*8 + 16]
	addsd xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm5, xmm2
	movsd  [esi + edx*8],     xmm3
	movsd  [esi + edx*8 + 8], xmm4
	movsd  [esi + edx*8 + 16], xmm5

	/* get group index for i particle */
	mov   edx, [ebp + _gid]      /* get group index for this i particle */
	mov   edx, [edx]
	add   [ebp + _gid],  4  /* advance pointer */

	/* accumulate total lj energy and update it */
	movapd xmm7, [esp + _vnbtot]
	/* accumulate */
	movhlps xmm6, xmm7
	addsd  xmm7, xmm6	/* low xmm7 has the sum now */
	
	/* add earlier value from mem */
	mov   eax, [ebp + _Vnb]
	addsd xmm7, [eax + edx*8] 
	/* move back to mem */
	movsd [eax + edx*8], xmm7 
	
	/* finish if last */
	mov   ecx, [ebp + _nri]
	dec ecx
	jecxz .i0300_end
	/* not last, iterate once more! */ 
	mov [ebp + _nri], ecx
	jmp .i0300_outer
.i0300_end:
	emms
	mov eax, [esp + _salign]
	add esp, eax
	add esp, 296
	pop edi
	pop esi
        pop edx
        pop ecx
        pop ebx
        pop eax
	leave
	ret

	


.globl inl1000_sse2
	.type inl1000_sse2,@function
inl1000_sse2:	
.equ		_nri,		8
.equ		_iinr,		12
.equ		_jindex,	16
.equ		_jjnr,		20
.equ		_shift,		24
.equ		_shiftvec,	28
.equ		_fshift,	32
.equ		_gid,		36
.equ		_pos,		40		
.equ		_faction,	44
.equ		_charge,	48
.equ		_facel,		52 /* double */
.equ		_Vc,		60
	/* stack offsets for local variables */ 
	/* bottom of stack is cache-aligned for sse2 use */
.equ		_ix,            0
.equ		_iy,	        16
.equ		_iz,            32
.equ		_iq,            48
.equ		_dx,            64
.equ		_dy,            80
.equ		_dz,            96
.equ		_vctot,         112
.equ		_fix,           128
.equ		_fiy,           144
.equ		_fiz,           160
.equ		_half,          176
.equ		_three,         192
.equ		_is3,           208
.equ		_ii3,           212
.equ		_innerjjnr,     216
.equ		_innerk,        220		
.equ		_salign,        224							
	push ebp
	mov ebp,esp	
        push eax
        push ebx
        push ecx
        push edx
	push esi
	push edi
	sub esp, 228		/* local stack space */
	mov  eax, esp
	and  eax, 0xf
	sub esp, eax
	mov [esp + _salign], eax

	emms

	movupd xmm0, [sse2_half]
	movupd xmm1, [sse2_three]
	movapd [esp + _half],  xmm0
	movapd [esp + _three], xmm1

	/* assume we have at least one i particle - start directly */	
i1000_outer:
	mov   eax, [ebp + _shift]      /* eax = pointer into shift[] */
	mov   ebx, [eax]		/* ebx=shift[n] */
	add   [ebp + _shift],  4  /* advance pointer one step */
	
	lea   ebx, [ebx + ebx*2]        /* ebx=3*is */
	mov   [esp + _is3],ebx    	/* store is3 */

	mov   eax, [ebp + _shiftvec]   /* eax = base of shiftvec[] */

	movsd xmm0, [eax + ebx*8]
	movsd xmm1, [eax + ebx*8 + 8]
	movsd xmm2, [eax + ebx*8 + 16] 

	mov   ecx, [ebp + _iinr]       /* ecx = pointer into iinr[] */	
	add   [ebp + _iinr],  4   /* advance pointer */
	mov   ebx, [ecx]	        /* ebx =ii */

	mov   edx, [ebp + _charge]
	movsd xmm3, [edx + ebx*8]	
	mulsd xmm3, [ebp + _facel]
	shufpd xmm3, xmm3, 0	
	lea   ebx, [ebx + ebx*2]	/* ebx = 3*ii=ii3 */
	mov   eax, [ebp + _pos]        /* eax = base of pos[] */ 

	addsd xmm0, [eax + ebx*8]
	addsd xmm1, [eax + ebx*8 + 8]
	addsd xmm2, [eax + ebx*8 + 16]

	movapd [esp + _iq], xmm3
	
	shufpd xmm0, xmm0, 0
	shufpd xmm1, xmm1, 0
	shufpd xmm2, xmm2, 0

	movapd [esp + _ix], xmm0
	movapd [esp + _iy], xmm1
	movapd [esp + _iz], xmm2

	mov   [esp + _ii3], ebx
	
	/* clear vctot and i forces */
	xorpd xmm4, xmm4
	movapd [esp + _vctot], xmm4
	movapd [esp + _fix], xmm4
	movapd [esp + _fiy], xmm4
	movapd [esp + _fiz], xmm4
	
	mov   eax, [ebp + _jindex]
	mov   ecx, [eax]	         /* jindex[n] */
	mov   edx, [eax + 4]	         /* jindex[n+1] */
	add   [ebp + _jindex],  4
	sub   edx, ecx                   /* number of innerloop atoms */

	mov   esi, [ebp + _pos]
	mov   edi, [ebp + _faction]	
	mov   eax, [ebp + _jjnr]
	shl   ecx, 2
	add   eax, ecx
	mov   [esp + _innerjjnr], eax     /* pointer to jjnr[nj0] */
	sub   edx,  2
	mov   [esp + _innerk], edx        /* number of innerloop atoms */
	jge   i1000_unroll_loop
	jmp   i1000_checksingle
i1000_unroll_loop:	
	/* twice unrolled innerloop here */
	mov   edx, [esp + _innerjjnr]     /* pointer to jjnr[k] */
	mov   eax, [edx]	
	mov   ebx, [edx + 4]
	add   [esp + _innerjjnr],  8 /* advance pointer (unrolled 2) */

	mov esi, [ebp + _charge]        /* base of charge[] */
	
	movlpd xmm3, [esi + eax*8]	/* jq A */
	movhpd xmm3, [esi + ebx*8]	/* jq B */

	movapd xmm5, [esp + _iq]
	
	mulpd xmm3, xmm5		/* qq */
	
	mov esi, [ebp + _pos]       /* base of pos[] */

	lea   eax, [eax + eax*2]         /* replace jnr with j3 */
	lea   ebx, [ebx + ebx*2]	

	/* move two coordinates to xmm0-xmm2 */	
	movlpd xmm0, [esi + eax*8]
	movlpd xmm1, [esi + eax*8 + 8]
	movlpd xmm2, [esi + eax*8 + 16]
	movhpd xmm0, [esi + ebx*8]
	movhpd xmm1, [esi + ebx*8 + 8]
	movhpd xmm2, [esi + ebx*8 + 16]		

	mov    edi, [ebp + _faction]
	
	/* move _ix-iz to xmm4-xmm6 */
	movapd xmm4, [esp + _ix]
	movapd xmm5, [esp + _iy]
	movapd xmm6, [esp + _iz]

	/* calc dr */
	subpd xmm4, xmm0
	subpd xmm5, xmm1
	subpd xmm6, xmm2

	/* store dr */
	movapd [esp + _dx], xmm4
	movapd [esp + _dy], xmm5
	movapd [esp + _dz], xmm6
	/* square it */
	mulpd xmm4,xmm4
	mulpd xmm5,xmm5
	mulpd xmm6,xmm6
	addpd xmm4, xmm5
	addpd xmm4, xmm6
	/* rsq in xmm4  */

	cvtpd2ps xmm5, xmm4	
	rsqrtps xmm5, xmm5
	cvtps2pd xmm2, xmm5	/* lu in low xmm2 */

	/* lookup seed in xmm2 */
	movapd xmm5, xmm2	/* copy of lu */
	mulpd xmm2, xmm2	/* lu*lu */
	movapd xmm1, [esp + _three]
	mulpd xmm2, xmm4	/* rsq*lu*lu */			
	movapd xmm0, [esp + _half]
	subpd xmm1, xmm2	/* 30-rsq*lu*lu */
	mulpd xmm1, xmm5	
	mulpd xmm1, xmm0	/* xmm0=iter1 of rinv (new lu) */

	movapd xmm5, xmm1	/* copy of lu */
	mulpd xmm1, xmm1	/* lu*lu */
	movapd xmm2, [esp + _three]
	mulpd xmm1, xmm4	/* rsq*lu*lu */			
	movapd xmm0, [esp + _half]
	subpd xmm2, xmm1	/* 30-rsq*lu*lu */
	mulpd xmm2, xmm5	
	mulpd xmm0, xmm2	/* xmm0=iter2 of rinv (new lu) */
	movapd xmm4, xmm0
	mulpd  xmm4, xmm4	/* xmm4=rinvsq */

	
	movapd xmm5, [esp + _vctot]
	mulpd  xmm3, xmm0	/* xmm3=vcoul */
	mulpd  xmm4, xmm3	/* xmm4=fscal */
	addpd  xmm5, xmm3

	movapd xmm0, [esp + _dx]
	movapd xmm1, [esp + _dy]
	movapd xmm2, [esp + _dz]

	movapd [esp + _vctot], xmm5

	mulpd  xmm0, xmm4
	mulpd  xmm1, xmm4
	mulpd  xmm2, xmm4
	/* xmm0-xmm2 contains tx-tz (partial force) */
	/* now update f_i */
	movapd xmm3, [esp + _fix]
	movapd xmm4, [esp + _fiy]
	movapd xmm5, [esp + _fiz]
	addpd  xmm3, xmm0
	addpd  xmm4, xmm1
	addpd  xmm5, xmm2
	movapd [esp + _fix], xmm3
	movapd [esp + _fiy], xmm4
	movapd [esp + _fiz], xmm5
	/* the fj's - start by accumulating forces from memory */
	movlpd xmm3, [edi + eax*8]
	movlpd xmm4, [edi + eax*8 + 8]
	movlpd xmm5, [edi + eax*8 + 16]
	movhpd xmm3, [edi + ebx*8]
	movhpd xmm4, [edi + ebx*8 + 8]
	movhpd xmm5, [edi + ebx*8 + 16]
	subpd xmm3, xmm0
	subpd xmm4, xmm1
	subpd xmm5, xmm2
	movlpd [edi + eax*8], xmm3
	movlpd [edi + eax*8 + 8], xmm4
	movlpd [edi + eax*8 + 16], xmm5
	movhpd [edi + ebx*8], xmm3
	movhpd [edi + ebx*8 + 8], xmm4
	movhpd [edi + ebx*8 + 16], xmm5
	
	/* should we do one more iteration? */
	sub   [esp + _innerk],  2
	jl    i1000_checksingle
	jmp   i1000_unroll_loop

i1000_checksingle:				
	mov   edx, [esp + _innerk]
	and   edx, 1
	jnz    i1000_dosingle
	jmp    i1000_updateouterdata
i1000_dosingle:			
	mov esi, [ebp + _charge]
	mov edi, [ebp + _pos]

	mov edx, [esp + _innerjjnr]     /* pointer to jjnr[k] */
	mov eax, [edx]	

	xorpd xmm3, xmm3
	movsd xmm3, [esi + eax*8]	/* jq A */
	movapd xmm5, [esp + _iq]
	unpcklpd xmm3, xmm6
	mulpd xmm3, xmm5		/* qq */
	
	mov esi, [ebp + _pos]       /* base of pos[] */

	lea   eax, [eax + eax*2]         /* replace jnr with j3 */

	/* move two coordinates to xmm0-xmm2 */	
	movlpd xmm0, [esi + eax*8]
	movlpd xmm1, [esi + eax*8 + 8]
	movlpd xmm2, [esi + eax*8 + 16]

	mov    edi, [ebp + _faction]

	/* move _ix-iz to xmm4-xmm6 */
	movapd xmm4, [esp + _ix]
	movapd xmm5, [esp + _iy]
	movapd xmm6, [esp + _iz]

	/* calc dr */
	subsd xmm4, xmm0
	subsd xmm5, xmm1
	subsd xmm6, xmm2

	/* store dr */
	movlpd [esp + _dx], xmm4
	movlpd [esp + _dy], xmm5
	movlpd [esp + _dz], xmm6
	/* square it */
	mulsd xmm4,xmm4
	mulsd xmm5,xmm5
	mulsd xmm6,xmm6
	addsd xmm4, xmm5
	addsd xmm4, xmm6
	/* rsq in xmm4 */

	cvtsd2ss xmm5, xmm4	
	rsqrtss xmm5, xmm5
	cvtss2sd xmm2, xmm5	/* lu in low xmm2 */

