	movapd xmm5, xmm2	/* copy of lu */
	mulsd xmm2, xmm2	/* lu*lu */
	movapd xmm1, [esp + _three]
	mulsd xmm2, xmm4	/* rsq*lu*lu */			
	movapd xmm0, [esp + _half]
