	addsd  xmm0, [esp + _fjx]
	addsd  xmm1, [esp + _fjy]
	addsd  xmm2, [esp + _fjz]

	movlpd xmm3, [edi + eax*8]
	movlpd xmm4, [edi + eax*8 + 8]
	movlpd xmm5, [edi + eax*8 + 16]
	subsd xmm3, xmm0
	subsd xmm4, xmm1
	subsd xmm5, xmm2
