	movapd xmm5, xmm1	/* copy of lu */
	mulpd xmm1, xmm1	/* lu*lu */
	movapd xmm2, [esp + _three]
	mulpd xmm1, xmm4	/* rsq*lu*lu */			
	movapd xmm0, [esp + _half]
	subpd xmm2, xmm1	/* 30-rsq*lu*lu */
	mulpd xmm2, xmm5	
