	subsd xmm4, xmm0
	subsd xmm5, xmm1
	subsd xmm6, xmm2

	/* store dr */
	movapd [esp + _dx], xmm4
	movapd [esp + _dy], xmm5
	movapd [esp + _dz], xmm6
