.equ		_type,		64
.equ		_ntype,		68
.equ		_nbfp,		72	
.equ		_Vnb,		76	
	/* stack offsets for local variables */ 
	/* bottom of stack is cache-aligned for sse2 use */
.equ		_ixO,	        0
.equ		_iyO,	        16
.equ		_izO,           32
.equ		_ixH1,	        48
.equ		_iyH1,	        64
.equ		_izH1,          80
.equ		_ixH2,	        96
.equ		_iyH2,	        112
.equ		_izH2,          128
