.equ		_iqH,           160 
.equ		_dxO,           176
.equ		_dyO,           192
.equ		_dzO,           208	
.equ		_dxH1,          224
.equ		_dyH1,          240
.equ		_dzH1,          256	
.equ		_dxH2,          272
.equ		_dyH2,          288
.equ		_dzH2,          304	
.equ		_qqO,           320
.equ		_qqH,           336
.equ		_c6,            352
.equ		_c12,           368
.equ		_six,           384
.equ		_twelve,        400		 
.equ		_vctot,         416
.equ		_vnbtot,        432
.equ		_fixO,          448
.equ		_fiyO,          464
.equ		_fizO,          480
.equ		_fixH1,         496
.equ		_fiyH1,         512
.equ		_fizH1,         528
.equ		_fixH2,         544
.equ		_fiyH2,         560
.equ		_fizH2,         576
.equ		_fjx,	        592
.equ		_fjy,           608
.equ		_fjz,           624
.equ		_half,          640
.equ		_three,         656
.equ		_is3,           672
.equ		_ii3,           676
.equ		_ntia,	        680	
.equ		_innerjjnr,     684
.equ		_innerk,        688
