	movapd xmm1, [esp + _krsqO]
	addsd  xmm0, xmm1
	mulsd  xmm1, [esp + _two]
	subsd  xmm0, [esp + _crf] /* xmm0=rinv+ krsq-crf */
	subsd  xmm7, xmm1
	mulsd  xmm0, [esp + _qqO]
	mulsd  xmm7, [esp + _qqO]
	addsd  xmm2, xmm7

