	movapd  xmm4, xmm6	
	mulsd   xmm4, xmm4	/* xmm6=rinv, xmm4=rinvsq */
	movapd  xmm7, xmm6
	movapd  xmm0, [esp + _krsqH1]
	addsd   xmm6, xmm0	/* xmm6=rinv+ krsq */
