	subsd   xmm6, [esp + _crf]
	subsd   xmm7, xmm0	/* xmm7=rinv-2*krsq */
	mulsd   xmm6, [esp + _qqH] /* vcoul */
	mulsd   xmm7, [esp + _qqH]
	mulsd  xmm4, xmm7		/* total fsH1 in xmm4 */
	
