.equ		_iqH,		160 
.equ		_dxO,		176
.equ		_dyO,		192
.equ		_dzO,		208	
.equ		_dxH1,		224
.equ		_dyH1,		240
.equ		_dzH1,		256	
.equ		_dxH2,		272
.equ		_dyH2,		288
.equ		_dzH2,		304	
.equ		_qqO,		320
.equ		_qqH,		336
.equ		_vctot,		352
.equ		_fixO,		384
.equ		_fiyO,		400
.equ		_fizO,		416
.equ		_fixH1,		432
.equ		_fiyH1,		448
.equ		_fizH1,		464
.equ		_fixH2,		480
.equ		_fiyH2,		496
.equ		_fizH2,		512
.equ		_fjx,		528
.equ		_fjy,		544
.equ		_fjz,		560
