	mulsd xmm0, xmm7	/* fsOH2 */
	movapd xmm1, xmm0
	movapd xmm2, xmm0

	movapd xmm3, [esp + _fjxH1]
	movapd xmm4, [esp + _fjyH1]
	movapd xmm5, [esp + _fjzH1]
	mulsd xmm0, [esp + _dxH2H1]
	mulsd xmm1, [esp + _dyH2H1]
	mulsd xmm2, [esp + _dzH2H1]
	subsd xmm3, xmm0
	subsd xmm4, xmm1
	subsd xmm5, xmm2
	addsd xmm0, [esp + _fixH2]
	addsd xmm1, [esp + _fiyH2]
	addsd xmm2, [esp + _fizH2]
	movlpd [esp + _fjxH1], xmm3
	movlpd [esp + _fjyH1], xmm4
	movlpd [esp + _fjzH1], xmm5
	movlpd [esp + _fixH2], xmm0
	movlpd [esp + _fiyH2], xmm1
	movlpd [esp + _fizH2], xmm2

	/* H2-H2 interaction */
	movapd xmm0, [esp + _rinvH2H2]
	movapd xmm7, xmm0	/* xmm7=rinv */
	movapd xmm5, [esp + _krf]	
	movapd xmm1, xmm0
	mulsd  xmm5, [esp + _rsqH2H2] /* xmm5=krsq */
	movapd xmm4, xmm5
	addsd  xmm4, xmm7	/* xmm4=r inv+ krsq */
	subsd  xmm4, [esp + _crf]
	mulsd xmm0, xmm0
	mulsd  xmm4, [esp + _qqHH] /* xmm4=voul=qq*(rinv+ krsq) */
	mulsd  xmm5, [esp + _two]
	subsd  xmm7, xmm5	/* xmm7=rinv-2*krsq */
	mulsd  xmm7, [esp + _qqHH] /* xmm7 = coul part of fscal */
	addsd  xmm6, xmm4	/* add to local vctot */
	mulsd xmm0, xmm7	/* fsOH2 */
	movapd xmm1, xmm0
	movapd xmm2, xmm0

	movapd xmm1, xmm0
	movlpd [esp + _vctot], xmm6
	movapd xmm2, xmm0
	
	movapd xmm3, [esp + _fjxH2]
	movapd xmm4, [esp + _fjyH2]
	movapd xmm5, [esp + _fjzH2]
	mulsd xmm0, [esp + _dxH2H2]
	mulsd xmm1, [esp + _dyH2H2]
	mulsd xmm2, [esp + _dzH2H2]
	subsd xmm3, xmm0
	subsd xmm4, xmm1
	subsd xmm5, xmm2
	addsd xmm0, [esp + _fixH2]
	addsd xmm1, [esp + _fiyH2]
	addsd xmm2, [esp + _fizH2]
	movlpd [esp + _fjxH2], xmm3
	movlpd [esp + _fjyH2], xmm4
	movlpd [esp + _fjzH2], xmm5
	movlpd [esp + _fixH2], xmm0
	movlpd [esp + _fiyH2], xmm1
	movlpd [esp + _fizH2], xmm2

	mov edi, [ebp + _faction]
	/* Did all interactions - now update j forces */
	movlpd xmm0, [edi + eax*8]
	movlpd xmm1, [edi + eax*8 + 8]
	movlpd xmm2, [edi + eax*8 + 16]
	movlpd xmm3, [edi + eax*8 + 24]
	movlpd xmm4, [edi + eax*8 + 32]
	movlpd xmm5, [edi + eax*8 + 40]
	movlpd xmm6, [edi + eax*8 + 48]
	movlpd xmm7, [edi + eax*8 + 56]
	addsd xmm0, [esp + _fjxO]
	addsd xmm1, [esp + _fjyO]
	addsd xmm2, [esp + _fjzO]
	addsd xmm3, [esp + _fjxH1]
	addsd xmm4, [esp + _fjyH1]
	addsd xmm5, [esp + _fjzH1]
	addsd xmm6, [esp + _fjxH2]
	addsd xmm7, [esp + _fjyH2]
	movlpd [edi + eax*8], xmm0
	movlpd [edi + eax*8 + 8], xmm1
	movlpd [edi + eax*8 + 16], xmm2
	movlpd [edi + eax*8 + 24], xmm3
	movlpd [edi + eax*8 + 32], xmm4
	movlpd [edi + eax*8 + 40], xmm5
	movlpd [edi + eax*8 + 48], xmm6
	movlpd [edi + eax*8 + 56], xmm7

	movlpd xmm0, [edi + eax*8 + 64]
	addsd xmm0, [esp + _fjzH2]
	movlpd [edi + eax*8 + 64], xmm0
	
.i2030_updateouterdata:
	mov   ecx, [esp + _ii3]
	mov   edi, [ebp + _faction]
	mov   esi, [ebp + _fshift]
	mov   edx, [esp + _is3]

	/* accumulate  Oi forces in xmm0, xmm1, xmm2 */
	movapd xmm0, [esp + _fixO]
	movapd xmm1, [esp + _fiyO]
	movapd xmm2, [esp + _fizO]

	movhlps xmm3, xmm0
	movhlps xmm4, xmm1
	movhlps xmm5, xmm2
	addsd  xmm0, xmm3
	addsd  xmm1, xmm4
	addsd  xmm2, xmm5 /* sum is in low xmm0-xmm2 */

	movapd xmm3, xmm0	
	movapd xmm4, xmm1	
	movapd xmm5, xmm2	

	/* increment i force */
	movsd  xmm3, [edi + ecx*8]
	movsd  xmm4, [edi + ecx*8 + 8]
	movsd  xmm5, [edi + ecx*8 + 16]
	addsd  xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm5, xmm2
	movsd  [edi + ecx*8],     xmm3
	movsd  [edi + ecx*8 + 8], xmm4
	movsd  [edi + ecx*8 + 16], xmm5

	/* accumulate force in xmm6/xmm7 for fshift */
	movapd xmm6, xmm0
	movsd xmm7, xmm2
	unpcklpd xmm6, xmm1

	/* accumulate H1i forces in xmm0, xmm1, xmm2 */
	movapd xmm0, [esp + _fixH1]
	movapd xmm1, [esp + _fiyH1]
	movapd xmm2, [esp + _fizH1]

	movhlps xmm3, xmm0
	movhlps xmm4, xmm1
	movhlps xmm5, xmm2
	addsd  xmm0, xmm3
	addsd  xmm1, xmm4
	addsd  xmm2, xmm5 /* sum is in low xmm0-xmm2 */

	/* increment i force */
	movsd  xmm3, [edi + ecx*8 + 24]
	movsd  xmm4, [edi + ecx*8 + 32]
	movsd  xmm5, [edi + ecx*8 + 40]
	addsd  xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm5, xmm2
	movsd  [edi + ecx*8 + 24], xmm3
	movsd  [edi + ecx*8 + 32], xmm4
	movsd  [edi + ecx*8 + 40], xmm5

	/* accumulate force in xmm6/xmm7 for fshift */
	addsd xmm7, xmm2
	unpcklpd xmm0, xmm1
	addpd xmm6, xmm0

	/* accumulate H2i forces in xmm0, xmm1, xmm2 */
	movapd xmm0, [esp + _fixH2]
	movapd xmm1, [esp + _fiyH2]
	movapd xmm2, [esp + _fizH2]

	movhlps xmm3, xmm0
	movhlps xmm4, xmm1
	movhlps xmm5, xmm2
	addsd  xmm0, xmm3
	addsd  xmm1, xmm4
	addsd  xmm2, xmm5 /* sum is in low xmm0-xmm2 */

	movapd xmm3, xmm0	
	movapd xmm4, xmm1	
	movapd xmm5, xmm2	

	/* increment i force */
	movsd  xmm3, [edi + ecx*8 + 48]
	movsd  xmm4, [edi + ecx*8 + 56]
	movsd  xmm5, [edi + ecx*8 + 64]
	addsd  xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm5, xmm2
	movsd  [edi + ecx*8 + 48], xmm3
	movsd  [edi + ecx*8 + 56], xmm4
	movsd  [edi + ecx*8 + 64], xmm5

	/* accumulate force in xmm6/xmm7 for fshift */
	addsd xmm7, xmm2
	unpcklpd xmm0, xmm1
	addpd xmm6, xmm0

	/* increment fshift force */
	movupd xmm3, [esi + edx*8]
	movsd  xmm4, [esi + edx*8 + 16]
	addpd  xmm3, xmm6
	addsd  xmm4, xmm7
	movupd [esi + edx*8],    xmm3
	movsd  [esi + edx*8 + 16], xmm4

	mov   edx, [ebp + _gid]  
	mov   edx, [edx]
	add   [ebp + _gid],  4	

	/* accumulate total potential energy and update it */
	movapd xmm7, [esp + _vctot]
	/* accumulate */
	movhlps xmm6, xmm7
	addsd  xmm7, xmm6	/* low xmm7 has the sum now */
        
	/* add earlier value from mem */
	mov   eax, [ebp + _Vc]
	addsd xmm7, [eax + edx*8] 
	/* move back to mem */
	movsd [eax + edx*8], xmm7 
	
	/* finish if last */
	mov   ecx, [ebp + _nri]
	dec ecx
	jecxz .i2030_end
	/* not last, iterate once more! */ 
	mov [ebp + _nri], ecx
	jmp .i2030_outer
.i2030_end:
	emms
	mov eax, [esp + _salign]
	add esp, eax
	add esp, 1460
	pop edi
	pop esi
        pop edx
        pop ecx
        pop ebx
        pop eax
	leave
	ret

	
	

.globl inl3000_sse2
	.type inl3000_sse2,@function
inl3000_sse2:	
.equ		_nri,		8
.equ		_iinr,		12
.equ		_jindex,	16
.equ		_jjnr,		20
.equ		_shift,		24
.equ		_shiftvec,	28
.equ		_fshift,	32
.equ		_gid,		36
.equ		_pos,		40		
.equ		_faction,	44
.equ		_charge,	48
.equ		_facel,		52 /* double */ 
.equ		_Vc,		60	
.equ		_tabscale,	64 /* double */ 
.equ		_VFtab,		72
	/* stack offsets for local variables */ 
	/* bottom of stack is cache-aligned for sse2 use */
.equ		_ix,		0
.equ		_iy,		16
.equ		_iz,		32
.equ		_iq,		48
.equ		_dx,		64
.equ		_dy,		80
.equ		_dz,		96
.equ		_two,		112
.equ		_tsc,		128
.equ		_qq,		144	
.equ		_fs,		160
.equ		_vctot,		176
.equ		_fix,		192
.equ		_fiy,		208
.equ		_fiz,		224
.equ		_half,		240
.equ		_three,		256
.equ		_is3,		272
.equ		_ii3,		276
.equ		_innerjjnr,	280
.equ		_innerk,	284
.equ		_salign,	288
	push ebp
	mov ebp,esp	
        push eax
        push ebx
        push ecx
        push edx
	push esi
	push edi
	sub esp, 292		/* local stack space */
	mov  eax, esp
	and  eax, 0xf
	sub esp, eax
	mov [esp + _salign], eax

	emms

	movupd xmm0, [sse2_half]
	movupd xmm1, [sse2_two]
	movupd xmm2, [sse2_three]
	movsd xmm3, [ebp + _tabscale]
	movapd [esp + _half],  xmm0
	movapd [esp + _two], xmm1
	movapd [esp + _three],  xmm2
	shufpd xmm3, xmm3, 0
	movapd [esp + _tsc], xmm3

	/* assume we have at least one i particle - start directly */	
.i3000_outer:
	mov   eax, [ebp + _shift]      /* eax = pointer into shift[] */
	mov   ebx, [eax]		/* ebx=shift[n] */
	add   [ebp + _shift],  4  /* advance pointer one step */
	
	lea   ebx, [ebx + ebx*2]        /* ebx=3*is */
	mov   [esp + _is3],ebx    	/* store is3 */

	mov   eax, [ebp + _shiftvec]   /* eax = base of shiftvec[] */

	movsd xmm0, [eax + ebx*8]
	movsd xmm1, [eax + ebx*8 + 8]
	movsd xmm2, [eax + ebx*8 + 16] 

	mov   ecx, [ebp + _iinr]       /* ecx = pointer into iinr[] */	
	add   [ebp + _iinr],  4   /* advance pointer */
	mov   ebx, [ecx]	        /* ebx =ii */

	mov   edx, [ebp + _charge]
	movsd xmm3, [edx + ebx*8]	
	mulsd xmm3, [ebp + _facel]
	shufpd xmm3, xmm3, 0

	lea   ebx, [ebx + ebx*2]	/* ebx = 3*ii=ii3 */
	mov   eax, [ebp + _pos]        /* eax = base of pos[] */ 

	addsd xmm0, [eax + ebx*8]
	addsd xmm1, [eax + ebx*8 + 8]
	addsd xmm2, [eax + ebx*8 + 16]

	movapd [esp + _iq], xmm3
	
	shufpd xmm0, xmm0, 0
	shufpd xmm1, xmm1, 0
	shufpd xmm2, xmm2, 0

	movapd [esp + _ix], xmm0
	movapd [esp + _iy], xmm1
	movapd [esp + _iz], xmm2

	mov   [esp + _ii3], ebx
	
	/* clear vctot and i forces */
	xorpd xmm4, xmm4
	movapd [esp + _vctot], xmm4
	movapd [esp + _fix], xmm4
	movapd [esp + _fiy], xmm4
	movapd [esp + _fiz], xmm4
	
	mov   eax, [ebp + _jindex]
	mov   ecx, [eax]	         /* jindex[n] */
	mov   edx, [eax + 4]	         /* jindex[n+1] */
	add   [ebp + _jindex],  4
	sub   edx, ecx                   /* number of innerloop atoms */

	mov   esi, [ebp + _pos]
	mov   edi, [ebp + _faction]	
	mov   eax, [ebp + _jjnr]
	shl   ecx, 2
	add   eax, ecx
	mov   [esp + _innerjjnr], eax     /* pointer to jjnr[nj0] */
	sub   edx,  2
	mov   [esp + _innerk], edx        /* number of innerloop atoms */
	jge   .i3000_unroll_loop
	jmp   .i3000_checksingle
.i3000_unroll_loop:
	/* twice unrolled innerloop here */
	mov   edx, [esp + _innerjjnr]   /* pointer to jjnr[k] */
	mov   eax, [edx]
	mov   ebx, [edx + 4]
	add   [esp + _innerjjnr], 8	/* advance pointer (unrolled 2) */

	mov esi, [ebp + _charge]        /* base of charge[] */

	movlpd xmm3, [esi + eax*8]
	movhpd xmm3, [esi + ebx*8]

	movapd xmm2, [esp + _iq]
	mulpd  xmm3, xmm2
	movapd [esp + _qq], xmm3	
	
	mov esi, [ebp + _pos]		/* base of pos[] */

	lea   eax, [eax + eax*2]         /* replace jnr with j3 */
	lea   ebx, [ebx + ebx*2]	

	/* move two coordinates to xmm0-xmm2 */
	movlpd xmm0, [esi + eax*8]
	movlpd xmm1, [esi + eax*8 + 8]
	movlpd xmm2, [esi + eax*8 + 16]
	movhpd xmm0, [esi + ebx*8]
	movhpd xmm1, [esi + ebx*8 + 8]
	movhpd xmm2, [esi + ebx*8 + 16]		

	mov    edi, [ebp + _faction]
	
	/* move _ix-iz to xmm4-xmm6 */
	movapd xmm4, [esp + _ix]
	movapd xmm5, [esp + _iy]
	movapd xmm6, [esp + _iz]

	/* calc dr */
	subpd xmm4, xmm0
	subpd xmm5, xmm1
	subpd xmm6, xmm2

	/* store dr */
	movapd [esp + _dx], xmm4
	movapd [esp + _dy], xmm5
	movapd [esp + _dz], xmm6
	/* square it */
	mulpd xmm4,xmm4
	mulpd xmm5,xmm5
	mulpd xmm6,xmm6
	addpd xmm4, xmm5
	addpd xmm4, xmm6
	/* rsq in xmm4 */

	cvtpd2ps xmm5, xmm4	
	rsqrtps xmm5, xmm5
	cvtps2pd xmm2, xmm5	/* lu in low xmm2 */

	/* lookup seed in xmm2 */
	movapd xmm5, xmm2	/* copy of lu */
	mulpd xmm2, xmm2	/* lu*lu */
	movapd xmm1, [esp + _three]
	mulpd xmm2, xmm4	/* rsq*lu*lu */			
	movapd xmm0, [esp + _half]
	subpd xmm1, xmm2	/* 30-rsq*lu*lu */
	mulpd xmm1, xmm5	
	mulpd xmm1, xmm0	/* xmm0=iter1 of rinv (new lu) */

	movapd xmm5, xmm1	/* copy of lu */
	mulpd xmm1, xmm1	/* lu*lu */
	movapd xmm2, [esp + _three]
	mulpd xmm1, xmm4	/* rsq*lu*lu */			
	movapd xmm0, [esp + _half]
	subpd xmm2, xmm1	/* 30-rsq*lu*lu */
	mulpd xmm2, xmm5	
	mulpd xmm0, xmm2	/* xmm0=iter2 of rinv (new lu) */
	mulpd xmm4, xmm0	/* xmm4=r */
	mulpd xmm4, [esp + _tsc]

	cvttpd2pi mm6, xmm4	/* mm6 = lu idx */
	cvtpi2pd xmm5, mm6
	subpd xmm4, xmm5
	movapd xmm1, xmm4	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulpd  xmm2, xmm2	/* xmm2=eps2 */
	
	pslld mm6, 2		/* idx *= 4 */
	
	movd mm0, eax	
	movd mm1, ebx

	mov  esi, [ebp + _VFtab]
	movd eax, mm6
	psrlq mm6, 32
	movd ebx, mm6		/* indices in eax/ebx */

	movupd xmm4, [esi + eax*8]	/* Y1 F1 */	
	movupd xmm3, [esi + ebx*8]	/* Y2 F2 */
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 Y2 */
	unpckhpd xmm5, xmm3	/* F1 F2 */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */	
	movupd xmm3, [esi + ebx*8 + 16]	/* G2 H2 */
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 G2 */
	unpckhpd xmm7, xmm3	/* H1 H2 */
	/* coulomb table ready, in xmm4-xmm7 */ 		
	mulpd  xmm6, xmm1	/* xmm6=Geps */
	mulpd  xmm7, xmm2	/* xmm7=Heps2 */
	addpd  xmm5, xmm6
	addpd  xmm5, xmm7	/* xmm5=Fp */	
	mulpd  xmm7, [esp + _two]	/* two*Heps2 */
	movapd xmm3, [esp + _qq]
	addpd  xmm7, xmm6
	addpd  xmm7, xmm5 /* xmm7=FF */
	mulpd  xmm5, xmm1 /* xmm5=eps*Fp */
	addpd  xmm5, xmm4 /* xmm5=VV */
	mulpd  xmm5, xmm3 /* vcoul=qq*VV */ 
	mulpd  xmm3, xmm7 /* fijC=FF*qq */
	/* at this point mm5 contains vcoul and mm3 fijC */
	/* increment vcoul - then we can get rid of mm5 */
	/* update vctot */
	addpd  xmm5, [esp + _vctot]
	movapd [esp + _vctot], xmm5 

	xorpd  xmm4, xmm4

	mulpd xmm3, [esp + _tsc]
	mulpd xmm3, xmm0
	subpd  xmm4, xmm3

	movapd xmm0, [esp + _dx]
	movapd xmm1, [esp + _dy]
	movapd xmm2, [esp + _dz]

	movd eax, mm0	
	movd ebx, mm1

	mov    edi, [ebp + _faction]
	mulpd  xmm0, xmm4
	mulpd  xmm1, xmm4
	mulpd  xmm2, xmm4
	/* xmm0-xmm2 contains tx-tz (partial force) */
	/* now update f_i */
	movapd xmm3, [esp + _fix]
	movapd xmm4, [esp + _fiy]
	movapd xmm5, [esp + _fiz]
	addpd  xmm3, xmm0
	addpd  xmm4, xmm1
	addpd  xmm5, xmm2
	movapd [esp + _fix], xmm3
	movapd [esp + _fiy], xmm4
	movapd [esp + _fiz], xmm5
	/* the fj's - start by accumulating forces from memory */
	movlpd xmm3, [edi + eax*8]
	movlpd xmm4, [edi + eax*8 + 8]
	movlpd xmm5, [edi + eax*8 + 16]
	movhpd xmm3, [edi + ebx*8]
	movhpd xmm4, [edi + ebx*8 + 8]
	movhpd xmm5, [edi + ebx*8 + 16]
	subpd xmm3, xmm0
	subpd xmm4, xmm1
	subpd xmm5, xmm2
	movlpd [edi + eax*8], xmm3
	movlpd [edi + eax*8 + 8], xmm4
	movlpd [edi + eax*8 + 16], xmm5
	movhpd [edi + ebx*8], xmm3
	movhpd [edi + ebx*8 + 8], xmm4
	movhpd [edi + ebx*8 + 16], xmm5
		
	/* should we do one more iteration? */
	sub   [esp + _innerk],  2
	jl    .i3000_checksingle
	jmp   .i3000_unroll_loop
.i3000_checksingle:
	mov   edx, [esp + _innerk]
	and   edx, 1
	jnz    .i3000_dosingle
	jmp    .i3000_updateouterdata
.i3000_dosingle:
	mov esi, [ebp + _charge]
	mov edi, [ebp + _pos]
	mov   ecx, [esp + _innerjjnr]
	mov   eax, [ecx]	
	xorpd  xmm6, xmm6
	movlpd xmm6, [esi + eax*8]	/* xmm6(0) has the charge */	
	mulsd  xmm6, [esp + _iq]
	movapd [esp + _qq], xmm6
		
	lea   eax, [eax + eax*2]
	
	/* move coordinates to xmm0-xmm2 */
	movlpd xmm0, [edi + eax*8]
	movlpd xmm1, [edi + eax*8 + 8]
	movlpd xmm2, [edi + eax*8 + 16]

	/* move _ix-iz to xmm4-xmm6 */
	movapd xmm4, [esp + _ix]
	movapd xmm5, [esp + _iy]
	movapd xmm6, [esp + _iz]

	/* calc dr */
	subsd xmm4, xmm0
	subsd xmm5, xmm1
	subsd xmm6, xmm2

	/* store dr */
	movapd [esp + _dx], xmm4
	movapd [esp + _dy], xmm5
	movapd [esp + _dz], xmm6
	/* square it */
	mulsd xmm4,xmm4
	mulsd xmm5,xmm5
	mulsd xmm6,xmm6
	addsd xmm4, xmm5
	addsd xmm4, xmm6
	/* rsq in xmm4 */

	cvtsd2ss xmm5, xmm4	
	rsqrtss xmm5, xmm5
	cvtss2sd xmm2, xmm5	/* lu in low xmm2 */

	/* lookup seed in xmm2 */
	movapd xmm5, xmm2	/* copy of lu */
	mulsd xmm2, xmm2	/* lu*lu */
	movapd xmm1, [esp + _three]
	mulsd xmm2, xmm4	/* rsq*lu*lu */			
	movapd xmm0, [esp + _half]
	subsd xmm1, xmm2	/* 30-rsq*lu*lu */
	mulsd xmm1, xmm5	
	mulsd xmm1, xmm0	/* xmm0=iter1 of rinv (new lu) */

	movapd xmm5, xmm1	/* copy of lu */
	mulsd xmm1, xmm1	/* lu*lu */
	movapd xmm2, [esp + _three]
	mulsd xmm1, xmm4	/* rsq*lu*lu */			
	movapd xmm0, [esp + _half]
	subsd xmm2, xmm1	/* 30-rsq*lu*lu */
	mulsd xmm2, xmm5	
	mulsd xmm0, xmm2	/* xmm0=iter2 of rinv (new lu) */
	
	mulsd xmm4, xmm0	/* xmm4=r */
	mulsd xmm4, [esp + _tsc]
	
	movd mm0, eax	

	cvttsd2si eax, xmm4	/* mm6 = lu idx */
	cvtsi2sd xmm5, eax
	subsd xmm4, xmm5
	movapd xmm1, xmm4	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulsd  xmm2, xmm2	/* xmm2=eps2 */
	
	shl eax, 2		/* idx *= 4 */
	
	mov  esi, [ebp + _VFtab]

	/* Coulomb */
	movupd xmm4, [esi + eax*8]	/* Y1 F1 */
	xorpd xmm3, xmm3
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1  */
	unpckhpd xmm5, xmm3	/* F1  */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */
	xorpd xmm3, xmm3
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1  */
	unpckhpd xmm7, xmm3	/* H1  */	
	/* table ready in xmm4-xmm7 */

	mulsd  xmm6, xmm1	/* xmm6=Geps */
	mulsd  xmm7, xmm2	/* xmm7=Heps2 */
	addsd  xmm5, xmm6
	addsd  xmm5, xmm7	/* xmm5=Fp */	
	mulsd  xmm7, [esp + _two]	/* two*Heps2 */
	movapd xmm3, [esp + _qq]
	addsd  xmm7, xmm6
	addsd  xmm7, xmm5 /* xmm7=FF */
	mulsd  xmm5, xmm1 /* xmm5=eps*Fp */
	addsd  xmm5, xmm4 /* xmm5=VV */
	mulsd  xmm5, xmm3 /* vcoul=qq*VV */ 
	mulsd  xmm3, xmm7 /* fijC=FF*qq */
	/* at this point mm5 contains vcoul and mm3 fijC */
	/* increment vcoul - then we can get rid of mm5 */
	/* update vctot */
	addsd  xmm5, [esp + _vctot]
	movsd [esp + _vctot], xmm5 

	xorpd xmm4, xmm4
	movd eax, mm0

	mulpd xmm3, [esp + _tsc]
	mulpd xmm3, xmm0
	subpd  xmm4, xmm3
	mov    edi, [ebp + _faction]

	movapd xmm0, [esp + _dx]
	movapd xmm1, [esp + _dy]
	movapd xmm2, [esp + _dz]

	mulpd  xmm0, xmm4
	mulpd  xmm1, xmm4
	mulpd  xmm2, xmm4
	/* xmm0-xmm2 contains tx-tz (partial force) */
	/* now update f_i */
	movapd xmm3, [esp + _fix]
	movapd xmm4, [esp + _fiy]
	movapd xmm5, [esp + _fiz]
	addsd  xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm5, xmm2
	movlpd [esp + _fix], xmm3
	movlpd [esp + _fiy], xmm4
	movlpd [esp + _fiz], xmm5
	/* update fj */
	movlpd xmm3, [edi + eax*8]
	movlpd xmm4, [edi + eax*8 + 8]
	movlpd xmm5, [edi + eax*8 + 16]
	subsd xmm3, xmm0
	subsd xmm4, xmm1
	subsd xmm5, xmm2
	movlpd [edi + eax*8], xmm3
	movlpd [edi + eax*8 + 8], xmm4
	movlpd [edi + eax*8 + 16], xmm5

.i3000_updateouterdata:
	mov   ecx, [esp + _ii3]
	mov   edi, [ebp + _faction]
	mov   esi, [ebp + _fshift]
	mov   edx, [esp + _is3]

	/* accumulate i forces in xmm0, xmm1, xmm2 */
	movapd xmm0, [esp + _fix]
	movapd xmm1, [esp + _fiy]
	movapd xmm2, [esp + _fiz]

	movhlps xmm3, xmm0
	movhlps xmm4, xmm1
	movhlps xmm5, xmm2
	addsd  xmm0, xmm3
	addsd  xmm1, xmm4
	addsd  xmm2, xmm5 /* sum is in low xmm0-xmm2 */

	/* increment i force */
	movsd  xmm3, [edi + ecx*8]
	movsd  xmm4, [edi + ecx*8 + 8]
	movsd  xmm5, [edi + ecx*8 + 16]
	addsd  xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm5, xmm2
	movsd  [edi + ecx*8],     xmm3
	movsd  [edi + ecx*8 + 8], xmm4
	movsd  [edi + ecx*8 + 16], xmm5

	/* increment fshift force */ 
	movsd  xmm3, [esi + edx*8]
	movsd  xmm4, [esi + edx*8 + 8]
	movsd  xmm5, [esi + edx*8 + 16]
	addsd  xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm5, xmm2
	movsd  [esi + edx*8],     xmm3
	movsd  [esi + edx*8 + 8], xmm4
	movsd  [esi + edx*8 + 16], xmm5

	/* get group index for i particle */
	mov   edx, [ebp + _gid]      /* get group index for this i particle */
	mov   edx, [edx]
	add   [ebp + _gid],  4  /* advance pointer */

	/* accumulate total potential energy and update it */
	movapd xmm7, [esp + _vctot]
	/* accumulate */
	movhlps xmm6, xmm7
	addsd  xmm7, xmm6	/* low xmm7 has the sum now */

	/* add earlier value from mem */
	mov   eax, [ebp + _Vc]
	addsd xmm7, [eax + edx*8] 
	/* move back to mem */
	movsd [eax + edx*8], xmm7 
	
	/* finish if last */
	mov   ecx, [ebp + _nri]
	dec ecx
	jecxz .i3000_end
	/* not last, iterate once more! */ 
	mov [ebp + _nri], ecx
	jmp .i3000_outer
.i3000_end:
	emms
	mov eax, [esp + _salign]
	add esp, eax
	add esp, 292
	pop edi
	pop esi
        pop edx
        pop ecx
        pop ebx
        pop eax
	leave
	ret




.globl inl3020_sse2
	.type inl3020_sse2,@function
inl3020_sse2:	
.equ		_nri,		8
.equ		_iinr,		12
.equ		_jindex,	16
.equ		_jjnr,		20
.equ		_shift,		24
.equ		_shiftvec,	28
.equ		_fshift,	32
.equ		_gid,		36
.equ		_pos,		40		
.equ		_faction,	44
.equ		_charge,	48
.equ		_facel,		52 /* double */ 
.equ		_Vc,		60	
.equ		_tabscale,	64 /* double */ 	
.equ		_VFtab,		72
	/* stack offsets for local variables */ 
	/* bottom of stack is cache-aligned for sse2 use */
.equ		_ixO,		0
.equ		_iyO,		16
.equ		_izO,		32
.equ		_ixH1,		48
.equ		_iyH1,		64
.equ		_izH1,		80
.equ		_ixH2,		96
.equ		_iyH2,		112
.equ		_izH2,		128
.equ		_iqO,		144 
.equ		_iqH,		160 
.equ		_dxO,		176
.equ		_dyO,		192
.equ		_dzO,		208	
.equ		_dxH1,		224
.equ		_dyH1,		240
.equ		_dzH1,		256	
.equ		_dxH2,		272
.equ		_dyH2,		288
.equ		_dzH2,		304	
.equ		_qqO,		320
.equ		_qqH,		336
.equ		_rinvO,		352
.equ		_rinvH1,	368
.equ		_rinvH2,	384		
.equ		_rO,		400
.equ		_rH1,		416
.equ		_rH2,		432
.equ		_tsc,		448	
.equ		_two,		464
.equ		_vctot,		480
.equ		_fixO,		496
.equ		_fiyO,		512
.equ		_fizO,		528
.equ		_fixH1,		544
.equ		_fiyH1,		560
.equ		_fizH1,		576
.equ		_fixH2,		592
.equ		_fiyH2,		608
.equ		_fizH2,		624
.equ		_fjx,		640
.equ		_fjy,		656
.equ		_fjz,		672
.equ		_half,		688
.equ		_three,		704
.equ		_is3,		720
.equ		_ii3,		724
.equ		_innerjjnr,	728
.equ		_innerk,	732
.equ		_salign,	736
	push ebp
	mov ebp,esp	
        push eax
        push ebx
        push ecx
        push edx
	push esi
	push edi
	sub esp, 740		/* local stack space */
	mov  eax, esp
	and  eax, 0xf
	sub esp, eax
	mov [esp + _salign], eax

	emms

	movupd xmm0, [sse2_half]
	movupd xmm1, [sse2_two]
	movupd xmm2, [sse2_three]
	movsd xmm3, [ebp + _tabscale]
	
	movapd [esp + _half],  xmm0
	movapd [esp + _two],  xmm1
	movapd [esp + _three],  xmm2
	shufpd xmm3, xmm3, 0 
	movapd [esp + _tsc], xmm3
	
	/* assume we have at least one i particle - start directly */
	mov   ecx, [ebp + _iinr]       /* ecx = pointer into iinr[] */	
	mov   ebx, [ecx]	        /* ebx =ii */

	mov   edx, [ebp + _charge]
	movsd xmm3, [edx + ebx*8]	
	movsd xmm4, [edx + ebx*8 + 8]	
	movsd xmm5, [ebp + _facel]
	mulsd  xmm3, xmm5
	mulsd  xmm4, xmm5

	shufpd xmm3, xmm3, 0
	shufpd xmm4, xmm4, 0
	movapd [esp + _iqO], xmm3
	movapd [esp + _iqH], xmm4
	
.i3020_outer:
	mov   eax, [ebp + _shift]      /* eax = pointer into shift[] */
	mov   ebx, [eax]		/* ebx=shift[n] */
	add   [ebp + _shift],  4  /* advance pointer one step */
	
	lea   ebx, [ebx + ebx*2]        /* ebx=3*is */
	mov   [esp + _is3],ebx    	/* store is3 */

	mov   eax, [ebp + _shiftvec]   /* eax = base of shiftvec[] */

	movsd xmm0, [eax + ebx*8]
	movsd xmm1, [eax + ebx*8 + 8]
	movsd xmm2, [eax + ebx*8 + 16] 

	mov   ecx, [ebp + _iinr]       /* ecx = pointer into iinr[] */	
	add   [ebp + _iinr],  4   /* advance pointer */
	mov   ebx, [ecx]	        /* ebx =ii */

	movapd xmm3, xmm0
	movapd xmm4, xmm1
	movapd xmm5, xmm2

	lea   ebx, [ebx + ebx*2]	/* ebx = 3*ii=ii3 */
	mov   eax, [ebp + _pos]        /* eax = base of pos[] */ 
	mov   [esp + _ii3], ebx

	addsd xmm3, [eax + ebx*8]
	addsd xmm4, [eax + ebx*8 + 8]
	addsd xmm5, [eax + ebx*8 + 16]		
	shufpd xmm3, xmm3, 0
	shufpd xmm4, xmm4, 0
	shufpd xmm5, xmm5, 0
	movapd [esp + _ixO], xmm3
	movapd [esp + _iyO], xmm4
	movapd [esp + _izO], xmm5

	movsd xmm3, xmm0
	movsd xmm4, xmm1
	movsd xmm5, xmm2
	addsd xmm0, [eax + ebx*8 + 24]
	addsd xmm1, [eax + ebx*8 + 32]
	addsd xmm2, [eax + ebx*8 + 40]		
	addsd xmm3, [eax + ebx*8 + 48]
	addsd xmm4, [eax + ebx*8 + 56]
	addsd xmm5, [eax + ebx*8 + 64]		

	shufpd xmm0, xmm0, 0
	shufpd xmm1, xmm1, 0
	shufpd xmm2, xmm2, 0
	shufpd xmm3, xmm3, 0
	shufpd xmm4, xmm4, 0
	shufpd xmm5, xmm5, 0
	movapd [esp + _ixH1], xmm0
	movapd [esp + _iyH1], xmm1
	movapd [esp + _izH1], xmm2
	movapd [esp + _ixH2], xmm3
	movapd [esp + _iyH2], xmm4
	movapd [esp + _izH2], xmm5
	
	/* clear vctot and i forces */
	xorpd xmm4, xmm4
	movapd [esp + _vctot], xmm4
	movapd [esp + _fixO], xmm4
	movapd [esp + _fiyO], xmm4
	movapd [esp + _fizO], xmm4
	movapd [esp + _fixH1], xmm4
	movapd [esp + _fiyH1], xmm4
	movapd [esp + _fizH1], xmm4
	movapd [esp + _fixH2], xmm4
	movapd [esp + _fiyH2], xmm4
	movapd [esp + _fizH2], xmm4
	
	mov   eax, [ebp + _jindex]
	mov   ecx, [eax]	         /* jindex[n] */
	mov   edx, [eax + 4]	         /* jindex[n+1] */
	add   [ebp + _jindex],  4
	sub   edx, ecx                   /* number of innerloop atoms */

	mov   esi, [ebp + _pos]
	mov   edi, [ebp + _faction]	
	mov   eax, [ebp + _jjnr]
	shl   ecx, 2
	add   eax, ecx
	mov   [esp + _innerjjnr], eax     /* pointer to jjnr[nj0] */
	sub   edx,  2
	mov   [esp + _innerk], edx        /* number of innerloop atoms */
	jge   .i3020_unroll_loop
	jmp   .i3020_checksingle
.i3020_unroll_loop:
	/* twice unrolled innerloop here */
	mov   edx, [esp + _innerjjnr]     /* pointer to jjnr[k] */
	mov   eax, [edx]	
	mov   ebx, [edx + 4]

	add   [esp + _innerjjnr],  8	/* advance pointer (unrolled 2) */
	mov esi, [ebp + _charge]        /* base of charge[] */
	
	movlpd xmm3, [esi + eax*8]
	movhpd xmm3, [esi + ebx*8]
	movapd xmm4, xmm3	     
	mulpd  xmm3, [esp + _iqO]
	mulpd  xmm4, [esp + _iqH]

	movapd  [esp + _qqO], xmm3
	movapd  [esp + _qqH], xmm4	

	mov esi, [ebp + _pos]       /* base of pos[] */

	lea   eax, [eax + eax*2]         /* replace jnr with j3 */
	lea   ebx, [ebx + ebx*2]	

