	subpd xmm4, xmm0
	subpd xmm5, xmm1
	subpd xmm6, xmm2

	/* store dr */
	movapd [esp + _dxH1], xmm4
	movapd [esp + _dyH1], xmm5
	movapd [esp + _dzH1], xmm6
	/* square it */
	mulpd xmm4,xmm4
	mulpd xmm5,xmm5
	mulpd xmm6,xmm6
	addpd xmm6, xmm5
	addpd xmm6, xmm4
