	addsd xmm1, [esp + _fiyH2]
	addsd xmm2, [esp + _fizH2]
	movlpd [esp + _fjxO], xmm3
	movlpd [esp + _fjyO], xmm4
	movlpd [esp + _fjzO], xmm5
	movlpd [esp + _fixH2], xmm0
	movlpd [esp + _fiyH2], xmm1
	movlpd [esp + _fizH2], xmm2

	/* H2-H1 interaction */
	movapd xmm0, [esp + _rinvH2H1]
	movapd xmm1, xmm0
	mulsd  xmm1, [esp + _rsqH2H1] /* xmm1=r */
	mulsd  xmm1, [esp + _tsc]
	cvttsd2si eax, xmm1	/* mm6 = lu idx */
	cvtsi2sd xmm6, eax
	subpd xmm1, xmm6	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulpd  xmm2, xmm2	/* xmm2=eps2 */
	
	shl eax, 2		/* idx *= 4 */
	mov  esi, [ebp + _VFtab]

	movupd xmm4, [esi + eax*8]	/* Y1 F1 */	
	xorpd xmm3, xmm3
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 */
	unpckhpd xmm5, xmm3	/* F1 */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */	
	xorpd xmm3, xmm3
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 */
	unpckhpd xmm7, xmm3	/* H1 */
	/* coulomb table ready, in xmm4-xmm7 */ 		
	mulsd  xmm6, xmm1	/* xmm6=Geps */
	mulsd  xmm7, xmm2	/* xmm7=Heps2 */
	addsd  xmm5, xmm6
	addsd  xmm5, xmm7	/* xmm5=Fp */	
	mulsd  xmm7, [esp + _two]	/* two*Heps2 */
	movapd xmm3, [esp + _qqHH]
	addsd  xmm7, xmm6
	addsd  xmm7, xmm5 /* xmm7=FF */
	mulsd  xmm5, xmm1 /* xmm5=eps*Fp */
	addsd  xmm5, xmm4 /* xmm5=VV */
	mulsd  xmm5, xmm3 /* vcoul=qq*VV */ 
	mulsd  xmm3, xmm7 /* fijC=FF*qq */
        /* at this point mm5 contains vcoul and xmm3 fijC */

        addsd  xmm5, [esp + _vctot]
        movlpd [esp + _vctot], xmm5
	xorpd  xmm1, xmm1
	mulsd  xmm3,  [esp + _tsc]
	mulsd  xmm3, xmm0
	subsd  xmm1, xmm3

	movapd xmm0, xmm1
	movapd xmm2, xmm1
	
	movapd xmm3, [esp + _fjxH1]
	movapd xmm4, [esp + _fjyH1]
	movapd xmm5, [esp + _fjzH1]
	mulsd xmm0, [esp + _dxH2H1]
	mulsd xmm1, [esp + _dyH2H1]
	mulsd xmm2, [esp + _dzH2H1]
	subsd xmm3, xmm0
	subsd xmm4, xmm1
	subsd xmm5, xmm2
	addsd xmm0, [esp + _fixH2]
	addsd xmm1, [esp + _fiyH2]
	addsd xmm2, [esp + _fizH2]
	movlpd [esp + _fjxH1], xmm3
	movlpd [esp + _fjyH1], xmm4
	movlpd [esp + _fjzH1], xmm5
	movlpd [esp + _fixH2], xmm0
	movlpd [esp + _fiyH2], xmm1
	movlpd [esp + _fizH2], xmm2

	/* H2-H2 interaction */
	movapd xmm0, [esp + _rinvH2H2]
	movapd xmm1, xmm0
	mulsd  xmm1, [esp + _rsqH2H2] /* xmm1=r */
	mulsd  xmm1, [esp + _tsc]	
	cvttsd2si eax, xmm1	/* mm6 = lu idx */
	cvtsi2sd xmm6, eax
	subsd xmm1, xmm6	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulsd  xmm2, xmm2	/* xmm2=eps2 */
	
	shl eax, 2		/* idx *= 4 */
	mov  esi, [ebp + _VFtab]

	movupd xmm4, [esi + eax*8]	/* Y1 F1 */	
	xorpd xmm3, xmm3
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 */
	unpckhpd xmm5, xmm3	/* F1 */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */	
	xorpd xmm3, xmm3
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 */
	unpckhpd xmm7, xmm3	/* H1 */
	/* coulomb table ready, in xmm4-xmm7 */ 		
	mulsd  xmm6, xmm1	/* xmm6=Geps */
	mulsd  xmm7, xmm2	/* xmm7=Heps2 */
	addsd  xmm5, xmm6
	addsd  xmm5, xmm7	/* xmm5=Fp */	
	mulsd  xmm7, [esp + _two]	/* two*Heps2 */
	movapd xmm3, [esp + _qqHH]
	addsd  xmm7, xmm6
	addsd  xmm7, xmm5 /* xmm7=FF */
	mulsd  xmm5, xmm1 /* xmm5=eps*Fp */
	addsd  xmm5, xmm4 /* xmm5=VV */
	mulsd  xmm5, xmm3 /* vcoul=qq*VV */ 
	mulsd  xmm3, xmm7 /* fijC=FF*qq */
        /* at this point mm5 contains vcoul and xmm3 fijC */

        addsd  xmm5, [esp + _vctot]
        movlpd [esp + _vctot], xmm5
	xorpd  xmm1, xmm1
	mulsd  xmm3,  [esp + _tsc]
	mulsd  xmm3, xmm0
	subsd  xmm1, xmm3

	movapd xmm0, xmm1
	movapd xmm2, xmm1
	
	movapd xmm3, [esp + _fjxH2]
	movapd xmm4, [esp + _fjyH2]
	movapd xmm5, [esp + _fjzH2]
	mulsd xmm0, [esp + _dxH2H2]
	mulsd xmm1, [esp + _dyH2H2]
	mulsd xmm2, [esp + _dzH2H2]
	subsd xmm3, xmm0
	subsd xmm4, xmm1
	subsd xmm5, xmm2
	addsd xmm0, [esp + _fixH2]
	addsd xmm1, [esp + _fiyH2]
	addsd xmm2, [esp + _fizH2]
	movlpd [esp + _fjxH2], xmm3
	movlpd [esp + _fjyH2], xmm4
	movlpd [esp + _fjzH2], xmm5
	movlpd [esp + _fixH2], xmm0
	movlpd [esp + _fiyH2], xmm1
	movlpd [esp + _fizH2], xmm2

	mov edi, [ebp + _faction]

	movd eax, mm0
	
	/* Did all interactions - now update j forces */
	movlpd xmm0, [edi + eax*8]
	movlpd xmm1, [edi + eax*8 + 8]
	movlpd xmm2, [edi + eax*8 + 16]
	movlpd xmm3, [edi + eax*8 + 24]
	movlpd xmm4, [edi + eax*8 + 32]
	movlpd xmm5, [edi + eax*8 + 40]
	movlpd xmm6, [edi + eax*8 + 48]
	movlpd xmm7, [edi + eax*8 + 56]
	addsd xmm0, [esp + _fjxO]
	addsd xmm1, [esp + _fjyO]
	addsd xmm2, [esp + _fjzO]
	addsd xmm3, [esp + _fjxH1]
	addsd xmm4, [esp + _fjyH1]
	addsd xmm5, [esp + _fjzH1]
	addsd xmm6, [esp + _fjxH2]
	addsd xmm7, [esp + _fjyH2]
	movlpd [edi + eax*8], xmm0
	movlpd [edi + eax*8 + 8], xmm1
	movlpd [edi + eax*8 + 16], xmm2
	movlpd [edi + eax*8 + 24], xmm3
	movlpd [edi + eax*8 + 32], xmm4
	movlpd [edi + eax*8 + 40], xmm5
	movlpd [edi + eax*8 + 48], xmm6
	movlpd [edi + eax*8 + 56], xmm7

	movlpd xmm0, [edi + eax*8 + 64]
	addsd xmm0, [esp + _fjzH2]
	movlpd [edi + eax*8 + 64], xmm0
	
.i3030_updateouterdata:
	mov   ecx, [esp + _ii3]
	mov   edi, [ebp + _faction]
	mov   esi, [ebp + _fshift]
	mov   edx, [esp + _is3]

	/* accumulate  Oi forces in xmm0, xmm1, xmm2 */
	movapd xmm0, [esp + _fixO]
	movapd xmm1, [esp + _fiyO]
	movapd xmm2, [esp + _fizO]

	movhlps xmm3, xmm0
	movhlps xmm4, xmm1
	movhlps xmm5, xmm2
	addsd  xmm0, xmm3
	addsd  xmm1, xmm4
	addsd  xmm2, xmm5 /* sum is in low xmm0-xmm2 */

	movapd xmm3, xmm0	
	movapd xmm4, xmm1	
	movapd xmm5, xmm2	

	/* increment i force */
	movsd  xmm3, [edi + ecx*8]
	movsd  xmm4, [edi + ecx*8 + 8]
	movsd  xmm5, [edi + ecx*8 + 16]
	addsd  xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm5, xmm2
	movsd  [edi + ecx*8],     xmm3
	movsd  [edi + ecx*8 + 8], xmm4
	movsd  [edi + ecx*8 + 16], xmm5

	/* accumulate force in xmm6/xmm7 for fshift */
	movapd xmm6, xmm0
	movsd xmm7, xmm2
	unpcklpd xmm6, xmm1

	/* accumulate H1i forces in xmm0, xmm1, xmm2 */
	movapd xmm0, [esp + _fixH1]
	movapd xmm1, [esp + _fiyH1]
	movapd xmm2, [esp + _fizH1]

	movhlps xmm3, xmm0
	movhlps xmm4, xmm1
	movhlps xmm5, xmm2
	addsd  xmm0, xmm3
	addsd  xmm1, xmm4
	addsd  xmm2, xmm5 /* sum is in low xmm0-xmm2 */

	/* increment i force */
	movsd  xmm3, [edi + ecx*8 + 24]
	movsd  xmm4, [edi + ecx*8 + 32]
	movsd  xmm5, [edi + ecx*8 + 40]
	addsd  xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm5, xmm2
	movsd  [edi + ecx*8 + 24], xmm3
	movsd  [edi + ecx*8 + 32], xmm4
	movsd  [edi + ecx*8 + 40], xmm5

	/* accumulate force in xmm6/xmm7 for fshift */
	addsd xmm7, xmm2
	unpcklpd xmm0, xmm1
	addpd xmm6, xmm0

	/* accumulate H2i forces in xmm0, xmm1, xmm2 */
	movapd xmm0, [esp + _fixH2]
	movapd xmm1, [esp + _fiyH2]
	movapd xmm2, [esp + _fizH2]

	movhlps xmm3, xmm0
	movhlps xmm4, xmm1
	movhlps xmm5, xmm2
	addsd  xmm0, xmm3
	addsd  xmm1, xmm4
	addsd  xmm2, xmm5 /* sum is in low xmm0-xmm2 */

	movapd xmm3, xmm0	
	movapd xmm4, xmm1	
	movapd xmm5, xmm2	

	/* increment i force */
	movsd  xmm3, [edi + ecx*8 + 48]
	movsd  xmm4, [edi + ecx*8 + 56]
	movsd  xmm5, [edi + ecx*8 + 64]
	addsd  xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm5, xmm2
	movsd  [edi + ecx*8 + 48], xmm3
	movsd  [edi + ecx*8 + 56], xmm4
	movsd  [edi + ecx*8 + 64], xmm5

	/* accumulate force in xmm6/xmm7 for fshift */
	addsd xmm7, xmm2
	unpcklpd xmm0, xmm1
	addpd xmm6, xmm0

	/* increment fshift force */
	movupd xmm3, [esi + edx*8]
	movsd  xmm4, [esi + edx*8 + 16]
	addpd  xmm3, xmm6
	addsd  xmm4, xmm7
	movupd [esi + edx*8],    xmm3
	movsd  [esi + edx*8 + 16], xmm4

	mov   edx, [ebp + _gid]  
	mov   edx, [edx]
	add   [ebp + _gid],  4	

	/* accumulate total potential energy and update it */
	movapd xmm7, [esp + _vctot]
	/* accumulate */
	movhlps xmm6, xmm7
	addsd  xmm7, xmm6	/* low xmm7 has the sum now */
        
	/* add earlier value from mem */
	mov   eax, [ebp + _Vc]
	addsd xmm7, [eax + edx*8] 
	/* move back to mem */
	movsd [eax + edx*8], xmm7 
	
	/* finish if last */
	mov   ecx, [ebp + _nri]
	dec ecx
	jecxz .i3030_end
	/* not last, iterate once more! */ 
	mov [ebp + _nri], ecx
	jmp .i3030_outer
.i3030_end:
	emms
	mov eax, [esp + _salign]
	add esp, eax
	add esp, 1444
	pop edi
	pop esi
        pop edx
        pop ecx
        pop ebx
        pop eax
	leave
	ret
	



.globl inl3100_sse2
	.type inl3100_sse2,@function
inl3100_sse2:	
.equ		_nri,		8
.equ		_iinr,		12
.equ		_jindex,	16
.equ		_jjnr,		20
.equ		_shift,		24
.equ		_shiftvec,	28
.equ		_fshift,	32
.equ		_gid,		36
.equ		_pos,		40
.equ		_faction,	44
.equ		_charge,	48
.equ		_facel,		52 /* double */ 
.equ		_Vc,		60	
.equ		_type,		64
.equ		_ntype,		68
.equ		_nbfp,		72	
.equ		_Vnb,		76
.equ		_tabscale,	80 /* double */ 
.equ		_VFtab,		88
	/* stack offsets for local variables */ 
	/* bottom of stack is cache-aligned for sse2 use */
.equ		_ix,		0
.equ		_iy,		16
.equ		_iz,		32
.equ		_iq,		48
.equ		_dx,		64
.equ		_dy,		80
.equ		_dz,		96
.equ		_two,		112
.equ		_six,		128
.equ		_twelve,	144
.equ		_tsc,		160
.equ		_qq,		176	
.equ		_c6,		192
.equ		_c12,		208
.equ		_fscal,		224
.equ		_vctot,		240
.equ		_vnbtot,	256
.equ		_fix,		272
.equ		_fiy,		288
.equ		_fiz,		304
.equ		_half,		320
.equ		_three,		336
.equ		_is3,		352
.equ		_ii3,		356
.equ		_ntia,		360	
.equ		_innerjjnr,	364
.equ		_innerk,	368
.equ		_salign,	372
	push ebp
	mov ebp,esp	
        push eax
        push ebx
        push ecx
        push edx
	push esi
	push edi
	sub esp, 376		/* local stack space */
	mov  eax, esp
	and  eax, 0xf
	sub esp, eax
	mov [esp + _salign], eax

	emms

	movupd xmm0, [sse2_half]
	movupd xmm1, [sse2_two]
	movupd xmm2, [sse2_three]
	movupd xmm3, [sse2_six]
	movupd xmm4, [sse2_twelve]
	movsd xmm5, [ebp + _tabscale]
	movapd [esp + _half],  xmm0
	movapd [esp + _two], xmm1
	movapd [esp + _three],  xmm2
	movapd [esp + _six],  xmm3
	movapd [esp + _twelve],  xmm4
	shufpd xmm5, xmm5, 0
	movapd [esp + _tsc], xmm5

	/* assume we have at least one i particle - start directly */	
.i3100_outer:
	mov   eax, [ebp + _shift]      /* eax = pointer into shift[] */
	mov   ebx, [eax]		/* ebx=shift[n] */
	add   [ebp + _shift],  4  /* advance pointer one step */
	
	lea   ebx, [ebx + ebx*2]        /* ebx=3*is */
	mov   [esp + _is3],ebx    	/* store is3 */

	mov   eax, [ebp + _shiftvec]   /* eax = base of shiftvec[] */

	movsd xmm0, [eax + ebx*8]
	movsd xmm1, [eax + ebx*8 + 8]
	movsd xmm2, [eax + ebx*8 + 16] 

	mov   ecx, [ebp + _iinr]       /* ecx = pointer into iinr[] */	
	add   [ebp + _iinr],  4   /* advance pointer */
	mov   ebx, [ecx]	        /* ebx =ii */

	mov   edx, [ebp + _charge]
	movsd xmm3, [edx + ebx*8]	
	mulsd xmm3, [ebp + _facel]
	shufpd xmm3, xmm3, 0

        mov   edx, [ebp + _type] 
        mov   edx, [edx + ebx*4]
        imul  edx, [ebp + _ntype]
        shl   edx, 1
        mov   [esp + _ntia], edx
		
	lea   ebx, [ebx + ebx*2]	/* ebx = 3*ii=ii3 */
	mov   eax, [ebp + _pos]        /* eax = base of pos[] */ 

	addsd xmm0, [eax + ebx*8]
	addsd xmm1, [eax + ebx*8 + 8]
	addsd xmm2, [eax + ebx*8 + 16]

	movapd [esp + _iq], xmm3
	
	shufpd xmm0, xmm0, 0
	shufpd xmm1, xmm1, 0
	shufpd xmm2, xmm2, 0

	movapd [esp + _ix], xmm0
	movapd [esp + _iy], xmm1
	movapd [esp + _iz], xmm2

	mov   [esp + _ii3], ebx
	
	/* clear vctot and i forces */
	xorpd xmm4, xmm4
	movapd [esp + _vctot], xmm4
	movapd [esp + _vnbtot], xmm4
	movapd [esp + _fix], xmm4
	movapd [esp + _fiy], xmm4
	movapd [esp + _fiz], xmm4
	
	mov   eax, [ebp + _jindex]
	mov   ecx, [eax]	         /* jindex[n] */
	mov   edx, [eax + 4]	         /* jindex[n+1] */
	add   [ebp + _jindex],  4
	sub   edx, ecx                   /* number of innerloop atoms */

	mov   esi, [ebp + _pos]
	mov   edi, [ebp + _faction]	
	mov   eax, [ebp + _jjnr]
	shl   ecx, 2
	add   eax, ecx
	mov   [esp + _innerjjnr], eax     /* pointer to jjnr[nj0] */
	sub   edx,  2
	mov   [esp + _innerk], edx        /* number of innerloop atoms */
	jge   .i3100_unroll_loop
	jmp   .i3100_checksingle
.i3100_unroll_loop:	
	/* twice unrolled innerloop here */
	mov   edx, [esp + _innerjjnr]     /* pointer to jjnr[k] */
	mov   eax, [edx]	
	mov   ebx, [edx + 4]              
	add   [esp + _innerjjnr],  8 /* advance pointer (unrolled 2) */

	mov esi, [ebp + _charge]        /* base of charge[] */
	movlpd xmm3, [esi + eax*8]
	movhpd xmm3, [esi + ebx*8]

	movapd xmm2, [esp + _iq]
	mulpd  xmm3, xmm2
	movapd [esp + _qq], xmm3	
	
	movd  mm0, eax		/* use mmx registers as temp storage */
	movd  mm1, ebx
	
	mov esi, [ebp + _type]
	mov eax, [esi + eax*4]
	mov ebx, [esi + ebx*4]
	mov esi, [ebp + _nbfp]
	shl eax, 1
	shl ebx, 1
	mov edi, [esp + _ntia]
	add eax, edi
	add ebx, edi

	movupd xmm6, [esi + eax*8]	/* c6a c12a */
	movupd xmm7, [esi + ebx*8]	/* c6b c12b */
	movapd xmm4, xmm6
	unpcklpd xmm4, xmm7
	unpckhpd xmm6, xmm7
	
	movd  eax, mm0
	movd  ebx, mm1
	movapd [esp + _c6], xmm4
	movapd [esp + _c12], xmm6
	
	mov esi, [ebp + _pos]       /* base of pos[] */

	lea   eax, [eax + eax*2]         /* replace jnr with j3 */
	lea   ebx, [ebx + ebx*2]	

	/* move two coordinates to xmm0-xmm2 */	
	movlpd xmm0, [esi + eax*8]
	movlpd xmm1, [esi + eax*8 + 8]
	movlpd xmm2, [esi + eax*8 + 16]
	movhpd xmm0, [esi + ebx*8]
	movhpd xmm1, [esi + ebx*8 + 8]
	movhpd xmm2, [esi + ebx*8 + 16]		
	
	/* move ix-iz to xmm4-xmm6 */
	movapd xmm4, [esp + _ix]
	movapd xmm5, [esp + _iy]
	movapd xmm6, [esp + _iz]

	/* calc dr */
	subpd xmm4, xmm0
	subpd xmm5, xmm1
	subpd xmm6, xmm2

	/* store dr */
	movapd [esp + _dx], xmm4
	movapd [esp + _dy], xmm5
	movapd [esp + _dz], xmm6
	/* square it */
	mulpd xmm4,xmm4
	mulpd xmm5,xmm5
	mulpd xmm6,xmm6
	addpd xmm4, xmm5
	addpd xmm4, xmm6
	/* rsq in xmm4 */

	cvtpd2ps xmm5, xmm4	
	rsqrtps xmm5, xmm5
	cvtps2pd xmm2, xmm5	/* lu in low xmm2 */

	/* lookup seed in xmm2 */
	movapd xmm5, xmm2	/* copy of lu */
	mulpd xmm2, xmm2	/* lu*lu */
	movapd xmm1, [esp + _three]
	mulpd xmm2, xmm4	/* rsq*lu*lu */			
	movapd xmm0, [esp + _half]
	subpd xmm1, xmm2	/* 30-rsq*lu*lu */
	mulpd xmm1, xmm5	
	mulpd xmm1, xmm0	/* xmm0=iter1 of rinv (new lu) */

	movapd xmm5, xmm1	/* copy of lu */
	mulpd xmm1, xmm1	/* lu*lu */
	movapd xmm2, [esp + _three]
	mulpd xmm1, xmm4	/* rsq*lu*lu */			
	movapd xmm0, [esp + _half]
	subpd xmm2, xmm1	/* 30-rsq*lu*lu */
	mulpd xmm2, xmm5	
	mulpd xmm0, xmm2	/* xmm0=rinv */
	
	mulpd xmm4, xmm0	/* xmm4=r */
	mulpd xmm4, [esp + _tsc]

	cvttpd2pi mm6, xmm4	/* mm6 = lu idx */
	cvtpi2pd xmm5, mm6
	subpd xmm4, xmm5
	movapd xmm1, xmm4	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulpd  xmm2, xmm2	/* xmm2=eps2 */
	
	pslld mm6, 2		/* idx *= 4 */
	
	movd mm0, eax	
	movd mm1, ebx

	mov  esi, [ebp + _VFtab]
	movd eax, mm6
	psrlq mm6, 32
	movd ebx, mm6		/* indices in eax/ebx */

	movupd xmm4, [esi + eax*8]	/* Y1 F1 */	
	movupd xmm3, [esi + ebx*8]	/* Y2 F2 */
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 Y2 */
	unpckhpd xmm5, xmm3	/* F1 F2 */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */	
	movupd xmm3, [esi + ebx*8 + 16]	/* G2 H2 */
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 G2 */
	unpckhpd xmm7, xmm3	/* H1 H2 */
	/* coulomb table ready, in xmm4-xmm7 */ 		
	mulpd  xmm6, xmm1	/* xmm6=Geps */
	mulpd  xmm7, xmm2	/* xmm7=Heps2 */
	addpd  xmm5, xmm6
	addpd  xmm5, xmm7	/* xmm5=Fp */	
	mulpd  xmm7, [esp + _two]	/* two*Heps2 */
	movapd xmm3, [esp + _qq]
	addpd  xmm7, xmm6
	addpd  xmm7, xmm5 /* xmm7=FF */
	mulpd  xmm5, xmm1 /* xmm5=eps*Fp */
	addpd  xmm5, xmm4 /* xmm5=VV */
	mulpd  xmm5, xmm3 /* vcoul=qq*VV */ 
	mulpd  xmm3, xmm7 /* fijC=FF*qq */
	/* at this point mm5 contains vcoul and mm3 fijC */
	
	/* L-J */
	movapd xmm4, xmm0
	mulpd  xmm4, xmm0	/* xmm4=rinvsq */

	/* increment vcoul - then we can get rid of mm5 */
	/* update vctot */
	addpd  xmm5, [esp + _vctot]

	movapd xmm6, xmm4
	mulpd  xmm6, xmm4

	movapd [esp + _vctot], xmm5 

	mulpd  xmm6, xmm4	/* xmm6=rinvsix */
	movapd xmm4, xmm6
	mulpd  xmm4, xmm4	/* xmm4=rinvtwelve */
	mulpd  xmm6, [esp + _c6]
	mulpd  xmm4, [esp + _c12]
	movapd xmm7, [esp + _vnbtot]
	addpd  xmm7, xmm4
	mulpd  xmm4, [esp + _twelve]
	subpd  xmm7, xmm6
	mulpd  xmm3, [esp + _tsc]
	mulpd  xmm6, [esp + _six]
	movapd [esp + _vnbtot], xmm7
	subpd  xmm4, xmm6
	mulpd  xmm4, xmm0
	subpd  xmm4, xmm3
	mulpd  xmm4, xmm0

	movapd xmm0, [esp + _dx]
	movapd xmm1, [esp + _dy]
	movapd xmm2, [esp + _dz]

	movd eax, mm0	
	movd ebx, mm1

	mov    edi, [ebp + _faction]
	mulpd  xmm0, xmm4
	mulpd  xmm1, xmm4
	mulpd  xmm2, xmm4
	/* xmm0-xmm2 contains tx-tz (partial force) */
	/* now update f_i */
	movapd xmm3, [esp + _fix]
	movapd xmm4, [esp + _fiy]
	movapd xmm5, [esp + _fiz]
	addpd  xmm3, xmm0
	addpd  xmm4, xmm1
	addpd  xmm5, xmm2
	movapd [esp + _fix], xmm3
	movapd [esp + _fiy], xmm4
	movapd [esp + _fiz], xmm5
	/* the fj's - start by accumulating forces from memory */
	movlpd xmm3, [edi + eax*8]
	movlpd xmm4, [edi + eax*8 + 8]
	movlpd xmm5, [edi + eax*8 + 16]
	movhpd xmm3, [edi + ebx*8]
	movhpd xmm4, [edi + ebx*8 + 8]
	movhpd xmm5, [edi + ebx*8 + 16]
	subpd xmm3, xmm0
	subpd xmm4, xmm1
	subpd xmm5, xmm2
	movlpd [edi + eax*8], xmm3
	movlpd [edi + eax*8 + 8], xmm4
	movlpd [edi + eax*8 + 16], xmm5
	movhpd [edi + ebx*8], xmm3
	movhpd [edi + ebx*8 + 8], xmm4
	movhpd [edi + ebx*8 + 16], xmm5
		
	/* should we do one more iteration? */
	sub   [esp + _innerk],  2
	jl    .i3100_checksingle
	jmp   .i3100_unroll_loop
.i3100_checksingle:
	mov   edx, [esp + _innerk]
	and   edx, 1
	jnz    .i3100_dosingle
	jmp    .i3100_updateouterdata
.i3100_dosingle:
	mov esi, [ebp + _charge]
	mov edi, [ebp + _pos]
	mov   ecx, [esp + _innerjjnr]
	mov   eax, [ecx]
	
	xorpd  xmm3, xmm3
	movlpd xmm3, [esi + eax*8]
	movapd xmm2, [esp + _iq]
	mulpd  xmm3, xmm2
	movapd [esp + _qq], xmm3	
	
	movd  mm0, eax		/* use mmx registers as temp storage */
	mov esi, [ebp + _type]
	mov eax, [esi + eax*4]
	mov esi, [ebp + _nbfp]
	shl eax, 1
	mov edi, [esp + _ntia]
	add eax, edi

	movupd xmm6, [esi + eax*8]	/* c6a c12a */
	xorpd xmm7, xmm7
	movapd xmm4, xmm6
	unpcklpd xmm4, xmm7
	unpckhpd xmm6, xmm7
	
	movd  eax, mm0
	movapd [esp + _c6], xmm4
	movapd [esp + _c12], xmm6
	
	mov esi, [ebp + _pos]       /* base of pos[] */

	lea   eax, [eax + eax*2]         /* replace jnr with j3 */

	/* move coordinates to xmm0-xmm2 */	
	movlpd xmm0, [esi + eax*8]
	movlpd xmm1, [esi + eax*8 + 8]
	movlpd xmm2, [esi + eax*8 + 16]
	
	/* move ix-iz to xmm4-xmm6 */
	movapd xmm4, [esp + _ix]
	movapd xmm5, [esp + _iy]
	movapd xmm6, [esp + _iz]

	/* calc dr */
	subsd xmm4, xmm0
	subsd xmm5, xmm1
	subsd xmm6, xmm2

	/* store dr */
	movapd [esp + _dx], xmm4
	movapd [esp + _dy], xmm5
	movapd [esp + _dz], xmm6
	/* square it */
	mulsd xmm4,xmm4
	mulsd xmm5,xmm5
	mulsd xmm6,xmm6
	addsd xmm4, xmm5
	addsd xmm4, xmm6
	/* rsq in xmm4 */

	cvtsd2ss xmm5, xmm4	
	rsqrtss xmm5, xmm5
	cvtss2sd xmm2, xmm5	/* lu in low xmm2 */

	/* lookup seed in xmm2 */
	movapd xmm5, xmm2	/* copy of lu */
	mulsd xmm2, xmm2	/* lu*lu */
	movapd xmm1, [esp + _three]
	mulsd xmm2, xmm4	/* rsq*lu*lu */			
	movapd xmm0, [esp + _half]
	subsd xmm1, xmm2	/* 30-rsq*lu*lu */
	mulsd xmm1, xmm5	
	mulsd xmm1, xmm0	/* xmm0=iter1 of rinv (new lu) */

	movapd xmm5, xmm1	/* copy of lu */
	mulsd xmm1, xmm1	/* lu*lu */
	movapd xmm2, [esp + _three]
	mulsd xmm1, xmm4	/* rsq*lu*lu */			
	movapd xmm0, [esp + _half]
	subsd xmm2, xmm1	/* 30-rsq*lu*lu */
	mulsd xmm2, xmm5	
	mulsd xmm0, xmm2	/* xmm0=rinv */
	
	mulsd xmm4, xmm0	/* xmm4=r */
	mulsd xmm4, [esp + _tsc]

	movd mm0, eax	
	cvttsd2si eax, xmm4	/* mm6 = lu idx */
	cvtsi2sd xmm5, eax
	subsd xmm4, xmm5
	movapd xmm1, xmm4	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulsd  xmm2, xmm2	/* xmm2=eps2 */
	
	shl eax, 2		/* idx *= 4 */
	
	mov  esi, [ebp + _VFtab]

	movupd xmm4, [esi + eax*8]	/* Y1 F1 */	
	xorpd xmm3, xmm3
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 */
	unpckhpd xmm5, xmm3	/* F1 */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */	
	xorpd xmm3, xmm3
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 */
	unpckhpd xmm7, xmm3	/* H1 */
	/* coulomb table ready, in xmm4-xmm7 */ 		
	mulsd  xmm6, xmm1	/* xmm6=Geps */
	mulsd  xmm7, xmm2	/* xmm7=Heps2 */
	addsd  xmm5, xmm6
	addsd  xmm5, xmm7	/* xmm5=Fp */	
	mulsd  xmm7, [esp + _two]	/* two*Heps2 */
	movapd xmm3, [esp + _qq]
	addsd  xmm7, xmm6
	addsd  xmm7, xmm5 /* xmm7=FF */
	mulsd  xmm5, xmm1 /* xmm5=eps*Fp */
	addsd  xmm5, xmm4 /* xmm5=VV */
	mulsd  xmm5, xmm3 /* vcoul=qq*VV */ 
	mulsd  xmm3, xmm7 /* fijC=FF*qq */
	/* at this point mm5 contains vcoul and mm3 fijC */
	
	/* L-J */
	movapd xmm4, xmm0
	mulsd  xmm4, xmm0	/* xmm4=rinvsq */

	/* increment vcoul - then we can get rid of mm5 */
	/* update vctot */
	addsd  xmm5, [esp + _vctot]

	movapd xmm6, xmm4
	mulsd  xmm6, xmm4

	movlpd [esp + _vctot], xmm5 

	mulsd  xmm6, xmm4	/* xmm6=rinvsix */
	movapd xmm4, xmm6
	mulsd  xmm4, xmm4	/* xmm4=rinvtwelve */
	mulsd  xmm6, [esp + _c6]
	mulsd  xmm4, [esp + _c12]
	movapd xmm7, [esp + _vnbtot]
	addsd  xmm7, xmm4
	mulsd  xmm4, [esp + _twelve]
	subsd  xmm7, xmm6
	mulsd  xmm3, [esp + _tsc]
	mulsd  xmm6, [esp + _six]
	movlpd [esp + _vnbtot], xmm7
	subsd  xmm4, xmm6
	mulsd  xmm4, xmm0
	subsd  xmm4, xmm3
	mulsd  xmm4, xmm0

	movapd xmm0, [esp + _dx]
	movapd xmm1, [esp + _dy]
	movapd xmm2, [esp + _dz]

	movd eax, mm0	

	mov    edi, [ebp + _faction]
	mulsd  xmm0, xmm4
	mulsd  xmm1, xmm4
	mulsd  xmm2, xmm4
	/* xmm0-xmm2 contains tx-tz (partial force) */
	/* now update f_i */
	movapd xmm3, [esp + _fix]
	movapd xmm4, [esp + _fiy]
	movapd xmm5, [esp + _fiz]
	addsd  xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm5, xmm2
	movlpd [esp + _fix], xmm3
	movlpd [esp + _fiy], xmm4
	movlpd [esp + _fiz], xmm5
	/* the fj's - start by accumulating forces from memory */
	movlpd xmm3, [edi + eax*8]
	movlpd xmm4, [edi + eax*8 + 8]
	movlpd xmm5, [edi + eax*8 + 16]
	subsd xmm3, xmm0
	subsd xmm4, xmm1
	subsd xmm5, xmm2
	movlpd [edi + eax*8], xmm3
	movlpd [edi + eax*8 + 8], xmm4
	movlpd [edi + eax*8 + 16], xmm5
		
.i3100_updateouterdata:
	mov   ecx, [esp + _ii3]
	mov   edi, [ebp + _faction]
	mov   esi, [ebp + _fshift]
	mov   edx, [esp + _is3]

	/* accumulate i forces in xmm0, xmm1, xmm2 */
	movapd xmm0, [esp + _fix]
	movapd xmm1, [esp + _fiy]
	movapd xmm2, [esp + _fiz]

	movhlps xmm3, xmm0
	movhlps xmm4, xmm1
	movhlps xmm5, xmm2
	addsd  xmm0, xmm3
	addsd  xmm1, xmm4
	addsd  xmm2, xmm5 /* sum is in low xmm0-xmm2 */

	/* increment i force */
	movsd  xmm3, [edi + ecx*8]
	movsd  xmm4, [edi + ecx*8 + 8]
	movsd  xmm5, [edi + ecx*8 + 16]
	addsd  xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm5, xmm2
	movsd  [edi + ecx*8],     xmm3
	movsd  [edi + ecx*8 + 8], xmm4
	movsd  [edi + ecx*8 + 16], xmm5

	/* increment fshift force */ 
	movsd  xmm3, [esi + edx*8]
	movsd  xmm4, [esi + edx*8 + 8]
	movsd  xmm5, [esi + edx*8 + 16]
	addsd  xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm5, xmm2
	movsd  [esi + edx*8],     xmm3
	movsd  [esi + edx*8 + 8], xmm4
	movsd  [esi + edx*8 + 16], xmm5

	/* get group index for i particle */
	mov   edx, [ebp + _gid]      /* get group index for this i particle */
	mov   edx, [edx]
	add   [ebp + _gid],  4  /* advance pointer */

	/* accumulate total potential energy and update it */
	movapd xmm7, [esp + _vctot]
	/* accumulate */
	movhlps xmm6, xmm7
	addsd  xmm7, xmm6	/* low xmm7 has the sum now */

	/* add earlier value from mem */
	mov   eax, [ebp + _Vc]
	addsd xmm7, [eax + edx*8] 
	/* move back to mem */
	movsd [eax + edx*8], xmm7 
	
	/* accumulate total lj energy and update it */
	movapd xmm7, [esp + _vnbtot]
	/* accumulate */
	movhlps xmm6, xmm7
	addsd  xmm7, xmm6	/* low xmm7 has the sum now */
	
	/* add earlier value from mem */
	mov   eax, [ebp + _Vnb]
	addsd xmm7, [eax + edx*8] 
	/* move back to mem */
	movsd [eax + edx*8], xmm7 
	
	/* finish if last */
	mov   ecx, [ebp + _nri]
	dec ecx
	jecxz .i3100_end
	/* not last, iterate once more! */ 
	mov [ebp + _nri], ecx
	jmp .i3100_outer
.i3100_end:
	emms
	mov eax, [esp + _salign]
	add esp, eax
	add esp, 376
	pop edi
	pop esi
        pop edx
        pop ecx
        pop ebx
        pop eax
	leave
	ret




