	mov   edx, [eax + 4]	         /* jindex[n+1] */
	add   [ebp + _jindex],  4
	sub   edx, ecx                   /* number of innerloop atoms */

	mov   esi, [ebp + _pos]
	mov   edi, [ebp + _faction]	
	mov   eax, [ebp + _jjnr]
	shl   ecx, 2
	add   eax, ecx
	mov   [esp + _innerjjnr], eax     /* pointer to jjnr[nj0] */
	sub   edx,  2
	mov   [esp + _innerk], edx        /* number of innerloop atoms */
	jge   .i3120_unroll_loop
	jmp   .i3120_checksingle
.i3120_unroll_loop:
	/* twice unrolled innerloop here */
	mov   edx, [esp + _innerjjnr]     /* pointer to jjnr[k] */
	mov   eax, [edx]	
	mov   ebx, [edx + 4]

	add   [esp + _innerjjnr],  8 /* advance pointer (unrolled 2) */

	mov esi, [ebp + _charge]        /* base of charge[] */
	
	movlpd xmm3, [esi + eax*8]
	movhpd xmm3, [esi + ebx*8]
	movapd xmm4, xmm3	     
	mulpd  xmm3, [esp + _iqO]
	mulpd  xmm4, [esp + _iqH]
	movapd  [esp + _qqO], xmm3
	movapd  [esp + _qqH], xmm4	

	movd  mm0, eax		/* use mmx registers as temp storage */
	movd  mm1, ebx
	mov esi, [ebp + _type]
	mov eax, [esi + eax*4]
	mov ebx, [esi + ebx*4]
	mov esi, [ebp + _nbfp]
	shl eax, 1	
	shl ebx, 1	
	mov edi, [esp + _ntia]
	add eax, edi
	add ebx, edi

	movupd xmm6, [esi + eax*8]	/* c6a c12a */
	movupd xmm7, [esi + ebx*8]	/* c6b c12b */
	movapd xmm4, xmm6
	unpcklpd xmm4, xmm7
	unpckhpd xmm6, xmm7
	
	movd  eax, mm0
	movd  ebx, mm1
	movapd [esp + _c6], xmm4
	movapd [esp + _c12], xmm6

	mov esi, [ebp + _pos]       /* base of pos[] */

	lea   eax, [eax + eax*2]         /* replace jnr with j3 */
	lea   ebx, [ebx + ebx*2]	

	/* move two coordinates to xmm0-xmm2 */	
	movlpd xmm0, [esi + eax*8]
	movlpd xmm1, [esi + eax*8 + 8]
	movlpd xmm2, [esi + eax*8 + 16]
	movhpd xmm0, [esi + ebx*8]
	movhpd xmm1, [esi + ebx*8 + 8]
	movhpd xmm2, [esi + ebx*8 + 16]		

	/* move ixO-izO to xmm4-xmm6 */
	movapd xmm4, [esp + _ixO]
	movapd xmm5, [esp + _iyO]
	movapd xmm6, [esp + _izO]

	/* calc dr */
	subpd xmm4, xmm0
	subpd xmm5, xmm1
	subpd xmm6, xmm2

	/* store dr */
	movapd [esp + _dxO], xmm4
	movapd [esp + _dyO], xmm5
	movapd [esp + _dzO], xmm6
	/* square it */
	mulpd xmm4,xmm4
	mulpd xmm5,xmm5
	mulpd xmm6,xmm6
	addpd xmm4, xmm5
	addpd xmm4, xmm6
	movapd xmm7, xmm4
	/* rsqO in xmm7 */

	/* move ixH1-izH1 to xmm4-xmm6 */
	movapd xmm4, [esp + _ixH1]
	movapd xmm5, [esp + _iyH1]
	movapd xmm6, [esp + _izH1]

	/* calc dr */
	subpd xmm4, xmm0
	subpd xmm5, xmm1
	subpd xmm6, xmm2

	/* store dr */
	movapd [esp + _dxH1], xmm4
	movapd [esp + _dyH1], xmm5
	movapd [esp + _dzH1], xmm6
	/* square it */
	mulpd xmm4,xmm4
	mulpd xmm5,xmm5
	mulpd xmm6,xmm6
	addpd xmm6, xmm5
	addpd xmm6, xmm4
	/* rsqH1 in xmm6 */

	/* move ixH2-izH2 to xmm3-xmm5 */ 
	movapd xmm3, [esp + _ixH2]
	movapd xmm4, [esp + _iyH2]
	movapd xmm5, [esp + _izH2]

	/* calc dr */
	subpd xmm3, xmm0
	subpd xmm4, xmm1
	subpd xmm5, xmm2

	/* store dr */
	movapd [esp + _dxH2], xmm3
	movapd [esp + _dyH2], xmm4
	movapd [esp + _dzH2], xmm5
	/* square it */
	mulpd xmm3,xmm3
	mulpd xmm4,xmm4
	mulpd xmm5,xmm5
	addpd xmm5, xmm4
	addpd xmm5, xmm3
	/* rsqH2 in xmm5, rsqH1 in xmm6, rsqO in xmm7 */

	/* start with rsqO - put seed in xmm2 */
	cvtpd2ps xmm2, xmm7	
	rsqrtps xmm2, xmm2
	cvtps2pd xmm2, xmm2

	movapd  xmm3, xmm2
	mulpd   xmm2, xmm2
	movapd  xmm4, [esp + _three]
	mulpd   xmm2, xmm7	/* rsq*lu*lu */
	subpd   xmm4, xmm2	/* 30-rsq*lu*lu */
	mulpd   xmm4, xmm3	/* lu*(3-rsq*lu*lu) */
	mulpd   xmm4, [esp + _half] /* iter1 ( new lu) */

	movapd xmm2, xmm7
	movapd xmm3, xmm4
	mulpd xmm4, xmm4	/* lu*lu */
	mulpd xmm2, xmm4	/* rsq*lu*lu */
	movapd xmm4, [esp + _three]
	subpd xmm4, xmm2	/* 3-rsq*lu*lu */
	mulpd xmm4, xmm3	/* lu*(	3-rsq*lu*lu) */
	mulpd xmm4, [esp + _half] /* rinv */
	movapd  [esp + _rinvO], xmm4	/* rinvO in xmm4 */
	mulpd   xmm7, xmm4
	movapd  [esp + _rO], xmm7	/* r in xmm7 */
	
	/* rsqH1 - seed in xmm2 */
	cvtpd2ps xmm2, xmm6	
	rsqrtps xmm2, xmm2
	cvtps2pd xmm2, xmm2

	movapd  xmm3, xmm2
	mulpd   xmm2, xmm2
	movapd  xmm4, [esp + _three]
	mulpd   xmm2, xmm6	/* rsq*lu*lu */
	subpd   xmm4, xmm2	/* 30-rsq*lu*lu */
	mulpd   xmm4, xmm3	/* lu*(3-rsq*lu*lu) */
	mulpd   xmm4, [esp + _half] /* iter1 ( new lu) */

	movapd xmm2, xmm6
	movapd xmm3, xmm4
	mulpd xmm4, xmm4	/* lu*lu */
	mulpd xmm2, xmm4	/* rsq*lu*lu */
	movapd xmm4, [esp + _three]
	subpd xmm4, xmm2	/* 3-rsq*lu*lu */
	mulpd xmm4, xmm3	/* lu*(	3-rsq*lu*lu) */
	mulpd xmm4, [esp + _half] /* rinv */
	movapd [esp + _rinvH1], xmm4	/* rinvH1 */
	mulpd  xmm6, xmm4
	movapd [esp + _rH1], xmm6	/* rH1 */
	
	/* rsqH2 - seed in xmm2 */
	cvtpd2ps xmm2, xmm5	
	rsqrtps xmm2, xmm2
	cvtps2pd xmm2, xmm2

	movapd  xmm3, xmm2
	mulpd   xmm2, xmm2
	movapd  xmm4, [esp + _three]
	mulpd   xmm2, xmm5	/* rsq*lu*lu */
	subpd   xmm4, xmm2	/* 30-rsq*lu*lu */
	mulpd   xmm4, xmm3	/* lu*(3-rsq*lu*lu) */
	mulpd   xmm4, [esp + _half] /* iter1 ( new lu) */

	movapd xmm2, xmm5
	movapd xmm3, xmm4
	mulpd xmm4, xmm4	/* lu*lu */
	mulpd xmm2, xmm4	/* rsq*lu*lu */
	movapd xmm4, [esp + _three]
	subpd xmm4, xmm2	/* 3-rsq*lu*lu */
	mulpd xmm4, xmm3	/* lu*(	3-rsq*lu*lu) */
	mulpd xmm4, [esp + _half] /* rinv */
	movapd [esp + _rinvH2], xmm4 /* rinv */
	mulpd xmm5, xmm4
	movapd [esp + _rH2], xmm5 /* r */

	/* do O interactions */
	/* rO is still in xmm7 */
	movapd xmm0, [esp + _rinvO]
	mulpd   xmm7, [esp + _tsc]
	cvttpd2pi mm6, xmm7	/* mm6 = lu idx */
	cvtpi2pd xmm6, mm6
	subpd xmm7, xmm6
	movapd xmm1, xmm7	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulpd  xmm2, xmm2	/* xmm2=eps2 */
	
	pslld mm6, 2		/* idx *= 4 */
	movd mm0, eax	
	movd mm1, ebx
	mov  esi, [ebp + _VFtab]
	movd eax, mm6
	psrlq mm6, 32
	movd ebx, mm6		/* indices in eax/ebx */

	movupd xmm4, [esi + eax*8]	/* Y1 F1 */	
	movupd xmm3, [esi + ebx*8]	/* Y2 F2 */
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 Y2 */
	unpckhpd xmm5, xmm3	/* F1 F2 */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */	
	movupd xmm3, [esi + ebx*8 + 16]	/* G2 H2 */
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 G2 */
	unpckhpd xmm7, xmm3	/* H1 H2 */
	/* coulomb table ready, in xmm4-xmm7 */ 
        mulpd  xmm6, xmm1       /* xmm6=Geps */
        mulpd  xmm7, xmm2       /* xmm7=Heps2 */
        addpd  xmm5, xmm6
        addpd  xmm5, xmm7       /* xmm5=Fp */       
        mulpd  xmm7, [esp + _two]       /* two*Heps2 */
        movapd xmm0, [esp + _qqO]
        addpd  xmm7, xmm6
        addpd  xmm7, xmm5 /* xmm7=FF */
        mulpd  xmm5, xmm1 /* xmm5=eps*Fp */
        addpd  xmm5, xmm4 /* xmm5=VV */
        mulpd  xmm5, xmm0 /* vcoul=qq*VV */ 
        mulpd  xmm0, xmm7 /* fijC=FF*qq */

	/* do nontable L-J */
	movapd xmm2, [esp + _rinvO]
	mulpd  xmm2, xmm2

        /* at this point mm5 contains vcoul and xmm0 fijC */
        /* increment vcoul - then we can get rid of mm5 */
        addpd  xmm5, [esp + _vctot]
        movapd [esp + _vctot], xmm5 

	movapd xmm1, xmm2
	mulpd  xmm1, xmm1
	mulpd  xmm1, xmm2	/* xmm1=rinvsix */
	movapd xmm4, xmm1
	mulpd  xmm4, xmm4	/* xmm4=rinvtwelve */
	mulpd  xmm1, [esp + _c6]
	mulpd  xmm4, [esp + _c12]
	movapd xmm3, xmm4
	subpd  xmm3, xmm1	/* xmm3=vnb12-vnb6 */
	mulpd  xmm1, [esp + _six]
	mulpd  xmm4, [esp + _twelve]
	subpd  xmm4, xmm1
	addpd  xmm3, [esp + _vnbtot]
	mulpd  xmm4, [esp + _rinvO]
	mulpd  xmm0, [esp + _tsc]
	subpd  xmm4, xmm0
	movapd [esp + _vnbtot], xmm3
	mulpd  xmm4, [esp + _rinvO]	

	movapd xmm0, [esp + _dxO]
	movapd xmm1, [esp + _dyO]
	movapd xmm2, [esp + _dzO]
	mulpd  xmm0, xmm4
	mulpd  xmm1, xmm4
	mulpd  xmm2, xmm4	/* tx in xmm0-xmm2 */

	/* update O forces */
	movapd xmm3, [esp + _fixO]
	movapd xmm4, [esp + _fiyO]
	movapd xmm7, [esp + _fizO]
	addpd  xmm3, xmm0
	addpd  xmm4, xmm1
	addpd  xmm7, xmm2
	movapd [esp + _fixO], xmm3
	movapd [esp + _fiyO], xmm4
	movapd [esp + _fizO], xmm7
	/* update j forces with water O */
	movapd [esp + _fjx], xmm0
	movapd [esp + _fjy], xmm1
	movapd [esp + _fjz], xmm2

	/* Done with O interactions - now H1! */
	movapd xmm7, [esp + _rH1]
	mulpd xmm7, [esp + _tsc]
	cvttpd2pi mm6, xmm7	/* mm6 = lu idx */
	cvtpi2pd xmm6, mm6
	subpd xmm7, xmm6
	movapd xmm1, xmm7	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulpd  xmm2, xmm2	/* xmm2=eps2 */
	
	pslld mm6, 2		/* idx *= 4 */
	mov  esi, [ebp + _VFtab]
	movd eax, mm6
	psrlq mm6, 32
	movd ebx, mm6		/* indices in eax/ebx */

	movupd xmm4, [esi + eax*8]	/* Y1 F1 */	
	movupd xmm3, [esi + ebx*8]	/* Y2 F2 */
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 Y2 */
	unpckhpd xmm5, xmm3	/* F1 F2 */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */	
	movupd xmm3, [esi + ebx*8 + 16]	/* G2 H2 */
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 G2 */
	unpckhpd xmm7, xmm3	/* H1 H2 */
	/* coulomb table ready, in xmm4-xmm7 */ 		
	mulpd  xmm6, xmm1	/* xmm6=Geps */
	mulpd  xmm7, xmm2	/* xmm7=Heps2 */
	addpd  xmm5, xmm6
	addpd  xmm5, xmm7	/* xmm5=Fp */	
	mulpd  xmm7, [esp + _two]	/* two*Heps2 */
	movapd xmm3, [esp + _qqH]
	addpd  xmm7, xmm6
	addpd  xmm7, xmm5 /* xmm7=FF */
	mulpd  xmm5, xmm1 /* xmm5=eps*Fp */
	addpd  xmm5, xmm4 /* xmm5=VV */
	mulpd  xmm5, xmm3 /* vcoul=qq*VV */ 
	mulpd  xmm3, xmm7 /* fijC=FF*qq */
        /* at this point mm5 contains vcoul and xmm3 fijC */
        /* increment vcoul */
	xorpd  xmm4, xmm4
        addpd  xmm5, [esp + _vctot]
	mulpd  xmm3, [esp + _rinvH1]
        movapd [esp + _vctot], xmm5 
	mulpd  xmm3, [esp + _tsc]
	subpd xmm4, xmm3

	movapd xmm0, [esp + _dxH1]
	movapd xmm1, [esp + _dyH1]
	movapd xmm2, [esp + _dzH1]
	mulpd  xmm0, xmm4
	mulpd  xmm1, xmm4
	mulpd  xmm2, xmm4

	/* update H1 forces */
	movapd xmm3, [esp + _fixH1]
	movapd xmm4, [esp + _fiyH1]
	movapd xmm7, [esp + _fizH1]
	addpd  xmm3, xmm0
	addpd  xmm4, xmm1
	addpd  xmm7, xmm2
	movapd [esp + _fixH1], xmm3
	movapd [esp + _fiyH1], xmm4
	movapd [esp + _fizH1], xmm7
	/* update j forces with water H1 */
	addpd  xmm0, [esp + _fjx]
	addpd  xmm1, [esp + _fjy]
	addpd  xmm2, [esp + _fjz]
	movapd [esp + _fjx], xmm0
	movapd [esp + _fjy], xmm1
	movapd [esp + _fjz], xmm2

	/* Done with H1, finally we do H2 interactions */
	movapd xmm7, [esp + _rH2]
	mulpd   xmm7, [esp + _tsc]
	cvttpd2pi mm6, xmm7	/* mm6 = lu idx */
	cvtpi2pd xmm6, mm6
	subpd xmm7, xmm6
	movapd xmm1, xmm7	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulpd  xmm2, xmm2	/* xmm2=eps2 */
	
	pslld mm6, 2		/* idx *= 4 */
	mov  esi, [ebp + _VFtab]
	movd eax, mm6
	psrlq mm6, 32
	movd ebx, mm6		/* indices in eax/ebx */

	movupd xmm4, [esi + eax*8]	/* Y1 F1 */	
	movupd xmm3, [esi + ebx*8]	/* Y2 F2 */
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 Y2 */
	unpckhpd xmm5, xmm3	/* F1 F2 */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */	
	movupd xmm3, [esi + ebx*8 + 16]	/* G2 H2 */
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 G2 */
	unpckhpd xmm7, xmm3	/* H1 H2 */
	/* coulomb table ready, in xmm4-xmm7 */ 		
	mulpd  xmm6, xmm1	/* xmm6=Geps */
	mulpd  xmm7, xmm2	/* xmm7=Heps2 */
	addpd  xmm5, xmm6
	addpd  xmm5, xmm7	/* xmm5=Fp */	
	mulpd  xmm7, [esp + _two]	/* two*Heps2 */
	movapd xmm3, [esp + _qqH]
	addpd  xmm7, xmm6
	addpd  xmm7, xmm5 /* xmm7=FF */
	mulpd  xmm5, xmm1 /* xmm5=eps*Fp */
	addpd  xmm5, xmm4 /* xmm5=VV */
	mulpd  xmm5, xmm3 /* vcoul=qq*VV */ 
	mulpd  xmm3, xmm7 /* fijC=FF*qq */
        /* at this point mm5 contains vcoul and xmm3 fijC */
        /* increment vcoul */
	xorpd  xmm4, xmm4
        addpd  xmm5, [esp + _vctot]
	mulpd  xmm3, [esp + _rinvH2]
        movapd [esp + _vctot], xmm5 
	mulpd  xmm3, [esp + _tsc]
	subpd  xmm4, xmm3

	movapd xmm0, [esp + _dxH2]
	movapd xmm1, [esp + _dyH2]
	movapd xmm2, [esp + _dzH2]
	mulpd  xmm0, xmm4
	mulpd  xmm1, xmm4
	mulpd  xmm2, xmm4

        movd eax, mm0   
        movd ebx, mm1
	
	/* update H2 forces */
	movapd xmm3, [esp + _fixH2]
	movapd xmm4, [esp + _fiyH2]
	movapd xmm7, [esp + _fizH2]
	addpd  xmm3, xmm0
	addpd  xmm4, xmm1
	addpd  xmm7, xmm2
	movapd [esp + _fixH2], xmm3
	movapd [esp + _fiyH2], xmm4
	movapd [esp + _fizH2], xmm7

	mov edi, [ebp + _faction]
	/* update j forces */
	addpd  xmm0, [esp + _fjx]
	addpd  xmm1, [esp + _fjy]
	addpd  xmm2, [esp + _fjz]

	/* the fj's - start by accumulating forces from memory */
	movlpd xmm3, [edi + eax*8]
	movlpd xmm4, [edi + eax*8 + 8]
	movlpd xmm5, [edi + eax*8 + 16]
	movhpd xmm3, [edi + ebx*8]
	movhpd xmm4, [edi + ebx*8 + 8]
	movhpd xmm5, [edi + ebx*8 + 16]
	subpd xmm3, xmm0
	subpd xmm4, xmm1
	subpd xmm5, xmm2
	movlpd [edi + eax*8], xmm3
	movlpd [edi + eax*8 + 8], xmm4
	movlpd [edi + eax*8 + 16], xmm5
	movhpd [edi + ebx*8], xmm3
	movhpd [edi + ebx*8 + 8], xmm4
	movhpd [edi + ebx*8 + 16], xmm5
			
	/* should we do one more iteration? */
	sub   [esp + _innerk],  2
	jl    .i3120_checksingle
	jmp   .i3120_unroll_loop
.i3120_checksingle:	
	mov   edx, [esp + _innerk]
	and   edx, 1
	jnz   .i3120_dosingle
	jmp   .i3120_updateouterdata
.i3120_dosingle:
	mov   edx, [esp + _innerjjnr]     /* pointer to jjnr[k] */
	mov   eax, [edx]

	mov esi, [ebp + _charge]        /* base of charge[] */
	xorpd xmm3, xmm3	
	movlpd xmm3, [esi + eax*8]
	movapd xmm4, xmm3	     
	mulpd  xmm3, [esp + _iqO]
	mulpd  xmm4, [esp + _iqH]

	movd  mm0, eax		/* use mmx registers as temp storage */
	movapd  [esp + _qqO], xmm3
	movapd  [esp + _qqH], xmm4	

	mov esi, [ebp + _type]
	mov eax, [esi + eax*4]
	mov esi, [ebp + _nbfp]
	shl eax, 1	
	mov edi, [esp + _ntia]
	add eax, edi

	movupd xmm6, [esi + eax*8]	/* c6a c12a */
	xorpd xmm7, xmm7
	movapd xmm4, xmm6
	unpcklpd xmm4, xmm7
	unpckhpd xmm6, xmm7
	
	movd  eax, mm0
	movapd [esp + _c6], xmm4
	movapd [esp + _c12], xmm6
	
	mov esi, [ebp + _pos]       /* base of pos[] */
	lea   eax, [eax + eax*2]         /* replace jnr with j3 */
	
	/* move coords to xmm0-xmm2 */
	movlpd xmm0, [esi + eax*8]
	movlpd xmm1, [esi + eax*8 + 8]
	movlpd xmm2, [esi + eax*8 + 16]

	/* move ixO-izO to xmm4-xmm6 */
	movapd xmm4, [esp + _ixO]
	movapd xmm5, [esp + _iyO]
	movapd xmm6, [esp + _izO]

	/* calc dr */
	subsd xmm4, xmm0
	subsd xmm5, xmm1
	subsd xmm6, xmm2

	/* store dr */
	movapd [esp + _dxO], xmm4
	movapd [esp + _dyO], xmm5
	movapd [esp + _dzO], xmm6
	/* square it */
	mulsd xmm4,xmm4
	mulsd xmm5,xmm5
	mulsd xmm6,xmm6
	addsd xmm4, xmm5
	addsd xmm4, xmm6
	movapd xmm7, xmm4
	/* rsqO in xmm7 */

	/* move ixH1-izH1 to xmm4-xmm6 */
	movapd xmm4, [esp + _ixH1]
	movapd xmm5, [esp + _iyH1]
	movapd xmm6, [esp + _izH1]

	/* calc dr */
	subsd xmm4, xmm0
	subsd xmm5, xmm1
	subsd xmm6, xmm2

	/* store dr */
	movapd [esp + _dxH1], xmm4
	movapd [esp + _dyH1], xmm5
	movapd [esp + _dzH1], xmm6
	/* square it */
	mulsd xmm4,xmm4
	mulsd xmm5,xmm5
	mulsd xmm6,xmm6
	addsd xmm6, xmm5
	addsd xmm6, xmm4
	/* rsqH1 in xmm6 */

	/* move ixH2-izH2 to xmm3-xmm5 */ 
	movapd xmm3, [esp + _ixH2]
	movapd xmm4, [esp + _iyH2]
	movapd xmm5, [esp + _izH2]

	/* calc dr */
	subsd xmm3, xmm0
	subsd xmm4, xmm1
	subsd xmm5, xmm2

	/* store dr */
	movapd [esp + _dxH2], xmm3
	movapd [esp + _dyH2], xmm4
	movapd [esp + _dzH2], xmm5
	/* square it */
	mulsd xmm3,xmm3
	mulsd xmm4,xmm4
	mulsd xmm5,xmm5
	addsd xmm5, xmm4
	addsd xmm5, xmm3
	/* rsqH2 in xmm5, rsqH1 in xmm6, rsqO in xmm7 */

	/* start with rsqO - put seed in xmm2 */
	cvtsd2ss xmm2, xmm7	
	rsqrtss xmm2, xmm2
	cvtss2sd xmm2, xmm2

	movapd  xmm3, xmm2
	mulsd   xmm2, xmm2
	movapd  xmm4, [esp + _three]
	mulsd   xmm2, xmm7	/* rsq*lu*lu */
	subsd   xmm4, xmm2	/* 30-rsq*lu*lu */
	mulsd   xmm4, xmm3	/* lu*(3-rsq*lu*lu) */
	mulsd   xmm4, [esp + _half] /* iter1 ( new lu) */

	movapd xmm2, xmm7
	movapd xmm3, xmm4
	mulsd xmm4, xmm4	/* lu*lu */
	mulsd xmm2, xmm4	/* rsq*lu*lu */
	movapd xmm4, [esp + _three]
	subsd xmm4, xmm2	/* 3-rsq*lu*lu */
	mulsd xmm4, xmm3	/* lu*(	3-rsq*lu*lu) */
	mulsd xmm4, [esp + _half] /* rinv */
	movapd  [esp + _rinvO], xmm4	/* rinvO in xmm4 */
	mulsd   xmm7, xmm4
	movapd  [esp + _rO], xmm7	/* r in xmm7 */
	
	/* rsqH1 - seed in xmm2 */
	cvtsd2ss xmm2, xmm6	
	rsqrtss xmm2, xmm2
	cvtss2sd xmm2, xmm2

	movapd  xmm3, xmm2
	mulsd   xmm2, xmm2
	movapd  xmm4, [esp + _three]
	mulsd   xmm2, xmm6	/* rsq*lu*lu */
	subsd   xmm4, xmm2	/* 30-rsq*lu*lu */
	mulsd   xmm4, xmm3	/* lu*(3-rsq*lu*lu) */
	mulsd   xmm4, [esp + _half] /* iter1 ( new lu) */

	movapd xmm2, xmm6
	movapd xmm3, xmm4
	mulsd xmm4, xmm4	/* lu*lu */
	mulsd xmm2, xmm4	/* rsq*lu*lu */
	movapd xmm4, [esp + _three]
	subsd xmm4, xmm2	/* 3-rsq*lu*lu */
	mulsd xmm4, xmm3	/* lu*(	3-rsq*lu*lu) */
	mulsd xmm4, [esp + _half] /* rinv */
	movapd [esp + _rinvH1], xmm4	/* rinvH1 */
	mulsd  xmm6, xmm4
	movapd [esp + _rH1], xmm6	/* rH1 */
	
	/* rsqH2 - seed in xmm2 */
	cvtsd2ss xmm2, xmm5	
	rsqrtss xmm2, xmm2
	cvtss2sd xmm2, xmm2

	movapd  xmm3, xmm2
	mulsd   xmm2, xmm2
	movapd  xmm4, [esp + _three]
	mulsd   xmm2, xmm5	/* rsq*lu*lu */
	subsd   xmm4, xmm2	/* 30-rsq*lu*lu */
	mulsd   xmm4, xmm3	/* lu*(3-rsq*lu*lu) */
	mulsd   xmm4, [esp + _half] /* iter1 ( new lu) */

	movapd xmm2, xmm5
	movapd xmm3, xmm4
	mulsd xmm4, xmm4	/* lu*lu */
	mulsd xmm2, xmm4	/* rsq*lu*lu */
	movapd xmm4, [esp + _three]
	subsd xmm4, xmm2	/* 3-rsq*lu*lu */
	mulsd xmm4, xmm3	/* lu*(	3-rsq*lu*lu) */
	mulsd xmm4, [esp + _half] /* rinv */
	movapd [esp + _rinvH2], xmm4 /* rinv */
	mulsd xmm5, xmm4
	movapd [esp + _rH2], xmm5 /* r */

	/* do O interactions */
	movd mm0, eax	
	/* rO is still in xmm7 */
	movapd xmm0, [esp + _rinvO]
	mulsd   xmm7, [esp + _tsc]
	cvttsd2si eax, xmm7	/* mm6 = lu idx */
	cvtsi2sd xmm6, eax
	subsd xmm7, xmm6
	movapd xmm1, xmm7	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulsd  xmm2, xmm2	/* xmm2=eps2 */
	
	shl eax, 2		/* idx *= 4 */
	mov  esi, [ebp + _VFtab]

	movupd xmm4, [esi + eax*8]	/* Y1 F1 */	
	xorpd xmm3, xmm3
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 */
	unpckhpd xmm5, xmm3	/* F1 */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */	
	xorpd xmm3, xmm3
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 */
	unpckhpd xmm7, xmm3	/* H1 */
	/* coulomb table ready, in xmm4-xmm7 */ 
        mulsd  xmm6, xmm1       /* xmm6=Geps */
        mulsd  xmm7, xmm2       /* xmm7=Heps2 */
        addsd  xmm5, xmm6
        addsd  xmm5, xmm7       /* xmm5=Fp */       
        mulsd  xmm7, [esp + _two]       /* two*Heps2 */
        movapd xmm0, [esp + _qqO]
        addsd  xmm7, xmm6
        addsd  xmm7, xmm5 /* xmm7=FF */
        mulsd  xmm5, xmm1 /* xmm5=eps*Fp */
        addsd  xmm5, xmm4 /* xmm5=VV */
        mulsd  xmm5, xmm0 /* vcoul=qq*VV */ 
        mulsd  xmm0, xmm7 /* fijC=FF*qq */

	/* do nontable L-J */
	movapd xmm2, [esp + _rinvO]
	mulsd  xmm2, xmm2

        /* at this point mm5 contains vcoul and xmm0 fijC */
        /* increment vcoul - then we can get rid of mm5 */
        addsd  xmm5, [esp + _vctot]
        movlpd [esp + _vctot], xmm5 

	movapd xmm1, xmm2
	mulsd  xmm1, xmm1
	mulsd  xmm1, xmm2	/* xmm1=rinvsix */
	movapd xmm4, xmm1
	mulsd  xmm4, xmm4	/* xmm4=rinvtwelve */
	mulsd  xmm1, [esp + _c6]
	mulsd  xmm4, [esp + _c12]
	movapd xmm3, xmm4
	subsd  xmm3, xmm1	/* xmm3=vnb12-vnb6 */
	mulsd  xmm1, [esp + _six]
	mulsd  xmm4, [esp + _twelve]
	subsd  xmm4, xmm1
	addsd  xmm3, [esp + _vnbtot]
	mulsd  xmm4, [esp + _rinvO]
	mulsd  xmm0, [esp + _tsc]
	subsd  xmm4, xmm0
	movlpd [esp + _vnbtot], xmm3
	mulsd  xmm4, [esp + _rinvO]	

	movapd xmm0, [esp + _dxO]
	movapd xmm1, [esp + _dyO]
	movapd xmm2, [esp + _dzO]
	mulsd  xmm0, xmm4
	mulsd  xmm1, xmm4
	mulsd  xmm2, xmm4	/* tx in xmm0-xmm2 */

	/* update O forces */
	movapd xmm3, [esp + _fixO]
	movapd xmm4, [esp + _fiyO]
	movapd xmm7, [esp + _fizO]
	addsd  xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm7, xmm2
	movlpd [esp + _fixO], xmm3
	movlpd [esp + _fiyO], xmm4
	movlpd [esp + _fizO], xmm7
	/* update j forces with water O */
	movlpd [esp + _fjx], xmm0
	movlpd [esp + _fjy], xmm1
	movlpd [esp + _fjz], xmm2

	/* Done with O interactions - now H1! */
	movapd xmm7, [esp + _rH1]
	mulpd xmm7, [esp + _tsc]
	cvttsd2si eax, xmm7	/* mm6 = lu idx */
	cvtsi2sd xmm6, eax
	subpd xmm7, xmm6
	movapd xmm1, xmm7	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulpd  xmm2, xmm2	/* xmm2=eps2 */
	
	shl eax, 2		/* idx *= 4 */
	mov  esi, [ebp + _VFtab]

	movupd xmm4, [esi + eax*8]	/* Y1 F1 */	
	xorpd xmm3, xmm3
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 */
	unpckhpd xmm5, xmm3	/* F1 */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */	
	xorpd xmm3, xmm3
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 */
	unpckhpd xmm7, xmm3	/* H1 */
	/* coulomb table ready, in xmm4-xmm7 */ 		
	mulsd  xmm6, xmm1	/* xmm6=Geps */
	mulsd  xmm7, xmm2	/* xmm7=Heps2 */
	addsd  xmm5, xmm6
	addsd  xmm5, xmm7	/* xmm5=Fp */	
	mulsd  xmm7, [esp + _two]	/* two*Heps2 */
	movapd xmm3, [esp + _qqH]
	addsd  xmm7, xmm6
	addsd  xmm7, xmm5 /* xmm7=FF */
	mulsd  xmm5, xmm1 /* xmm5=eps*Fp */
	addsd  xmm5, xmm4 /* xmm5=VV */
	mulsd  xmm5, xmm3 /* vcoul=qq*VV */ 
	mulsd  xmm3, xmm7 /* fijC=FF*qq */
        /* at this point mm5 contains vcoul and xmm3 fijC */
        /* increment vcoul */
	xorpd  xmm4, xmm4
        addsd  xmm5, [esp + _vctot]
	mulsd  xmm3, [esp + _rinvH1]
        movlpd [esp + _vctot], xmm5 
	mulsd  xmm3, [esp + _tsc]
	subsd xmm4, xmm3

	movapd xmm0, [esp + _dxH1]
	movapd xmm1, [esp + _dyH1]
	movapd xmm2, [esp + _dzH1]
	mulsd  xmm0, xmm4
	mulsd  xmm1, xmm4
	mulsd  xmm2, xmm4

	/* update H1 forces */
	movapd xmm3, [esp + _fixH1]
	movapd xmm4, [esp + _fiyH1]
	movapd xmm7, [esp + _fizH1]
	addsd  xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm7, xmm2
	movlpd [esp + _fixH1], xmm3
	movlpd [esp + _fiyH1], xmm4
	movlpd [esp + _fizH1], xmm7
	/* update j forces with water H1 */
	addsd  xmm0, [esp + _fjx]
	addsd  xmm1, [esp + _fjy]
	addsd  xmm2, [esp + _fjz]
	movlpd [esp + _fjx], xmm0
	movlpd [esp + _fjy], xmm1
	movlpd [esp + _fjz], xmm2

	/* Done with H1, finally we do H2 interactions */
	movapd xmm7, [esp + _rH2]
	mulsd   xmm7, [esp + _tsc]
	cvttsd2si eax, xmm7	/* mm6 = lu idx */
	cvtsi2sd xmm6, eax
	subsd xmm7, xmm6
	movapd xmm1, xmm7	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulsd  xmm2, xmm2	/* xmm2=eps2 */
	
	shl eax, 2		/* idx *= 4 */
	mov  esi, [ebp + _VFtab]

	movupd xmm4, [esi + eax*8]	/* Y1 F1 */	
	xorpd xmm3, xmm3
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 */
	unpckhpd xmm5, xmm3	/* F1 */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */	
	xorpd xmm3, xmm3
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 */
	unpckhpd xmm7, xmm3	/* H1 */
	/* coulomb table ready, in xmm4-xmm7 */ 		
	mulsd  xmm6, xmm1	/* xmm6=Geps */
	mulsd  xmm7, xmm2	/* xmm7=Heps2 */
	addsd  xmm5, xmm6
	addsd  xmm5, xmm7	/* xmm5=Fp */	
	mulsd  xmm7, [esp + _two]	/* two*Heps2 */
	movapd xmm3, [esp + _qqH]
	addsd  xmm7, xmm6
	addsd  xmm7, xmm5 /* xmm7=FF */
	mulsd  xmm5, xmm1 /* xmm5=eps*Fp */
	addsd  xmm5, xmm4 /* xmm5=VV */
	mulsd  xmm5, xmm3 /* vcoul=qq*VV */ 
	mulsd  xmm3, xmm7 /* fijC=FF*qq */
        /* at this point mm5 contains vcoul and xmm3 fijC */
        /* increment vcoul */
	xorpd  xmm4, xmm4
        addsd  xmm5, [esp + _vctot]
	mulsd  xmm3, [esp + _rinvH2]
        movlpd [esp + _vctot], xmm5 
	mulsd  xmm3, [esp + _tsc]
	subsd  xmm4, xmm3

	movapd xmm0, [esp + _dxH2]
	movapd xmm1, [esp + _dyH2]
	movapd xmm2, [esp + _dzH2]
	mulsd  xmm0, xmm4
	mulsd  xmm1, xmm4
	mulsd  xmm2, xmm4

        movd eax, mm0   
	
	/* update H2 forces */
	movapd xmm3, [esp + _fixH2]
	movapd xmm4, [esp + _fiyH2]
	movapd xmm7, [esp + _fizH2]
	addsd  xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm7, xmm2
	movlpd [esp + _fixH2], xmm3
	movlpd [esp + _fiyH2], xmm4
	movlpd [esp + _fizH2], xmm7

	mov edi, [ebp + _faction]
	/* update j forces */
	addsd  xmm0, [esp + _fjx]
	addsd  xmm1, [esp + _fjy]
	addsd  xmm2, [esp + _fjz]

	/* the fj's - start by accumulating forces from memory */
	movlpd xmm3, [edi + eax*8]
	movlpd xmm4, [edi + eax*8 + 8]
	movlpd xmm5, [edi + eax*8 + 16]
	subsd xmm3, xmm0
	subsd xmm4, xmm1
	subsd xmm5, xmm2
	movlpd [edi + eax*8], xmm3
	movlpd [edi + eax*8 + 8], xmm4
	movlpd [edi + eax*8 + 16], xmm5

.i3120_updateouterdata:
	mov   ecx, [esp + _ii3]
	mov   edi, [ebp + _faction]
	mov   esi, [ebp + _fshift]
	mov   edx, [esp + _is3]

	/* accumulate  Oi forces in xmm0, xmm1, xmm2 */
	movapd xmm0, [esp + _fixO]
	movapd xmm1, [esp + _fiyO]
	movapd xmm2, [esp + _fizO]

	movhlps xmm3, xmm0
	movhlps xmm4, xmm1
	movhlps xmm5, xmm2
	addsd  xmm0, xmm3
	addsd  xmm1, xmm4
	addsd  xmm2, xmm5 /* sum is in low xmm0-xmm2 */

	movapd xmm3, xmm0	
	movapd xmm4, xmm1	
	movapd xmm5, xmm2	

	/* increment i force */
	movsd  xmm3, [edi + ecx*8]
	movsd  xmm4, [edi + ecx*8 + 8]
	movsd  xmm5, [edi + ecx*8 + 16]
	addsd  xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm5, xmm2
	movsd  [edi + ecx*8],     xmm3
	movsd  [edi + ecx*8 + 8], xmm4
	movsd  [edi + ecx*8 + 16], xmm5

	/* accumulate force in xmm6/xmm7 for fshift */
	movapd xmm6, xmm0
	movsd xmm7, xmm2
	unpcklpd xmm6, xmm1

	/* accumulate H1i forces in xmm0, xmm1, xmm2 */
	movapd xmm0, [esp + _fixH1]
	movapd xmm1, [esp + _fiyH1]
	movapd xmm2, [esp + _fizH1]

	movhlps xmm3, xmm0
	movhlps xmm4, xmm1
	movhlps xmm5, xmm2
	addsd  xmm0, xmm3
	addsd  xmm1, xmm4
	addsd  xmm2, xmm5 /* sum is in low xmm0-xmm2 */

	/* increment i force */
	movsd  xmm3, [edi + ecx*8 + 24]
	movsd  xmm4, [edi + ecx*8 + 32]
	movsd  xmm5, [edi + ecx*8 + 40]
	addsd  xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm5, xmm2
	movsd  [edi + ecx*8 + 24], xmm3
	movsd  [edi + ecx*8 + 32], xmm4
	movsd  [edi + ecx*8 + 40], xmm5

	/* accumulate force in xmm6/xmm7 for fshift */
	addsd xmm7, xmm2
	unpcklpd xmm0, xmm1
	addpd xmm6, xmm0

	/* accumulate H2i forces in xmm0, xmm1, xmm2 */
	movapd xmm0, [esp + _fixH2]
	movapd xmm1, [esp + _fiyH2]
	movapd xmm2, [esp + _fizH2]

	movhlps xmm3, xmm0
	movhlps xmm4, xmm1
	movhlps xmm5, xmm2
	addsd  xmm0, xmm3
	addsd  xmm1, xmm4
	addsd  xmm2, xmm5 /* sum is in low xmm0-xmm2 */

	movapd xmm3, xmm0	
	movapd xmm4, xmm1	
	movapd xmm5, xmm2	

	/* increment i force */
	movsd  xmm3, [edi + ecx*8 + 48]
	movsd  xmm4, [edi + ecx*8 + 56]
	movsd  xmm5, [edi + ecx*8 + 64]
	addsd  xmm3, xmm0
	addsd  xmm4, xmm1
	addsd  xmm5, xmm2
	movsd  [edi + ecx*8 + 48], xmm3
	movsd  [edi + ecx*8 + 56], xmm4
	movsd  [edi + ecx*8 + 64], xmm5

	/* accumulate force in xmm6/xmm7 for fshift */
	addsd xmm7, xmm2
	unpcklpd xmm0, xmm1
	addpd xmm6, xmm0

	/* increment fshift force */
	movupd xmm3, [esi + edx*8]
	movsd  xmm4, [esi + edx*8 + 16]
	addpd  xmm3, xmm6
	addsd  xmm4, xmm7
	movupd [esi + edx*8],    xmm3
	movsd  [esi + edx*8 + 16], xmm4

	mov   edx, [ebp + _gid]  
	mov   edx, [edx]
	add   [ebp + _gid],  4	

	/* accumulate total potential energy and update it */
	movapd xmm7, [esp + _vctot]
	/* accumulate */
	movhlps xmm6, xmm7
	addsd  xmm7, xmm6	/* low xmm7 has the sum now */
        
	/* add earlier value from mem */
	mov   eax, [ebp + _Vc]
	addsd xmm7, [eax + edx*8] 
	/* move back to mem */
	movsd [eax + edx*8], xmm7 
	
	/* accumulate total lj energy and update it */
	movapd xmm7, [esp + _vnbtot]
	/* accumulate */
	movhlps xmm6, xmm7
	addsd  xmm7, xmm6	/* low xmm7 has the sum now */

	/* add earlier value from mem */
	mov   eax, [ebp + _Vnb]
	addsd xmm7, [eax + edx*8] 
	/* move back to mem */
	movsd [eax + edx*8], xmm7 
	
	/* finish if last */
	mov   ecx, [ebp + _nri]
	dec ecx
	jecxz .i3120_end
	/* not last, iterate once more! */ 
	mov [ebp + _nri], ecx
	jmp .i3120_outer
.i3120_end:
	emms
	mov eax, [esp + _salign]
	add esp, eax
	add esp, 824
	pop edi
	pop esi
        pop edx
        pop ecx
        pop ebx
        pop eax
	leave
	ret
	

	
