	subpd xmm4, xmm3

	movapd xmm0, [esp + _dxH1]
	movapd xmm1, [esp + _dyH1]
	movapd xmm2, [esp + _dzH1]
	mulpd  xmm0, xmm4
	mulpd  xmm1, xmm4
	mulpd  xmm2, xmm4

