	movapd [esp + _dxH1], xmm4
	movapd [esp + _dyH1], xmm5
	movapd [esp + _dzH1], xmm6
	/* square it */
	mulsd xmm4,xmm4
	mulsd xmm5,xmm5
	mulsd xmm6,xmm6
	addsd xmm6, xmm5
	addsd xmm6, xmm4
