	movapd [esp + _dxH2], xmm3
	movapd [esp + _dyH2], xmm4
	movapd [esp + _dzH2], xmm5
	/* square it */
	mulsd xmm3,xmm3
	mulsd xmm4,xmm4
	mulsd xmm5,xmm5
	addsd xmm5, xmm4
	addsd xmm5, xmm3
