	mulsd  xmm5, xmm1 /* xmm5=eps*Fp */
	addsd  xmm5, xmm4 /* xmm5=VV */
	mulsd  xmm5, xmm3 /* vcoul=qq*VV */ 
	mulsd  xmm3, xmm7 /* fijC=FF*qq */
        /* at this point mm5 contains vcoul and xmm3 fijC */

        addsd  xmm5, [esp + _vctot]
        movlpd [esp + _vctot], xmm5
	xorpd  xmm1, xmm1
	mulsd  xmm3,  [esp + _tsc]
	mulsd  xmm3, xmm0
	subsd  xmm1, xmm3

