	movlpd [esp + _fjyH2], xmm4
	movlpd [esp + _fjzH2], xmm5
	movlpd [esp + _fixH1], xmm0
	movlpd [esp + _fiyH1], xmm1
	movlpd [esp + _fizH1], xmm2

