	mulsd xmm1, xmm1	/* lu*lu */
	movapd xmm2, [esp + _three]
	mulsd xmm1, xmm4	/* rsq*lu*lu */			
	movapd xmm0, [esp + _half]
	subsd xmm2, xmm1	/* 30-rsq*lu*lu */
	mulsd xmm2, xmm5	
