	subpd xmm4, xmm0
	subpd xmm5, xmm1
	subpd xmm6, xmm2

	/* square it */
	mulpd xmm4,xmm4
	mulpd xmm5,xmm5
	mulpd xmm6,xmm6
	addpd xmm4, xmm5
	addpd xmm4, xmm6
	movapd xmm7, xmm4
	/* rsqO in xmm7 */

	/* move ixH1-izH1 to xmm4-xmm6 */
	movapd xmm4, [esp + _ixH1]
	movapd xmm5, [esp + _iyH1]
	movapd xmm6, [esp + _izH1]

	/* calc dr */
	subpd xmm4, xmm0
	subpd xmm5, xmm1
	subpd xmm6, xmm2

	/* square it */
	mulpd xmm4,xmm4
	mulpd xmm5,xmm5
	mulpd xmm6,xmm6
	addpd xmm6, xmm5
	addpd xmm6, xmm4
	/* rsqH1 in xmm6 */

	/* move ixH2-izH2 to xmm3-xmm5 */ 
	movapd xmm3, [esp + _ixH2]
	movapd xmm4, [esp + _iyH2]
	movapd xmm5, [esp + _izH2]

	/* calc dr */
	subpd xmm3, xmm0
	subpd xmm4, xmm1
	subpd xmm5, xmm2

	/* square it */
	mulpd xmm3,xmm3
	mulpd xmm4,xmm4
	mulpd xmm5,xmm5
	addpd xmm5, xmm4
	addpd xmm5, xmm3
	/* rsqH2 in xmm5, rsqH1 in xmm6, rsqO in xmm7 */

	/* start with rsqO - put seed in xmm2 */
	cvtpd2ps xmm2, xmm7	
	rsqrtps xmm2, xmm2
	cvtps2pd xmm2, xmm2

	movapd  xmm3, xmm2
	mulpd   xmm2, xmm2
	movapd  xmm4, [esp + _three]
	mulpd   xmm2, xmm7	/* rsq*lu*lu */
	subpd   xmm4, xmm2	/* 30-rsq*lu*lu */
	mulpd   xmm4, xmm3	/* lu*(3-rsq*lu*lu) */
	mulpd   xmm4, [esp + _half] /* iter1 ( new lu) */

	movapd xmm3, xmm4
	mulpd xmm4, xmm4	/* lu*lu */
	mulpd xmm7, xmm4	/* rsq*lu*lu */
	movapd xmm4, [esp + _three]
	subpd xmm4, xmm7	/* 3-rsq*lu*lu */
	mulpd xmm4, xmm3	/* lu*(	3-rsq*lu*lu) */
	mulpd xmm4, [esp + _half] /* rinv */
	movapd  xmm7, xmm4	/* rinvO in xmm7 */
	
