	mulpd  xmm6, [esp + _qqH]	/* xmm6=vcoul */
	addpd  xmm6, [esp + _vctot]
	movapd [esp + _vctot], xmm6

	/* H2 interactions */
	mulpd  xmm5, [esp + _qqH]	/* xmm5=vcoul */
	addpd  xmm5, [esp + _vctot]
	movapd [esp + _vctot], xmm5

