	sub   [esp + _innerk],  2
	jl    imc1020_checksingle
	jmp   imc1020_unroll_loop
imc1020_checksingle:				
	mov   edx, [esp + _innerk]
	and   edx, 1
	jnz    imc1020_dosingle
	jmp    imc1020_updateouterdata
imc1020_dosingle:
