	mov   eax, [edx]	

	mov esi, [ebp + _charge]        /* base of charge[] */
	xorpd xmm6, xmm6
	movlpd xmm6, [esi + eax*8]	/* jq A */
	
	movapd xmm3, [esp + _iqO]
	movapd xmm4, [esp + _iqH]
	mulsd xmm3, xmm6		/* qqO */
	mulsd xmm4, xmm6		/* qqH */
	
	movapd  [esp + _qqO], xmm3
	movapd  [esp + _qqH], xmm4	

	mov esi, [ebp + _pos]       /* base of pos[] */

