	mov   eax, [ecx]

	movlpd xmm3, [esi + eax*8]
	movapd xmm5, [esp + _iq]
	mulpd xmm3, xmm5		/* qq */
	
	mov esi, [ebp + _pos]       /* base of pos[] */

	lea eax, [eax + eax*2]         /* replace jnr with j3 */

	/* move two coordinates to xmm0-xmm2 */	
	movlpd xmm0, [esi + eax*8]
	movlpd xmm1, [esi + eax*8 + 8]
	movlpd xmm2, [esi + eax*8 + 16]
	
	/* move ix-iz to xmm4-xmm6 */
	movapd xmm4, [esp + _ix]
	movapd xmm5, [esp + _iy]
	movapd xmm6, [esp + _iz]

	/* calc dr */
	subsd xmm4, xmm0
	subsd xmm5, xmm1
	subsd xmm6, xmm2

	/* square it */
	mulsd xmm4,xmm4
	mulsd xmm5,xmm5
	mulsd xmm6,xmm6
	addsd xmm4, xmm5
	addsd xmm4, xmm6
	/* rsq in xmm4 */

	cvtsd2ss xmm5, xmm4	
	rsqrtss xmm5, xmm5
	cvtss2sd xmm2, xmm5	/* lu in low xmm2 */

	movapd xmm7, [esp + _krf]	
