.equ		_iyO,		16
.equ		_izO,		32
.equ		_ixH1,		48
.equ		_iyH1,		64
.equ		_izH1,		80
.equ		_ixH2,		96
.equ		_iyH2,		112
.equ		_izH2,		128
.equ		_iqO,		144 
.equ		_iqH,		160 
.equ		_qqO,		176
.equ		_qqH,		192
.equ		_c6,		208
.equ		_c12,		224		 
.equ		_vctot,		240
.equ		_vnbtot,	256
.equ		_half,		272
.equ		_three,		288
.equ		_krf,		304
.equ		_crf,		320
