	movapd [esp + _three], xmm1
	shufpd xmm5, xmm5, 0
	shufpd xmm6, xmm6, 0
	movapd [esp + _krf], xmm5
	movapd [esp + _crf], xmm6
	
	/* assume we have at least one i particle - start directly */
	mov   ecx, [ebp + _iinr]       /* ecx = pointer into iinr[] */	
	mov   ebx, [ecx]	        /* ebx =ii */

	mov   edx, [ebp + _charge]
