	xorpd xmm3, xmm3
	movlpd xmm3, [esi + eax*8]
	movapd xmm4, xmm3
	mulpd  xmm3, [esp + _iqO]
	mulpd  xmm4, [esp + _iqH]

	movd  mm0, eax		/* use mmx registers as temp storage */

	movapd  [esp + _qqO], xmm3
	movapd  [esp + _qqH], xmm4
	
	mov esi, [ebp + _type]
	mov eax, [esi + eax*4]
	mov esi, [ebp + _nbfp]
	shl eax, 1	
	mov edi, [esp + _ntia]
	add eax, edi

	movupd xmm6, [esi + eax*8]	/* c6a c12a */
	xorpd xmm7, xmm7
	movapd xmm4, xmm6
	unpcklpd xmm4, xmm7
	unpckhpd xmm6, xmm7
	
	movd  eax, mm0
	movapd [esp + _c6], xmm4
	movapd [esp + _c12], xmm6
	
	mov esi, [ebp + _pos]       /* base of pos[] */

	lea   eax, [eax + eax*2]         /* replace jnr with j3 */

	/* move coordinates to xmm0-xmm2 */
	movlpd xmm0, [esi + eax*8]
	movlpd xmm1, [esi + eax*8 + 8]
	movlpd xmm2, [esi + eax*8 + 16]

	/* move ixO-izO to xmm4-xmm6 */
	movapd xmm4, [esp + _ixO]
	movapd xmm5, [esp + _iyO]
	movapd xmm6, [esp + _izO]

	/* calc dr */
	subsd xmm4, xmm0
	subsd xmm5, xmm1
	subsd xmm6, xmm2

	/* square it */
	mulsd xmm4,xmm4
	mulsd xmm5,xmm5
	mulsd xmm6,xmm6
	addsd xmm4, xmm5
	addsd xmm4, xmm6
	movapd xmm7, xmm4
	/* rsqO in xmm7 */

	/* move ixH1-izH1 to xmm4-xmm6 */
	movapd xmm4, [esp + _ixH1]
	movapd xmm5, [esp + _iyH1]
	movapd xmm6, [esp + _izH1]

	/* calc dr */
	subsd xmm4, xmm0
	subsd xmm5, xmm1
	subsd xmm6, xmm2

	/* square it */
	mulsd xmm4,xmm4
	mulsd xmm5,xmm5
	mulsd xmm6,xmm6
	addsd xmm6, xmm5
	addsd xmm6, xmm4
	/* rsqH1 in xmm6 */

	/* move ixH2-izH2 to xmm3-xmm5 */ 
	movapd xmm3, [esp + _ixH2]
	movapd xmm4, [esp + _iyH2]
	movapd xmm5, [esp + _izH2]

	/* calc dr */
	subsd xmm3, xmm0
	subsd xmm4, xmm1
	subsd xmm5, xmm2

	/* square it */
	mulsd xmm3,xmm3
	mulsd xmm4,xmm4
	mulsd xmm5,xmm5
	addsd xmm5, xmm4
	addsd xmm5, xmm3
	/* rsqH2 in xmm5, rsqH1 in xmm6, rsqO in xmm7 */

	movapd xmm0, xmm5
	movapd xmm1, xmm6
	movapd xmm2, xmm7

	mulsd  xmm0, [esp + _krf]	
	mulsd  xmm1, [esp + _krf]	
	mulsd  xmm2, [esp + _krf]	

	movapd [esp + _krsqH2], xmm0
	movapd [esp + _krsqH1], xmm1
	movapd [esp + _krsqO], xmm2

	/* start with rsqO - put seed in xmm2 */
	cvtsd2ss xmm2, xmm7	
	rsqrtss xmm2, xmm2
	cvtss2sd xmm2, xmm2

	movapd  xmm3, xmm2
	mulsd   xmm2, xmm2
	movapd  xmm4, [esp + _three]
	mulsd   xmm2, xmm7	/* rsq*lu*lu */
	subsd   xmm4, xmm2	/* 30-rsq*lu*lu */
	mulsd   xmm4, xmm3	/* lu*(3-rsq*lu*lu) */
	mulsd   xmm4, [esp + _half] /* iter1 ( new lu) */

	movapd xmm3, xmm4
	mulsd xmm4, xmm4	/* lu*lu */
	mulsd xmm7, xmm4	/* rsq*lu*lu */
	movapd xmm4, [esp + _three]
	subsd xmm4, xmm7	/* 3-rsq*lu*lu */
	mulsd xmm4, xmm3	/* lu*(	3-rsq*lu*lu) */
	mulsd xmm4, [esp + _half] /* rinv */
	movapd  xmm7, xmm4	/* rinvO in xmm7 */
	
	/* rsqH1 - seed in xmm2 */
	cvtsd2ss xmm2, xmm6	
	rsqrtss xmm2, xmm2
	cvtss2sd xmm2, xmm2

	movapd  xmm3, xmm2
	mulsd   xmm2, xmm2
	movapd  xmm4, [esp + _three]
	mulsd   xmm2, xmm6	/* rsq*lu*lu */
	subsd   xmm4, xmm2	/* 30-rsq*lu*lu */
	mulsd   xmm4, xmm3	/* lu*(3-rsq*lu*lu) */
	mulsd   xmm4, [esp + _half] /* iter1 ( new lu) */

	movapd xmm3, xmm4
	mulsd xmm4, xmm4	/* lu*lu */
	mulsd xmm6, xmm4	/* rsq*lu*lu */
	movapd xmm4, [esp + _three]
	subsd xmm4, xmm6	/* 3-rsq*lu*lu */
	mulsd xmm4, xmm3	/* lu*(	3-rsq*lu*lu) */
	mulsd xmm4, [esp + _half] /* rinv */
	movapd  xmm6, xmm4	/* rinvH1 in xmm6 */
	
	/* rsqH2 - seed in xmm2 */
	cvtsd2ss xmm2, xmm5	
	rsqrtss xmm2, xmm2
	cvtss2sd xmm2, xmm2

	movapd  xmm3, xmm2
	mulsd   xmm2, xmm2
	movapd  xmm4, [esp + _three]
	mulsd   xmm2, xmm5	/* rsq*lu*lu */
	subsd   xmm4, xmm2	/* 30-rsq*lu*lu */
	mulsd   xmm4, xmm3	/* lu*(3-rsq*lu*lu) */
	mulsd   xmm4, [esp + _half] /* iter1 ( new lu) */

	movapd xmm3, xmm4
	mulsd xmm4, xmm4	/* lu*lu */
	mulsd xmm5, xmm4	/* rsq*lu*lu */
	movapd xmm4, [esp + _three]
	subsd xmm4, xmm5	/* 3-rsq*lu*lu */
	mulsd xmm4, xmm3	/* lu*(	3-rsq*lu*lu) */
	mulsd xmm4, [esp + _half] /* rinv */
	movapd  xmm5, xmm4	/* rinvH2 in xmm5 */

	/* do O interactions */
	movapd  xmm4, xmm7	
	mulsd   xmm4, xmm4	/* xmm7=rinv, xmm4=rinvsq */
	movapd xmm1, xmm4
	mulsd  xmm1, xmm4
	mulsd  xmm1, xmm4	/* xmm1=rinvsix */
	movapd xmm2, xmm1
	mulsd  xmm2, xmm2	/* xmm2=rinvtwelve */
	mulsd  xmm1, [esp + _c6]
	mulsd  xmm2, [esp + _c12]
	movapd xmm3, xmm2
	subsd  xmm3, xmm1	/* vnb=vnb12-vnb6 */		
	addsd  xmm3, [esp + _vnbtot]

	movapd xmm0, xmm7
	movapd xmm1, [esp + _krsqO]
	addsd  xmm0, xmm1
	subsd  xmm0, [esp + _crf] /* xmm0=rinv+ krsq-crf */
	mulsd  xmm0, [esp + _qqO]
	addsd  xmm0, [esp + _vctot]
	movlpd [esp + _vnbtot], xmm3
	movlpd [esp + _vctot], xmm0

	/* H1 interactions */
	movapd  xmm0, [esp + _krsqH1]
	addsd   xmm6, xmm0	/* xmm6=rinv+ krsq */
	subsd   xmm6, [esp + _crf]
	mulsd   xmm6, [esp + _qqH] /* vcoul */
	addsd  xmm6, [esp + _vctot]
	movlpd [esp + _vctot], xmm6

	/* H2 interactions */
	movapd  xmm0, [esp + _krsqH2]
	addsd   xmm5, xmm0	/* xmm5=rinv+ krsq */
	subsd   xmm5, [esp + _crf]
	mulsd   xmm5, [esp + _qqH] /* vcoul */
	addsd  xmm5, [esp + _vctot]
	movlpd [esp + _vctot], xmm5

.imc2120_updateouterdata:
	/* get gid */
	mov   edx, [ebp + _gid]  
	mov   edx, [edx]
	add   [ebp + _gid],  4	

	/* accumulate total potential energy and update it */
	movapd xmm7, [esp + _vctot]
	/* accumulate */
	movhlps xmm6, xmm7
	addsd  xmm7, xmm6	/* low xmm7 has the sum now */

	/* add earlier value from mem */
	mov   eax, [ebp + _Vc]
	addsd xmm7, [eax + edx*8] 
	/* move back to mem */
	movsd [eax + edx*8], xmm7 
	
	/* accumulate total lj energy and update it */
	movapd xmm7, [esp + _vnbtot]
	/* accumulate */
	movhlps xmm6, xmm7
	addsd  xmm7, xmm6	/* low xmm7 has the sum now */
	
	/* add earlier value from mem */
	mov   eax, [ebp + _Vnb]
	addsd xmm7, [eax + edx*8] 
	/* move back to mem */
	movsd [eax + edx*8], xmm7 
	
	/* finish if last */
	mov   ecx, [ebp + _nri]
	dec ecx
	jecxz .imc2120_end
	/* not last, iterate once more! */ 
	mov [ebp + _nri], ecx
	jmp .imc2120_outer
.imc2120_end:
	emms
	mov eax, [esp + _salign]
	add esp, eax
	add esp, 408
	pop edi
	pop esi
        pop edx
        pop ecx
        pop ebx
        pop eax
	leave
	ret



	
