.imc2130_updateouterdata:
	/* get gid */
	mov   edx, [ebp + _gid]  
	mov   edx, [edx]
	add   [ebp + _gid],  4	

	/* accumulate total potential energy and update it */
	movapd xmm7, [esp + _vctot]
	/* accumulate */
	movhlps xmm6, xmm7
	addsd  xmm7, xmm6	/* low xmm7 has the sum now */

	/* add earlier value from mem */
	mov   eax, [ebp + _Vc]
	addsd xmm7, [eax + edx*8] 
	/* move back to mem */
	movsd [eax + edx*8], xmm7 
	
	/* accumulate total lj energy and update it */
	movapd xmm7, [esp + _vnbtot]
	/* accumulate */
	movhlps xmm6, xmm7
	addsd  xmm7, xmm6	/* low xmm7 has the sum now */
	
	/* add earlier value from mem */
	mov   eax, [ebp + _Vnb]
	addsd xmm7, [eax + edx*8] 
	/* move back to mem */
	movsd [eax + edx*8], xmm7 
	
	/* finish if last */
	mov   ecx, [ebp + _nri]
	dec ecx
	jecxz .imc2130_end
	/* not last, iterate once more! */ 
	mov [ebp + _nri], ecx
	jmp .imc2130_outer
.imc2130_end:
	emms
	mov eax, [esp + _salign]
	add esp, eax
	add esp, 772
	pop edi
	pop esi
        pop edx
        pop ecx
        pop ebx
        pop eax
	leave
	ret


	

