.equ		_argkrf,	56 /* double */
.equ		_argcrf,	64 /* double */
	/* stack offsets for local variables */ 
	/* bottom of stack is cache-aligned for sse use */
.equ		_ixO,		0
.equ		_iyO,		16
.equ		_izO,		32
.equ		_ixH1,		48
.equ		_iyH1,		64
.equ		_izH1,		80
.equ		_ixH2,		96
.equ		_iyH2,		112
.equ		_izH2,		128
