.equ		_jyO,		160
.equ		_jzO,		176
.equ		_jxH1,		192
.equ		_jyH1,		208
.equ		_jzH1,		224
.equ		_jxH2,		240
.equ		_jyH2,		256
.equ		_jzH2,		272
.equ		_qqOO,		288
.equ		_qqOH,		304
.equ		_qqHH,		320
.equ		_vctot,		336
.equ		_half,		352
.equ		_three,		368
.equ		_rsqOO,		384
.equ		_rsqOH1,	400
.equ		_rsqOH2,	416
.equ		_rsqH1O,	432
.equ		_rsqH1H1,	448
.equ		_rsqH1H2,	464
.equ		_rsqH2O,	480
.equ		_rsqH2H1,	496
.equ		_rsqH2H2,	512
.equ		_rinvOO,	528
.equ		_rinvOH1,	544
.equ		_rinvOH2,	560
.equ		_rinvH1O,	576
.equ		_rinvH1H1,	592
.equ		_rinvH1H2,	608
.equ		_rinvH2O,	624
.equ		_rinvH2H1,	640
.equ		_rinvH2H2,	656
.equ		_krf,		672	
.equ		_crf,		688
.equ		_is3,		704
.equ		_ii3,		708
.equ		_innerjjnr,	712
.equ		_innerk,	716
