	shufpd xmm1, xmm1, 0
	shufpd xmm2, xmm2, 0

	movapd [esp + _ix], xmm0
	movapd [esp + _iy], xmm1
	movapd [esp + _iz], xmm2

	mov   [esp + _ii3], ebx
	
