	movapd xmm1, xmm4	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulsd  xmm2, xmm2	/* xmm2=eps2 */
	
	shl eax, 2		/* idx *= 4 */
	
	mov  esi, [ebp + _VFtab]

