	mulpd   xmm4, xmm3	/* lu*(3-rsq*lu*lu) */
	mulpd   xmm4, [esp + _half] /* iter1 ( new lu) */

	movapd xmm2, xmm7
	movapd xmm3, xmm4
	mulpd xmm4, xmm4	/* lu*lu */
	mulpd xmm2, xmm4	/* rsq*lu*lu */
	movapd xmm4, [esp + _three]
	subpd xmm4, xmm2	/* 3-rsq*lu*lu */
	mulpd xmm4, xmm3	/* lu*(	3-rsq*lu*lu) */
	mulpd xmm4, [esp + _half] /* rinv */
	movapd  [esp + _rinvO], xmm4	/* rinvO in xmm4 */
	mulpd   xmm7, xmm4
	movapd  [esp + _rO], xmm7	/* r in xmm7 */
	
	/* rsqH1 - seed in xmm2 */
	cvtpd2ps xmm2, xmm6	
	rsqrtps xmm2, xmm2
	cvtps2pd xmm2, xmm2

	movapd  xmm3, xmm2
	mulpd   xmm2, xmm2
	movapd  xmm4, [esp + _three]
	mulpd   xmm2, xmm6	/* rsq*lu*lu */
	subpd   xmm4, xmm2	/* 30-rsq*lu*lu */
	mulpd   xmm4, xmm3	/* lu*(3-rsq*lu*lu) */
	mulpd   xmm4, [esp + _half] /* iter1 ( new lu) */

	movapd xmm2, xmm6
	movapd xmm3, xmm4
	mulpd xmm4, xmm4	/* lu*lu */
	mulpd xmm2, xmm4	/* rsq*lu*lu */
	movapd xmm4, [esp + _three]
	subpd xmm4, xmm2	/* 3-rsq*lu*lu */
	mulpd xmm4, xmm3	/* lu*(	3-rsq*lu*lu) */
	mulpd xmm4, [esp + _half] /* rinv */
	movapd [esp + _rinvH1], xmm4	/* rinvH1 */
	mulpd  xmm6, xmm4
	movapd [esp + _rH1], xmm6	/* rH1 */
	
	/* rsqH2 - seed in xmm2 */
	cvtpd2ps xmm2, xmm5	
	rsqrtps xmm2, xmm2
	cvtps2pd xmm2, xmm2

	movapd  xmm3, xmm2
	mulpd   xmm2, xmm2
	movapd  xmm4, [esp + _three]
	mulpd   xmm2, xmm5	/* rsq*lu*lu */
	subpd   xmm4, xmm2	/* 30-rsq*lu*lu */
	mulpd   xmm4, xmm3	/* lu*(3-rsq*lu*lu) */
	mulpd   xmm4, [esp + _half] /* iter1 ( new lu) */

	movapd xmm2, xmm5
	movapd xmm3, xmm4
	mulpd xmm4, xmm4	/* lu*lu */
	mulpd xmm2, xmm4	/* rsq*lu*lu */
	movapd xmm4, [esp + _three]
	subpd xmm4, xmm2	/* 3-rsq*lu*lu */
	mulpd xmm4, xmm3	/* lu*(	3-rsq*lu*lu) */
	mulpd xmm4, [esp + _half] /* rinv */
	movapd [esp + _rinvH2], xmm4 /* rinv */
	mulpd xmm5, xmm4
	movapd [esp + _rH2], xmm5 /* r */

	/* do O interactions */
	/* rO is still in xmm7 */
	mulpd xmm7, [esp + _tsc]
	cvttpd2pi mm6, xmm7	/* mm6 = lu idx */
	cvtpi2pd xmm6, mm6
	subpd xmm7, xmm6
	movapd xmm1, xmm7	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulpd  xmm2, xmm2	/* xmm2=eps2 */
	
	pslld mm6, 2		/* idx *= 4 */
	mov  esi, [ebp + _VFtab]
	movd eax, mm6
	psrlq mm6, 32
	movd ebx, mm6		/* indices in eax/ebx */

	movupd xmm4, [esi + eax*8]	/* Y1 F1 */	
	movupd xmm3, [esi + ebx*8]	/* Y2 F2 */
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 Y2 */
	unpckhpd xmm5, xmm3	/* F1 F2 */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */	
	movupd xmm3, [esi + ebx*8 + 16]	/* G2 H2 */
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 G2 */
	unpckhpd xmm7, xmm3	/* H1 H2 */
	/* coulomb table ready, in xmm4-xmm7 */ 		
	mulpd  xmm6, xmm1	/* xmm6=Geps */
	mulpd  xmm7, xmm2	/* xmm7=Heps2 */
	addpd  xmm5, xmm6
	addpd  xmm5, xmm7	/* xmm5=Fp */	
	movapd xmm3, [esp + _qqO]
	mulpd  xmm5, xmm1 /* xmm5=eps*Fp */
	addpd  xmm5, xmm4 /* xmm5=VV */
	mulpd  xmm5, xmm3 /* vcoul=qq*VV */ 
        /* at this point mm5 contains vcoul */
        /* increment vcoul - then we can get rid of mm5 */
        addpd  xmm5, [esp + _vctot]
        movapd [esp + _vctot], xmm5 

	/* Done with O interactions - now H1! */
	movapd xmm7, [esp + _rH1]
	mulpd xmm7, [esp + _tsc]
	cvttpd2pi mm6, xmm7	/* mm6 = lu idx */
	cvtpi2pd xmm6, mm6
	subpd xmm7, xmm6
	movapd xmm1, xmm7	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulpd  xmm2, xmm2	/* xmm2=eps2 */
	
	pslld mm6, 2		/* idx *= 4 */
	mov  esi, [ebp + _VFtab]
	movd eax, mm6
	psrlq mm6, 32
	movd ebx, mm6		/* indices in eax/ebx */

	movupd xmm4, [esi + eax*8]	/* Y1 F1 */	
	movupd xmm3, [esi + ebx*8]	/* Y2 F2 */
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 Y2 */
	unpckhpd xmm5, xmm3	/* F1 F2 */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */	
	movupd xmm3, [esi + ebx*8 + 16]	/* G2 H2 */
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 G2 */
	unpckhpd xmm7, xmm3	/* H1 H2 */
	/* coulomb table ready, in xmm4-xmm7 */ 		
	mulpd  xmm6, xmm1	/* xmm6=Geps */
	mulpd  xmm7, xmm2	/* xmm7=Heps2 */
	addpd  xmm5, xmm6
	addpd  xmm5, xmm7	/* xmm5=Fp */	
	movapd xmm3, [esp + _qqH]
	mulpd  xmm5, xmm1 /* xmm5=eps*Fp */
	addpd  xmm5, xmm4 /* xmm5=VV */
	mulpd  xmm5, xmm3 /* vcoul=qq*VV */ 
        /* at this point mm5 contains vcoul */
        /* increment vcoul */
        addpd  xmm5, [esp + _vctot]
	movapd [esp + _vctot], xmm5
	
	/* Done with H1, finally we do H2 interactions */
	movapd xmm7, [esp + _rH2]
	mulpd   xmm7, [esp + _tsc]
	cvttpd2pi mm6, xmm7	/* mm6 = lu idx */
	cvtpi2pd xmm6, mm6
	subpd xmm7, xmm6
	movapd xmm1, xmm7	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulpd  xmm2, xmm2	/* xmm2=eps2 */
	
	pslld mm6, 2		/* idx *= 4 */
	mov  esi, [ebp + _VFtab]
	movd eax, mm6
	psrlq mm6, 32
	movd ebx, mm6		/* indices in eax/ebx */

	movupd xmm4, [esi + eax*8]	/* Y1 F1 */	
	movupd xmm3, [esi + ebx*8]	/* Y2 F2 */
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 Y2 */
	unpckhpd xmm5, xmm3	/* F1 F2 */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */	
	movupd xmm3, [esi + ebx*8 + 16]	/* G2 H2 */
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 G2 */
	unpckhpd xmm7, xmm3	/* H1 H2 */
	/* coulomb table ready, in xmm4-xmm7 */ 		
	mulpd  xmm6, xmm1	/* xmm6=Geps */
	mulpd  xmm7, xmm2	/* xmm7=Heps2 */
	addpd  xmm5, xmm6
	addpd  xmm5, xmm7	/* xmm5=Fp */	
	movapd xmm3, [esp + _qqH]
	mulpd  xmm5, xmm1 /* xmm5=eps*Fp */
	addpd  xmm5, xmm4 /* xmm5=VV */
	mulpd  xmm5, xmm3 /* vcoul=qq*VV */ 
        /* at this point mm5 contains vcoul */
        /* increment vcoul */
        addpd  xmm5, [esp + _vctot]
	movapd [esp + _vctot], xmm5

	/* should we do one more iteration? */
	sub   [esp + _innerk],  2
	jl    .imc3020_checksingle
	jmp   .imc3020_unroll_loop
.imc3020_checksingle:	
	mov   edx, [esp + _innerk]
	and   edx, 1
	jnz   .imc3020_dosingle
	jmp   .imc3020_updateouterdata
.imc3020_dosingle:
	mov   edx, [esp + _innerjjnr]     /* pointer to jjnr[k] */
	mov   eax, [edx]	

	mov esi, [ebp + _charge]        /* base of charge[] */
	xorpd xmm3, xmm3
	movlpd xmm3, [esi + eax*8]
	movapd xmm4, xmm3	     
	mulpd  xmm3, [esp + _iqO]
	mulpd  xmm4, [esp + _iqH]

	movapd  [esp + _qqO], xmm3
	movapd  [esp + _qqH], xmm4	

	mov esi, [ebp + _pos]       /* base of pos[] */

	lea   eax, [eax + eax*2]         /* replace jnr with j3 */
	/* move coordinates to xmm0-xmm2 */	
	movlpd xmm0, [esi + eax*8]
	movlpd xmm1, [esi + eax*8 + 8]
	movlpd xmm2, [esi + eax*8 + 16]

	/* move ixO-izO to xmm4-xmm6 */
	movapd xmm4, [esp + _ixO]
	movapd xmm5, [esp + _iyO]
	movapd xmm6, [esp + _izO]

	/* calc dr */
	subsd xmm4, xmm0
	subsd xmm5, xmm1
	subsd xmm6, xmm2

	/* square it */
	mulsd xmm4,xmm4
	mulsd xmm5,xmm5
	mulsd xmm6,xmm6
	addsd xmm4, xmm5
	addsd xmm4, xmm6
	movapd xmm7, xmm4
	/* rsqO in xmm7 */

	/* move ixH1-izH1 to xmm4-xmm6 */
	movapd xmm4, [esp + _ixH1]
	movapd xmm5, [esp + _iyH1]
	movapd xmm6, [esp + _izH1]

	/* calc dr */
	subsd xmm4, xmm0
	subsd xmm5, xmm1
	subsd xmm6, xmm2

	/* square it */
	mulsd xmm4,xmm4
	mulsd xmm5,xmm5
	mulsd xmm6,xmm6
	addsd xmm6, xmm5
	addsd xmm6, xmm4
	/* rsqH1 in xmm6 */

	/* move ixH2-izH2 to xmm3-xmm5 */ 
	movapd xmm3, [esp + _ixH2]
	movapd xmm4, [esp + _iyH2]
	movapd xmm5, [esp + _izH2]

	/* calc dr */
	subsd xmm3, xmm0
	subsd xmm4, xmm1
	subsd xmm5, xmm2

	/* square it */
	mulsd xmm3,xmm3
	mulsd xmm4,xmm4
	mulsd xmm5,xmm5
	addsd xmm5, xmm4
	addsd xmm5, xmm3
	/* rsqH2 in xmm5, rsqH1 in xmm6, rsqO in xmm7 */

	/* start with rsqO - put seed in xmm2 */
	cvtsd2ss xmm2, xmm7	
	rsqrtss xmm2, xmm2
	cvtss2sd xmm2, xmm2

	movapd  xmm3, xmm2
	mulsd   xmm2, xmm2
	movapd  xmm4, [esp + _three]
	mulsd   xmm2, xmm7	/* rsq*lu*lu */
	subsd   xmm4, xmm2	/* 30-rsq*lu*lu */
	mulsd   xmm4, xmm3	/* lu*(3-rsq*lu*lu) */
	mulsd   xmm4, [esp + _half] /* iter1 ( new lu) */

	movapd xmm2, xmm7
	movapd xmm3, xmm4
	mulsd xmm4, xmm4	/* lu*lu */
	mulsd xmm2, xmm4	/* rsq*lu*lu */
	movapd xmm4, [esp + _three]
	subsd xmm4, xmm2	/* 3-rsq*lu*lu */
	mulsd xmm4, xmm3	/* lu*(	3-rsq*lu*lu) */
	mulsd xmm4, [esp + _half] /* rinv */
	movapd  [esp + _rinvO], xmm4	/* rinvO in xmm4 */
	mulsd   xmm7, xmm4
	movapd  [esp + _rO], xmm7	/* r in xmm7 */
	
	/* rsqH1 - seed in xmm2 */
	cvtsd2ss xmm2, xmm6	
	rsqrtss xmm2, xmm2
	cvtss2sd xmm2, xmm2

	movapd  xmm3, xmm2
	mulsd   xmm2, xmm2
	movapd  xmm4, [esp + _three]
	mulsd   xmm2, xmm6	/* rsq*lu*lu */
	subsd   xmm4, xmm2	/* 30-rsq*lu*lu */
	mulsd   xmm4, xmm3	/* lu*(3-rsq*lu*lu) */
	mulsd   xmm4, [esp + _half] /* iter1 ( new lu) */

	movapd xmm2, xmm6
	movapd xmm3, xmm4
	mulsd xmm4, xmm4	/* lu*lu */
	mulsd xmm2, xmm4	/* rsq*lu*lu */
	movapd xmm4, [esp + _three]
	subsd xmm4, xmm2	/* 3-rsq*lu*lu */
	mulsd xmm4, xmm3	/* lu*(	3-rsq*lu*lu) */
	mulsd xmm4, [esp + _half] /* rinv */
	movapd [esp + _rinvH1], xmm4	/* rinvH1 */
	mulsd  xmm6, xmm4
	movapd [esp + _rH1], xmm6	/* rH1 */
	
	/* rsqH2 - seed in xmm2 */
	cvtsd2ss xmm2, xmm5	
	rsqrtss xmm2, xmm2
	cvtss2sd xmm2, xmm2

	movapd  xmm3, xmm2
	mulsd   xmm2, xmm2
	movapd  xmm4, [esp + _three]
	mulsd   xmm2, xmm5	/* rsq*lu*lu */
	subsd   xmm4, xmm2	/* 30-rsq*lu*lu */
	mulsd   xmm4, xmm3	/* lu*(3-rsq*lu*lu) */
	mulsd   xmm4, [esp + _half] /* iter1 ( new lu) */

	movapd xmm2, xmm5
	movapd xmm3, xmm4
	mulsd xmm4, xmm4	/* lu*lu */
	mulsd xmm2, xmm4	/* rsq*lu*lu */
	movapd xmm4, [esp + _three]
	subsd xmm4, xmm2	/* 3-rsq*lu*lu */
	mulsd xmm4, xmm3	/* lu*(	3-rsq*lu*lu) */
	mulsd xmm4, [esp + _half] /* rinv */
	movapd [esp + _rinvH2], xmm4 /* rinv */
	mulsd xmm5, xmm4
	movapd [esp + _rH2], xmm5 /* r */

	/* do O interactions */
	movd mm0, eax	
	/* rO is still in xmm7 */
	mulsd   xmm7, [esp + _tsc]
	cvttsd2si eax, xmm7	/* mm6 = lu idx */
	cvtsi2sd xmm6, eax
	subsd xmm7, xmm6
	movapd xmm1, xmm7	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulsd  xmm2, xmm2	/* xmm2=eps2 */
	
	shl eax, 2		/* idx *= 4 */
	mov  esi, [ebp + _VFtab]

	movupd xmm4, [esi + eax*8]	/* Y1 F1 */
	xorpd xmm3, xmm3	
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 */
	unpckhpd xmm5, xmm3	/* F1  */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */
	xorpd xmm3, xmm3
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 */
	unpckhpd xmm7, xmm3	/* H1 */
	/* coulomb table ready, in xmm4-xmm7 */ 		
	mulsd  xmm6, xmm1	/* xmm6=Geps */
	mulsd  xmm7, xmm2	/* xmm7=Heps2 */
	addsd  xmm5, xmm6
	addsd  xmm5, xmm7	/* xmm5=Fp */	
	movapd xmm3, [esp + _qqO]
	mulsd  xmm5, xmm1 /* xmm5=eps*Fp */
	addsd  xmm5, xmm4 /* xmm5=VV */
	mulsd  xmm5, xmm3 /* vcoul=qq*VV */ 
        /* at this point mm5 contains vcoul */
        /* increment vcoul - then we can get rid of mm5 */
        addsd  xmm5, [esp + _vctot]
        movlpd [esp + _vctot], xmm5 

	/* Done with O interactions - now H1! */
	movapd xmm7, [esp + _rH1]
	mulsd xmm7, [esp + _tsc]
	cvttsd2si eax, xmm7	/* mm6 = lu idx */
	cvtsi2sd xmm6, eax
	subsd xmm7, xmm6
	movapd xmm1, xmm7	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulsd  xmm2, xmm2	/* xmm2=eps2 */
	
	shl eax, 2		/* idx *= 4 */
	mov  esi, [ebp + _VFtab]
	
	movupd xmm4, [esi + eax*8]	/* Y1 F1 */
	xorpd xmm3, xmm3
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1  */
	unpckhpd xmm5, xmm3	/* F1  */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */
	xorpd xmm3, xmm3
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 */
	unpckhpd xmm7, xmm3	/* H1 */
	/* coulomb table ready, in xmm4-xmm7 */ 		
	mulsd  xmm6, xmm1	/* xmm6=Geps */
	mulsd  xmm7, xmm2	/* xmm7=Heps2 */
	addsd  xmm5, xmm6
	addsd  xmm5, xmm7	/* xmm5=Fp */	
	movapd xmm3, [esp + _qqH]
	mulsd  xmm5, xmm1 /* xmm5=eps*Fp */
	addsd  xmm5, xmm4 /* xmm5=VV */
	mulsd  xmm5, xmm3 /* vcoul=qq*VV */ 
        /* at this point mm5 contains vcoul */
        /* increment vcoul */
        addsd  xmm5, [esp + _vctot]
        movlpd [esp + _vctot], xmm5 


	/* Done with H1, finally we do H2 interactions */
	movapd xmm7, [esp + _rH2]
	mulsd   xmm7, [esp + _tsc]
	cvttsd2si eax, xmm7	/* mm6 = lu idx */
	cvtsi2sd xmm6, eax
	subsd xmm7, xmm6
	movapd xmm1, xmm7	/* xmm1=eps */
	movapd xmm2, xmm1	
	mulsd  xmm2, xmm2	/* xmm2=eps2 */
	
	shl eax, 2		/* idx *= 4 */
	mov  esi, [ebp + _VFtab]

	movupd xmm4, [esi + eax*8]	/* Y1 F1 */
	xorpd xmm3, xmm3
	movapd xmm5, xmm4
	unpcklpd xmm4, xmm3	/* Y1 */
	unpckhpd xmm5, xmm3	/* F1 */

	movupd xmm6, [esi + eax*8 + 16]	/* G1 H1 */
	xorpd xmm3, xmm3
	movapd xmm7, xmm6
	unpcklpd xmm6, xmm3	/* G1 */
	unpckhpd xmm7, xmm3	/* H1 */
	/* coulomb table ready, in xmm4-xmm7 */ 		
	mulsd  xmm6, xmm1	/* xmm6=Geps */
	mulsd  xmm7, xmm2	/* xmm7=Heps2 */
	addsd  xmm5, xmm6
	addsd  xmm5, xmm7	/* xmm5=Fp */	
	movapd xmm3, [esp + _qqH]
	mulsd  xmm5, xmm1 /* xmm5=eps*Fp */
	addsd  xmm5, xmm4 /* xmm5=VV */
	mulsd  xmm5, xmm3 /* vcoul=qq*VV */ 
        /* at this point mm5 contains vcoul */
        /* increment vcoul */
        addsd  xmm5, [esp + _vctot]
        movlpd [esp + _vctot], xmm5 

.imc3020_updateouterdata:
	/* get gid */
	mov   edx, [ebp + _gid]  
	mov   edx, [edx]
	add   [ebp + _gid],  4	

	/* accumulate total potential energy and update it */
	movapd xmm7, [esp + _vctot]
	/* accumulate */
	movhlps xmm6, xmm7
	addsd  xmm7, xmm6	/* low xmm7 has the sum now */
        
	/* add earlier value from mem */
	mov   eax, [ebp + _Vc]
	addsd xmm7, [eax + edx*8] 
	/* move back to mem */
	movsd [eax + edx*8], xmm7 
	
	/* finish if last */
	mov   ecx, [ebp + _nri]
	dec ecx
	jecxz .imc3020_end
	/* not last, iterate once more! */ 
	mov [ebp + _nri], ecx
	jmp .imc3020_outer
.imc3020_end:
	emms
	mov eax, [esp + _salign]
	add esp, eax
	add esp, 388
	pop edi
	pop esi
        pop edx
        pop ecx
        pop ebx
        pop eax
	leave
	ret
	

