mcinl3100_sse2:	
.equ		_nri,		8
.equ		_iinr,		12
.equ		_jindex,	16
.equ		_jjnr,		20
.equ		_shift,		24
.equ		_shiftvec,	28
.equ		_gid,		32
.equ		_pos,		36
.equ		_charge,	40
.equ		_facel,		44 /* double */
.equ		_Vc,		52			
.equ		_type,		56
.equ		_ntype,		60
.equ		_nbfp,		64	
.equ		_Vnb,		68
.equ		_tabscale,	72 /* double */
.equ		_VFtab,		80
